INSERT INTO Ben_Jing_Note VALUES (1, '丹溪', '燥湿，分水道，实大肠，化食毒，行积滞，逐凝血，解燥渴，补脾胃，降心火，偏主石淋为要药。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 4);
INSERT INTO Ben_Jing_Note VALUES (2, '别录', '疗小腹痛结烦疼。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 8);
INSERT INTO Ben_Jing_Note VALUES (3, '抱朴子金丹篇', '灵丹经用丹砂雄黄雌黄石硫磺曾青矾石戎盐太一余粮，亦用六一泥及神室祭蘸合之三十六日成。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 9);
INSERT INTO Ben_Jing_Note VALUES (4, '甄权', '治肺痈吐脓，咳逆上气，黄疸。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 10);
INSERT INTO Ben_Jing_Note VALUES (5, '别录', '疗上气心腹痛，寒热邪气结心，补心气不足，定惊悸安魂魄，填下焦，止消渴，除胃中久寒，散痈肿，令人悦泽。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 11);
INSERT INTO Ben_Jing_Note VALUES (6, '甄权', '补五脏虚气。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 12);
INSERT INTO Ben_Jing_Note VALUES (7, '别录', '吐风痰，癫痫，平肝。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 14);
INSERT INTO Ben_Jing_Note VALUES (8, '别录', '四肢湿痹不得曲伸，小儿温疟，身积热不解，可作浴汤。                    ', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 15);
INSERT INTO Ben_Jing_Note VALUES (9, '甄权', '治头目风热，风眩倒地，脑骨疼痛，身上一切游风令消散，利血脉，调四肢。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 16);
INSERT INTO Ben_Jing_Note VALUES (10, '甄权', '主五劳七伤，虚损痿弱，止呕哕，补五脏六腑，保中守神。消胸中痰，治肺痿及痫疾，冷气上逆，伤寒不下食，凡虚而多梦者可用。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 17);
INSERT INTO Ben_Jing_Note VALUES (11, '别录', '保定肺气去寒热养肌肤，利小便，冷而能补。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 18);
INSERT INTO Ben_Jing_Note VALUES (12, '别录', '温中下气，烦满短气，伤脏咳嗽，止渴。通经脉，利气血，解百药毒，为九土之精，安和七十二种石，一千二百种草。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 19);
INSERT INTO Ben_Jing_Note VALUES (13, '别录', '主男子五劳七伤，女子伤中胞露下血，破恶血溺血，利大小肠，去胃中宿食，饱力断绝，补五脏内伤不足，通血脉益气力，利耳目。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 20);
INSERT INTO Ben_Jing_Note VALUES (14, '别录', '大风在身面，风眩头痛，眼泪出，消痰水，逐皮间风水结肿，除心下急满，霍乱吐下不止，利腰脐间血，益津液，暖胃消谷嗜食。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 21);
INSERT INTO Ben_Jing_Note VALUES (15, '甄权', '治男女虚冷，添精益髓，去腰疼膝冷，消渴热中，久服去面皯，悦颜色。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 22);
INSERT INTO Ben_Jing_Note VALUES (16, '别录', '疗伤中少气，男子阴消，老人失溺，补中续绝，益精，利阴气，填骨髓，止白发，除脑中痛，及腰脊痛，妇人月水不调血结。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 23);
INSERT INTO Ben_Jing_Note VALUES (17, '别录', '疗血逆，大热头痛心烦。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 24);
INSERT INTO Ben_Jing_Note VALUES (18, '别录', '心腹中结气，虚热湿毒肿痛，茎中寒，及目痛眦烂泪出。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 25);
INSERT INTO Ben_Jing_Note VALUES (19, '效用', '根为解热利尿，镇静药，治膀胱宿水，膀胱热结，癃闭，癫狂痫症。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 26);
INSERT INTO Ben_Jing_Note VALUES (20, '别录', '除伤寒心下烦热，诸痰热结实，胸中邪气，五脏间游气，大肠停积水胀，及湿痹拘急，亦可做浴汤。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 27);
INSERT INTO Ben_Jing_Note VALUES (21, '别录', '疗身重目黄，心下支满，虚劳客热，口干燥渴，止呕吐，愈痿躄，强阴益精，消谷调中，保神定肺气，定五脏，令人肥健，美颜色，有子。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 28);
INSERT INTO Ben_Jing_Note VALUES (22, '别录', '疗目赤痛，去肤翳止泪出，利水道，下乳汁，通关节，破坚积。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 6);
INSERT INTO Ben_Jing_Note VALUES (23, '别录', '男子伤中，女子淋沥不欲食，养肺，强阴益精，令人有子，明目疗赤痛。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 30);
INSERT INTO Ben_Jing_Note VALUES (24, '别录', '消毒，杀鬼、精物、温疟、蛊毒，气劣气不足，肌中偏寒，引药之精。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 31);
INSERT INTO Ben_Jing_Note VALUES (25, '别录', '疗诸贼风，百节痛风，无问久新。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 29);
INSERT INTO Ben_Jing_Note VALUES (26, '别录', '除筋骨中邪气不仁，利肠胃，消水肿，令人能食。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 33);
INSERT INTO Ben_Jing_Note VALUES (27, '别录', '补虚损，五脏痞满，起阴气，止泄精，消渴，淋沥，逐膀胱三焦停水。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 34);
INSERT INTO Ben_Jing_Note VALUES (28, '别录', '利丈夫，定心气，止惊悸益精，去心下隔气，皮肤中热，面目黄。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 35);
INSERT INTO Ben_Jing_Note VALUES (29, '别录', '除胃中伏热，时气温热，热泄下痢，去肠中小虫，益肝胆气，止惊惕。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 36);
INSERT INTO Ben_Jing_Note VALUES (30, '别录', '温中下气，破痰，利水道，开胸中滞积，除喉痹，鼻塞不闻香臭，风痫癫疾，下乳结，汗不出，血不行，安五脏，益肝胆，通精气。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 37);
INSERT INTO Ben_Jing_Note VALUES (31, '别录', '补内绝不足，平胃气，长肌肤肉，逐皮肤邪热，疿气脚湿，疼冷痹弱，定志除惊。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 38);
INSERT INTO Ben_Jing_Note VALUES (32, '别录', '疗头面游风，小腹及阴中相引痛，补五劳，益精，利男子。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 39);
INSERT INTO Ben_Jing_Note VALUES (33, '效用', '白英为解热药，治风疹，丹毒，黄疸及热疟。有补益之效。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 40);
INSERT INTO Ben_Jing_Note VALUES (34, '别录', '疗五脏十二经脉中百二十疾，暴伤寒，腹中大热，止烦满消渴利小便，及瘘蚀疮，能化七十二种石。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 2);
INSERT INTO Ben_Jing_Note VALUES (35, '别录', '疗心下坚，隔中寒热，周痹，妇人月水不通。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 43);
INSERT INTO Ben_Jing_Note VALUES (36, '灵胎', '此因其物之所能，以益人之能也，昔圣人幽赞于神明而生耆，此草中之神物也，服之则补人之心神，自能聪慧前知，食肉者鄙，不益信夫。能益智通灵。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 45);
INSERT INTO Ben_Jing_Note VALUES (37, '其他', '赤芝—味苦平，主胸中结，益心气，补中，增慧智，不忘，久服轻身不老，延年神仙。一名丹芝。生霍山。黑芝—味咸平，主癃，利水道，益肾气，通九窍，聪察，久服轻身不老，延年神仙。一名元芝。生恒山。青芝—味酸平，主明目补肝气，安精魂，仁恕，久服轻身不老，延年神仙。一名龙芝。生太白。白芝—味辛平，主咳逆上气，益肺气，通利口鼻，强志意，勇悍，安魂，久服轻身不老，延年神仙。一名玉芝。生华山。黄芝—味甘平，主心腹五邪，益脾气，安神，忠信和乐，久服轻身不老，延年神仙。一名金芝。生嵩山。紫芝—味甘温，主耳聋，利关节，保神，益精气，坚筋骨，好颜色，久服轻身不老，延年神仙。一名木芝。生高夏地上，色紫形如桑。六芝皆无毒，六月八月采。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 46);
INSERT INTO Ben_Jing_Note VALUES (38, '别录', '主头面游风，头风眼眩，下气，止腰痛，治虚劳羸瘦，充五脏，除烦热。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 47);
INSERT INTO Ben_Jing_Note VALUES (39, '甄权', '填骨髓，明耳目，利五脏，调六腑，通关节，治经络中结气，使人健少睡，益心力。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 48);
INSERT INTO Ben_Jing_Note VALUES (40, '别录', '除脑中冷痛，面上游风去来，目泪多涕唾，忽忽如醉，诸寒冷气，心腹坚痛，中恶卒急肿痛，胁风痛。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 49);
INSERT INTO Ben_Jing_Note VALUES (41, '别录', '身中老风，头中久风，风眩。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 50);
INSERT INTO Ben_Jing_Note VALUES (42, '别录', '五脏冷热，久下泄澼脓血，止消渴，大惊，调胃厚肠益胆，疗口疮。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 51);
INSERT INTO Ben_Jing_Note VALUES (43, '别录', '大惊入腹除邪气，养肾主腰髋痛，坚筋利关节，久服明目润泽。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 52);
INSERT INTO Ben_Jing_Note VALUES (44, '别录', '主身体风痿，头痛，咳逆伤肺，肺痿，止烦下气，小儿头疮，痈肿阴溃，可做磨粉。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 53);
INSERT INTO Ben_Jing_Note VALUES (45, '别录', '女人子脏冷，逐五脏间恶血，补丈夫虚损五劳羸瘦，止渴，腹痛泄痢，益气，利阴气。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 54);
INSERT INTO Ben_Jing_Note VALUES (46, '别录', '除膀胱邪气，腰痛止痢。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 55);
INSERT INTO Ben_Jing_Note VALUES (47, '别录', '烦满胁痛，风头面去来，四肢挛急，字乳，金疮内痉。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 56);
INSERT INTO Ben_Jing_Note VALUES (48, '甄权', '治痢血鼻衄，吐血，尿血，泻血，利水道，通经络，止女子崩中。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 57);
INSERT INTO Ben_Jing_Note VALUES (49, '别录', '妇人崩中漏血。金疮血内漏，止痛生肌肉，及腕伤恶血腹痛，关节缓急。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 59);
INSERT INTO Ben_Jing_Note VALUES (50, '甄权', '治冷气麻痹瘫缓不随，语多恍惚，善惊失志。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 42);
INSERT INTO Ben_Jing_Note VALUES (51, '效用', '营实为清热解毒要药，治痈疽恶疮，热毒阴蚀，蔷薇根能清热生肌，缩尿。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 61);
INSERT INTO Ben_Jing_Note VALUES (52, '别录', '除小虫去痹，除胸中结热，止烦渴，逐水，大吐下。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 62);
INSERT INTO Ben_Jing_Note VALUES (53, '别录', '疗唇口青。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 63);
INSERT INTO Ben_Jing_Note VALUES (54, '别录', '养血，去心腹痛疾，结气，腰脊强，脚痹，除风邪留热。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 64);
INSERT INTO Ben_Jing_Note VALUES (55, '别录', '止血内崩，下血，膀胱不足，踒跌，蛊毒，久服益精气，可以染绛，又苗根主痹及热中，伤跌折。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 65);
INSERT INTO Ben_Jing_Note VALUES (56, '别录', '头眩顶重，皮间邪气，如蜂螫针剌，鱼子细起，热疮，痈疽，痔，湿痹，止风邪咳嗽，下乳汁，可煮、可干用。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 66);
INSERT INTO Ben_Jing_Note VALUES (57, '别录', '养五脏，除热，生阴中肌。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 67);
INSERT INTO Ben_Jing_Note VALUES (58, '别录', '续筋骨，合金疮。  太乙金锁丹：功于秘精益髓，龙骨，覆盆子各五两，莲花蕊四两，栀子花三两，芡实米100 粒，共为末，另以金樱子 200 枚，去皮臼杵捣烂，水七升煮浓汁一升，加药粉杵为丸梧子大，每空心盐酒服下 30 丸，服至百日，极效。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 68);
INSERT INTO Ben_Jing_Note VALUES (59, '别录', '除胸中痰癖。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 69);
INSERT INTO Ben_Jing_Note VALUES (60, '甄权', '治腹胀，大小便不通，女子月经不通。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 3);
INSERT INTO Ben_Jing_Note VALUES (61, '别录', '养肝胆，除寒热，杀白虫，疗头风，脑中寒，止烦渴，补不足，盛阴气。味酸性小寒无毒，目痛止泪出，风痹，利关节，通九窍，破症瘕积聚。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 7);
INSERT INTO Ben_Jing_Note VALUES (62, '括要', '治虫毒蜂螫，傅肿伤，吸脓拔毒。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 72);
INSERT INTO Ben_Jing_Note VALUES (63, '别录', '治通身发黄，小便不利，除头热，去伏瘕。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 73);
INSERT INTO Ben_Jing_Note VALUES (64, '甄权', '止气奔喘促，消痰饮，破留血，项间瘿瘤之疾。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 74);
INSERT INTO Ben_Jing_Note VALUES (65, '别录', '除胃中热，喘息，烦满，止渴，明耳目。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 75);
INSERT INTO Ben_Jing_Note VALUES (66, '别录', '鬼疰精物邪恶气，杀百虫蛊毒老魅注易，立走啼哭。悲伤恍惚。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 77);
INSERT INTO Ben_Jing_Note VALUES (67, '别录', '补内虚不足痞满，身无润泽。出汗。除茎中热痛，疗疣虫肿不消食。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 78);
INSERT INTO Ben_Jing_Note VALUES (68, '别录', '暴症，逐水。疗痿躄。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 79);
INSERT INTO Ben_Jing_Note VALUES (69, '苏颂', '治疟多用。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 80);
INSERT INTO Ben_Jing_Note VALUES (70, '别录', '主金疮，止血逐痛，出刺。除风痹，内塞，止心烦，鼻衄，痈疽恶疮瘘乳，妇人难产。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 81);
INSERT INTO Ben_Jing_Note VALUES (71, '甄权', '小儿惊痫，热拥不通，疗痈，豌豆疮，水煎绵沾拭疮上。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 82);
INSERT INTO Ben_Jing_Note VALUES (72, '别录', '天行热闷，膈内热结，服一合，取利为度。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 83);
INSERT INTO Ben_Jing_Note VALUES (73, '别录', '疗胸痹，心腹结热，邪气头痛，皮间邪热，安五脏，久服利人，又云主头肿痛，益气长肌肉。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 84);
INSERT INTO Ben_Jing_Note VALUES (74, '别录', '心痛胁痛胁风，温经通脉，止烦出汗。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 88);
INSERT INTO Ben_Jing_Note VALUES (75, '好古', '补命门不足，能益火消阴。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 89);
INSERT INTO Ben_Jing_Note VALUES (76, '别录', '松脂除胃中伏热，咽干消渴，风痹死肌，炼之令白，其赤者主恶痹。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 90);
INSERT INTO Ben_Jing_Note VALUES (77, '大明', '主五痔，心痛，眼赤，杀腹脏虫，及皮肤风热，肠风泻血，赤白痢，并炒研服。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 91);
INSERT INTO Ben_Jing_Note VALUES (78, '大明', '治虫牙，鼻内息肉。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 5);
INSERT INTO Ben_Jing_Note VALUES (79, '别录', '疗恍惚虚损，吸吸历节，腰中重痛，益血止汗。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 93);
INSERT INTO Ben_Jing_Note VALUES (80, '别录', '止消渴好睡。大腹淋沥，膈中痰水，水肿淋结，开胸腑，调脏气，伐肾邪，长阴益气力，保神气。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 94);
INSERT INTO Ben_Jing_Note VALUES (81, '本草原文', '味甘平，主大小便不通，利水道，除邪气，久服轻身不饥。今名榆白皮。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 95);
INSERT INTO Ben_Jing_Note VALUES (82, '别录', '疗惊气在皮间肌肤赤起，目热赤口疮。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 96);
INSERT INTO Ben_Jing_Note VALUES (83, '别录', '干漆疗咳嗽，消瘀血痞结腰痛，女子疝瘕，利小肠，去疣虫。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 97);
INSERT INTO Ben_Jing_Note VALUES (84, '别录', '男子阴痿囊下湿，小便余沥，女人阴痒及腰脊痛，两脚疼痹风弱，五缓虚羸，补中益精，坚筋骨，强志意。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 98);
INSERT INTO Ben_Jing_Note VALUES (85, '别录', '风头痛脑鸣，目泪出，益气，令人光泽，脂致。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 99);
INSERT INTO Ben_Jing_Note VALUES (86, '别录', '温中解肌，利九窍，通鼻塞涕出，治面肿，引齿痛，眩冒身几几如在车船之上者，生须发，去白虫。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 100);
INSERT INTO Ben_Jing_Note VALUES (87, '别录', '去女子崩中内伤不足，产后余疾，下乳汁，主金疮，去痹。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 101);
INSERT INTO Ben_Jing_Note VALUES (88, '别录', '除固热在骨髓，去鼻中息肉。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 32);
INSERT INTO Ben_Jing_Note VALUES (89, '别录', '破心下结痰痞气，齆鼻。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 104);
INSERT INTO Ben_Jing_Note VALUES (90, '别录', '温中下气，令妇人子脏热。男子阴强，久服令人有子。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 70);
INSERT INTO Ben_Jing_Note VALUES (91, '别录', '合鸡子黄煎之消积水，疗小儿惊热百病。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 106);
INSERT INTO Ben_Jing_Note VALUES (92, '别录', '心腹烦满，恚怒，气伏在心下不得喘息，肠痈内疽，阴蚀，四肢痿枯，夜卧自惊汗出，止汗，缩小便，溺血，养精神，定魂魄，安五脏。白龙骨主多寐泄精，小便泄精。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 107);
INSERT INTO Ben_Jing_Note VALUES (93, '别录', '疗诸凶邪鬼气，中恶，心腹暴痛，胀急，痞满，风毒，去面黡，目中肤翳，妇人难产，堕胎。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 108);
INSERT INTO Ben_Jing_Note VALUES (94, '别录', '脚中酸痛，不欲践地。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 102);
INSERT INTO Ben_Jing_Note VALUES (95, '别录', '疗虚劳洒洒如疟，羸瘦，四肢酸疼，腰脊痛，小便数利，泄精溺血，破瘀血在腹，散石淋，痈肿，骨中热疽痒，安胎下气，杀鬼精物，久服耐老，不可近丈夫阴令痿。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 111);
INSERT INTO Ben_Jing_Note VALUES (96, '别录', '丈夫小腹痛，虚劳羸瘦，阴气不足，脚酸不能久立，养肝气。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 112);
INSERT INTO Ben_Jing_Note VALUES (97, '灵胎', '疏肝养窜，鸡金消积。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 113);
INSERT INTO Ben_Jing_Note VALUES (98, '唐本', '主心腹热胀，口干渴。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 114);
INSERT INTO Ben_Jing_Note VALUES (99, '别录', '蜜蜂子治心腹痛，面目黄，大人小儿腹中五虫从口吐出者。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 115);
INSERT INTO Ben_Jing_Note VALUES (100, '甄权', '蜜蜡主孕妇胎动，下血不绝欲死，以鸡子大煎三五沸，投美酒半升服立瘥。又主白发镊去，消腊点孔中，即生黑者。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 116);
INSERT INTO Ben_Jing_Note VALUES (101, '别录', '疗妇人乳内闭。肿痛，根治恶疮疗淋，利小便。解蜀椒毒。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 117);
INSERT INTO Ben_Jing_Note VALUES (102, '别录', '惊恚气，心腹痛不可久立，骨中寒热，伤寒劳复；或肌体寒热欲死，以作汤良，久服益气资智，烧灰治小儿头疮难燥，女子阴疮。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 118);
INSERT INTO Ben_Jing_Note VALUES (103, '别录', '疗男子虚损，四脏气微，梦寐失精，遗溺，久服益气养神。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 119);
INSERT INTO Ben_Jing_Note VALUES (104, '丹溪', '主热痰湿痰老痰顽痰，疝气，白浊带下，香附末姜汁调服，主心痛。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 120);
INSERT INTO Ben_Jing_Note VALUES (105, '别录', '下胸胁气，客热头痛，补内伤大劳嘘吸，强阴，利大小肠。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 92);
INSERT INTO Ben_Jing_Note VALUES (106, '孟铣', '主五脏不足，伤中，益十二经脉血气。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 123);
INSERT INTO Ben_Jing_Note VALUES (107, '别录', '补中益气，坚志强力，除烦闷，疗心下悬。除肠癖。 （痢疾的古名)', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 124);
INSERT INTO Ben_Jing_Note VALUES (108, '别录', '逐水，利小便。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 125);
INSERT INTO Ben_Jing_Note VALUES (109, '别录', '主皮肤中热气，使人润泽，散恶疮疝瘕，强阴。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 71);
INSERT INTO Ben_Jing_Note VALUES (110, '日华', '开胃助气。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 127);
INSERT INTO Ben_Jing_Note VALUES (111, '别录', '坚筋骨，明耳目，耐饥渴，疗金疮止痛，及伤寒温疟，大吐后虚热羸困。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 128);
INSERT INTO Ben_Jing_Note VALUES (112, '别录', '麻仁治中风汗出，逐水气，利小便，破积血，复血脉，乳妇产后余疾，沐发长润。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 129);
INSERT INTO Ben_Jing_Note VALUES (113, '别录', '疗妇人乳内闭肿痛。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 130);
INSERT INTO Ben_Jing_Note VALUES (114, '倪注', '主治因上焦不通造成之全身性水肿，专治膈上之疾，中风浊痰不出，无法吞咽。仲景方之瓜蒂散。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 131);
INSERT INTO Ben_Jing_Note VALUES (115, '别录', '肠澼，渴热中疾，恶疮，久服耐饥寒。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 133);
INSERT INTO Ben_Jing_Note VALUES (116, '别录', '疗疥虫疮，鼻中息肉，及绝筋破骨，百节中大风，积聚癖气，中恶腹痛，鬼疰，诸蛇毒，解藜芦毒。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 134);
INSERT INTO Ben_Jing_Note VALUES (117, '别录', '疗心腹积聚，邪气冷痛在胁，咳逆上气，脚冷疼弱无力，及鼻衄，恶疮下部虫疮，止血杀疥虫。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 135);
INSERT INTO Ben_Jing_Note VALUES (118, '别录', '下气坚肌，续绝补中，疗五痨七伤，虚损少气，止痢，久服悦泽不老，耐寒暑。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 1);
INSERT INTO Ben_Jing_Note VALUES (119, '别录', '以傅男子阴，阴消无气。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 137);
INSERT INTO Ben_Jing_Note VALUES (120, '别录', '除时气头痛身热，三焦大热，皮肤热，肠胃中结气，解肌发汗，止消渴烦逆腹胀，暴气喘息，咽热，亦可作浴汤。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 138);
INSERT INTO Ben_Jing_Note VALUES (121, '别录', '养肾脏，强骨气，益精除烦，通关节，消痈肿鼠瘘，颈核喉痛，小儿惊痫，炼水饮之。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 139);
INSERT INTO Ben_Jing_Note VALUES (122, '别录', '疗男子阴头寒，阴下湿痒，去汗臭，消水肿，令人有子。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 140);
INSERT INTO Ben_Jing_Note VALUES (123, '别录', '益气补虚损，疗脚弱疼冷，下焦伤竭，强阴。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 142);
INSERT INTO Ben_Jing_Note VALUES (124, '别录', '除胸膈中热气，食不下，止烦，去黑子，可以染皂。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 143);
INSERT INTO Ben_Jing_Note VALUES (125, '别录', '主胸痹，悦泽人面。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 149);
INSERT INTO Ben_Jing_Note VALUES (126, '别录', '养肝胆气，安五脏，除伏热，肠澼，止渴，醒酒，小便黄赤，疗恶疮下部虫。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 150);
INSERT INTO Ben_Jing_Note VALUES (127, '别录', '下气止呕咳，治气冲胸中，吐逆霍乱，疗脾不能消谷，止泄，除膀胱留热停水，起淋，利小便，去寸白虫。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 105);
INSERT INTO Ben_Jing_Note VALUES (128, '别录', '五脏邪气缓急，风胁痛，止好睡，通腠理，解肌泄邪恶气，消赤黑斑毒，不可多服，令人虚。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 152);
INSERT INTO Ben_Jing_Note VALUES (129, '东垣', '主利阴窍，治五淋，除水肿癃，泻肺。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 153);
INSERT INTO Ben_Jing_Note VALUES (130, '别录', '通顺血脉，缓中散恶血，逐贼血，去水气，利膀胱大小肠，消痈肿，时行寒热，中恶，腹痛腰痛。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 154);
INSERT INTO Ben_Jing_Note VALUES (131, '别录', '止心烦满，利大小便，长肌肤肥大。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 155);
INSERT INTO Ben_Jing_Note VALUES (132, '别录', '养肾气，逐膀胱邪逆，止霍乱，长毛发。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 156);
INSERT INTO Ben_Jing_Note VALUES (133, '别录', '暴中风伤寒，身热又满，狂邪忽忽不知人，温疟洒洒，血瘕，下寒血，除胸中气，下水，止烦渴，散颈下核，痈肿，心腹痛，坚症。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 157);
INSERT INTO Ben_Jing_Note VALUES (134, '别录', '疗小儿百病，诸痫热，口不开，大人狂癫，又堕胎，久服令人不忘。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 109);
INSERT INTO Ben_Jing_Note VALUES (135, '别录', '疗伤寒久疟烦热，胁下邪气，膈中恶，及风汗内疽，多服令人泄。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 159);
INSERT INTO Ben_Jing_Note VALUES (136, '别录', '疗腹中结实，心下满，洗洗恶恶，风目眩项直，咳嗽上气，止烦热渴，出汗。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 160);
INSERT INTO Ben_Jing_Note VALUES (137, '其他', '  ——行手足阳明，入手太阴而为阳明主药、故治头面诸疾。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 161);
INSERT INTO Ben_Jing_Note VALUES (138, '别录', '坚筋骨，消瘰疬赤痈，下部有疮，洗出虫，丈夫久服，令人有子。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 162);
INSERT INTO Ben_Jing_Note VALUES (139, '别录', '疗痰热，胃中热，小腹绞痛，消谷，利小肠，女子血闭，淋露下血，小儿腹痛。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 163);
INSERT INTO Ben_Jing_Note VALUES (140, '别录', '疗失溺不节，男女脚弱腰痛，风邪淋露，少气，目暗，坚脊，利俯仰，女子伤中关节重。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 164);
INSERT INTO Ben_Jing_Note VALUES (141, '别录', '平肾胃气，补阴气不足，失精茎冷，令人皮肤光泽有子。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 165);
INSERT INTO Ben_Jing_Note VALUES (142, '别录', '下五淋，客热在肠胃，止渴，坚筋，妇人崩中。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 166);
INSERT INTO Ben_Jing_Note VALUES (143, '别录', '通水道，疗肿胀满痛，以合膏疗小儿疮及面皻。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 167);
INSERT INTO Ben_Jing_Note VALUES (144, '别录', '除痈肿，浮肿，结热，风痹不足，产后痛。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 168);
INSERT INTO Ben_Jing_Note VALUES (145, '别录', '疗四肢不安，时行腹中大热，饮水欲走大呼，小儿惊痫，妇人产后余痛。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 169);
INSERT INTO Ben_Jing_Note VALUES (146, '别录', '温中止痛，除客血内塞，中风痉汗不出，湿痹中恶，客气虚冷，补五脏，生肌肉。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 151);
INSERT INTO Ben_Jing_Note VALUES (147, '别录', '疗肠胃大热，唾血衄血，肠中聚血，廱肿诸疮，止渴益精。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 171);
INSERT INTO Ben_Jing_Note VALUES (148, '东垣', '头面身体皮肤风湿。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 172);
INSERT INTO Ben_Jing_Note VALUES (149, '别录', '酸浆果主治烦热，定志益气，利水道，难产吞之，立产。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 170);
INSERT INTO Ben_Jing_Note VALUES (150, '本草', '主治风寒湿痹。腰痛久冷，关节老血。膀胱宿水，阴痿，茎痛，遗浊，痔瘘，恶疮。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 174);
INSERT INTO Ben_Jing_Note VALUES (151, '别录', '疗伤中淋露，下水气，利阴气，益精。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 175);
INSERT INTO Ben_Jing_Note VALUES (152, '别录', '疗诸邪气，热结鼠瘘，散痈肿流血，妇人带下，下乳汁不通，止小便数不禁，逐四肢骨节中水，治马谷刺人疮。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 176);
INSERT INTO Ben_Jing_Note VALUES (153, '别录', '止脓血诸瘘恶疮热疮．补绝伤，产后内塞，可作金疮膏，消酒除渴，明目。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 177);
INSERT INTO Ben_Jing_Note VALUES (154, '别录', '疗皮间积聚，暴溃瘤气，结热，利小便。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 178);
INSERT INTO Ben_Jing_Note VALUES (155, '甄权', '产后腹痛，频产血气衰冷，成劳瘦羸，妇人血沥腰痛。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 179);
INSERT INTO Ben_Jing_Note VALUES (156, '别录', '寒冷腹痛，中恶霍乱胀满，风邪诸毒，皮肤间结气，止唾血。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 147);
INSERT INTO Ben_Jing_Note VALUES (157, '别录', '除浮肿胪胀，痞满寒热。通身疼痛，及乳难喉痹，止涕泪。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 158);
INSERT INTO Ben_Jing_Note VALUES (158, '别录', '除时气头痛客热，五劳劳气，腰痛，风噤癫疾。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 182);
INSERT INTO Ben_Jing_Note VALUES (159, '别录', '疗水肿风肿，去膀胱热，伤寒热邪气，中风手脚挛急，通腠理，利九窍，止泄，散痈肿恶结，诸瘑疥癣虫疮。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 180);
INSERT INTO Ben_Jing_Note VALUES (160, '别录', '止烦下气，通膀胱满，补五劳，安五脏，去恶风，益精。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 173);
INSERT INTO Ben_Jing_Note VALUES (161, '别录', '女子阴中内伤。皮间积实。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 186);
INSERT INTO Ben_Jing_Note VALUES (162, '苏敬', '疗血胀下气，治杖疮捣汁涂之。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 187);
INSERT INTO Ben_Jing_Note VALUES (163, '藏器', '去邪除劳渴，冷风出汗，煮汁服之，捣烂醋和，敷疔肿毒。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 188);
INSERT INTO Ben_Jing_Note VALUES (164, '别录', '去肺中水气，唾血，热渴，水肿腹满胪胀，利水道，去寸白，可以缝金疮。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 190);
INSERT INTO Ben_Jing_Note VALUES (165, '别录', '治胸中痰热，咳逆上气。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 191);
INSERT INTO Ben_Jing_Note VALUES (166, '别录', '利五脏，去痰冷逆气，饮食不消，心腹诸冷绞痛，中恶心腹痛。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 192);
INSERT INTO Ben_Jing_Note VALUES (167, '别录', '疗目赤热疮，胸心大小肠大热，心中烦闷。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 193);
INSERT INTO Ben_Jing_Note VALUES (168, '别录', '除胸胁痰癖。逐停水，破结实，消胀满，心下急痞痛逆气，胁风痛，安胃气，止溏泄，明目。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 194);
INSERT INTO Ben_Jing_Note VALUES (169, '别录', '疗男子少精，妇人带下，小儿痫身热，可作洗目汤，久服皮肤光泽肥大有子。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 196);
INSERT INTO Ben_Jing_Note VALUES (170, '别录', '疗喉痹，吐逆，疝瘕，去老血，产后余疾，腹痛，出汗，利五脏。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 197);
INSERT INTO Ben_Jing_Note VALUES (171, '别录', '肠胃风邪，寒热疝瘕，头风，鼻塞，目黄耳聋，面疱下气，出汗强阴，益精，安五脏，通九窍，止小便利，久服明目强力。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 198);
INSERT INTO Ben_Jing_Note VALUES (172, '甄权', '解伤寒瘟疫大热，发汗，主肿胀满腹急痛。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 200);
INSERT INTO Ben_Jing_Note VALUES (173, '别录', '主五脏邪气，安志厌食，除蛊毒，去三虫。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 202);
INSERT INTO Ben_Jing_Note VALUES (174, '别录', '主中恶腹痛，去白虫，消皮肤风毒肿。令阴中解。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 203);
INSERT INTO Ben_Jing_Note VALUES (175, '大明', '煎膏消痈肿，续筋骨。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 204);
INSERT INTO Ben_Jing_Note VALUES (176, '别录', '消渴，喘息呼吸。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 181);
INSERT INTO Ben_Jing_Note VALUES (177, '别录', '疗虚劳洒洒如疟，羸瘦，四肢酸痛，腰脊痛，小便数利，泄经溺血，破瘀血在腹，散石淋，痈肿，骨中热，举疽痒，安胎下气，杀鬼精物，久服耐老，不可近丈夫阴令萎。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 206);
INSERT INTO Ben_Jing_Note VALUES (178, '别录', '疗小儿百病，诸痫热，口不开，大人狂癫，又堕胎，久服令人不忘。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 207);
INSERT INTO Ben_Jing_Note VALUES (179, '甄权', '产后奔血不定，淋沥，主热风风痫，大小便不利，肠中结实。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 199);
INSERT INTO Ben_Jing_Note VALUES (180, '别录', '温中益气，消痰下气，疗霍乱及腹痛胀满，胃中冷逆，胸中呕不止，泄痢淋露，除惊去留热，心烦满，厚肠胃。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 195);
INSERT INTO Ben_Jing_Note VALUES (181, '别录', '疗腹痛疝积，烧灰酒服。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 213);
INSERT INTO Ben_Jing_Note VALUES (182, '别录', '合乱发、蛇皮烧灰，以酒日服二方寸匕，治恶疽附骨痈，根在脏腑间，歴节肿出，疔肿恶脉，诸毒皆瘥。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 214);
INSERT INTO Ben_Jing_Note VALUES (183, '别录', '疗温疟，血瘕腰痛，小儿胁下坚。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 215);
INSERT INTO Ben_Jing_Note VALUES (184, '别录', '解结散血，愈漆创，养筋益气。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 216);
INSERT INTO Ben_Jing_Note VALUES (185, '灵胎', '清火散风。即今日之蝉。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 217);
INSERT INTO Ben_Jing_Note VALUES (186, '别录', '止咳逆，治脱肛，散淋结，头中风眩，痿躄，强阴益精。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 218);
INSERT INTO Ben_Jing_Note VALUES (187, '别录', '惊气入腹，腹痛环脐，丈夫阴中肿痛，令人有子，又止疮多脓汁不燥。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 219);
INSERT INTO Ben_Jing_Note VALUES (188, '别录', '女子崩中赤白，产后腹痛，灭诸疮瘢痕，为末封疔肿，拔根极效。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 220);
INSERT INTO Ben_Jing_Note VALUES (189, '别录', '主治五癃结气，利小便水道，破石淋，下血。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 223);
INSERT INTO Ben_Jing_Note VALUES (190, '药征', '瘀血，少腹鞭满，兼治发狂瘀热喜忘，及妇人经水不利。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 225);
INSERT INTO Ben_Jing_Note VALUES (191, '药征', '主治干血，故兼治少腹满痛，及妇人经水不利。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 227);
INSERT INTO Ben_Jing_Note VALUES (192, '别录', '五脏不足，味气结积，益气止痛，去黑皯，润肌肤皮毛。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 229);
INSERT INTO Ben_Jing_Note VALUES (193, '别录', '疗髓谷疽。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 234);
INSERT INTO Ben_Jing_Note VALUES (194, '别录', '疗伤寒时气寒热，热在肌肤，湿风注毒伏在骨间，食噎不通，久服强筋骨，轻身，起阴益气，利丈夫。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 210);
INSERT INTO Ben_Jing_Note VALUES (195, '本绖原文', '味辛，寒，无毒。治伏尸，毒螫，杀三虫。锡镜鼻，平。治女子血闭，症瘕，伏肠，绝孕。生山谷。一名解锡。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 237);
INSERT INTO Ben_Jing_Note VALUES (196, '别录', '带下百病，产难胞不出，堕胎，养血气，除五脏血脉中热，血痹，血痢，大人小儿惊气入腹，及阴痿不起。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 238);
INSERT INTO Ben_Jing_Note VALUES (197, '别录', '阴肿痛瘘下无子，泻痢。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 239);
INSERT INTO Ben_Jing_Note VALUES (198, '别录', '腰脊风寒，脚气寒冷弱，心腹冷痛，霍乱转筋，下痢赤白，温中，强阴，坚肌骨，又堕胎为百药长。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 242);
INSERT INTO Ben_Jing_Note VALUES (199, '元素', '主诸风，风痹血痹，半身不遂，除寒冷，温养脏腑，去心下坚痞，感寒腹痛。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 243);
INSERT INTO Ben_Jing_Note VALUES (200, '别录', '伤寒瘟疫头痛寒热诸毒气，令人骏健。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 211);
INSERT INTO Ben_Jing_Note VALUES (201, '别录', '消心腹胸膈痰热结满，咳嗽上气，心下急痛坚痞，时气呕逆，消痈肿，疗痿黄，悦泽面目，堕胎。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 245);
INSERT INTO Ben_Jing_Note VALUES (202, '大明', '主镇心安神，治伤寒毒气，反胃呕哕，蛇蜴所咬，炙熨之。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 236);
INSERT INTO Ben_Jing_Note VALUES (203, '别录', '平胃，下气，除痰实，肠间结热，心腹胀满，女子寒血闭、小腹痛，诸老血留结。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 247);
INSERT INTO Ben_Jing_Note VALUES (204, '别录', '下膀胱水伏留热气，皮间邪水上出面目浮肿，身暴中风，热沸痒，利小便，久服令人虚。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 248);
INSERT INTO Ben_Jing_Note VALUES (205, '别录', '利五脏肠胃，补血气，除寒热风痹，温中消谷，疗喉咽痛，下蛊毒。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 249);
INSERT INTO Ben_Jing_Note VALUES (206, '别录', '止遗漏，热气疮痒如麻豆，可作浴汤。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 60);
INSERT INTO Ben_Jing_Note VALUES (207, '别录', '消胸上痰结，唾如胶漆，心胸痰水，膀胱留饮，风气湿痹，皮间死肉，利大肠，通血脉，益色泽。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 252);
INSERT INTO Ben_Jing_Note VALUES (208, '别录', '疗哕逆喉痹不通，鼻中息肉，马刀乱疮，不入汤用。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 253);
INSERT INTO Ben_Jing_Note VALUES (209, '别录', '疗老血在心脾间，咳唾言语气臭，散胸中热气。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 255);
INSERT INTO Ben_Jing_Note VALUES (210, '别录', '疗心腹邪气，腹痛湿痹，养胎，利小儿。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 256);
INSERT INTO Ben_Jing_Note VALUES (211, '别录', '疗鬼蛊往来，水胀，洒洒恶寒，鼠瘘', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 257);
INSERT INTO Ben_Jing_Note VALUES (212, '别录', '下五水，散膀胱留热，皮中痞热气肿满。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 258);
INSERT INTO Ben_Jing_Note VALUES (213, '别录', '杀火毒。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 259);
INSERT INTO Ben_Jing_Note VALUES (214, '甄权', '治肝脏热毒充眼，赤障青盲翳肿，恶疮疥疮。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 260);
INSERT INTO Ben_Jing_Note VALUES (215, '甄权', '除白癣疥虫，结热不消，阴下痿，面上皯疱。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 262);
INSERT INTO Ben_Jing_Note VALUES (216, '别录', '颈腋痈肿头痛，发汗，利大小便。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 263);
INSERT INTO Ben_Jing_Note VALUES (217, '别录', '利大小肠，明目。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 264);
INSERT INTO Ben_Jing_Note VALUES (218, '别录', '去寸白，破症瘕，除头风，止金疮。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 265);
INSERT INTO Ben_Jing_Note VALUES (219, '别录', '疗痰饮咳嗽', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 266);
INSERT INTO Ben_Jing_Note VALUES (220, '甄权', '治浮风搔痒，煎汁洗恶疮。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 267);
INSERT INTO Ben_Jing_Note VALUES (221, '别录', '邪气鬼疰，蛊毒。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 268);
INSERT INTO Ben_Jing_Note VALUES (222, '别录', '疗胸邪气，水肿痿痹，腹满洪，直疏五臓，散水气。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 269);
INSERT INTO Ben_Jing_Note VALUES (223, '别录', '浸淫疽痔，杀虫。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 270);
INSERT INTO Ben_Jing_Note VALUES (224, '别录', '疗女子阴蚀。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 271);
INSERT INTO Ben_Jing_Note VALUES (225, '别录', '除胸下积癖。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 272);
INSERT INTO Ben_Jing_Note VALUES (226, '别录', '治鼻衄。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 273);
INSERT INTO Ben_Jing_Note VALUES (227, '灵胎', '养血明日。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 228);
INSERT INTO Ben_Jing_Note VALUES (228, '别录', '治阴下湿，风眩。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 246);
INSERT INTO Ben_Jing_Note VALUES (229, '别录', '癫疾痈疮，除蚀，下三虫，去蛇毒。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 280);
INSERT INTO Ben_Jing_Note VALUES (230, '别录', '下三虫。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 281);
INSERT INTO Ben_Jing_Note VALUES (231, '灵胎', '泻湿热，散结气。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 284);
INSERT INTO Ben_Jing_Note VALUES (232, '别录', '消胸中痰水喜唾，水肿，五水在五脏皮肤，及腰痛，下寒毒，肉毒，根疗疥疮，可用毒鱼。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 285);
INSERT INTO Ben_Jing_Note VALUES (233, '别录', '疗女子月闭烂胎，金疮脓血，不利丈夫，杀班蝥蛇虺毒，可练饵之益血脉。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 286);
INSERT INTO Ben_Jing_Note VALUES (234, '别录', '除六腑寒冷，伤寒温疟，大风汗不出，心腹留饮宿食，肠澼下痢，泄精，女子字乳余疾，散风邪瘕结，水肿黄疸鬼疰蛊毒，杀虫鱼毒，开腠理，通血脉，坚齿发，明目，调关节，耐寒暑，可作膏药。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 287);
INSERT INTO Ben_Jing_Note VALUES (235, '别录', '疗腹胀满，消谷，除咳嗽，囊结，妇人胞不落，明目益精，可为沐药不入汤。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 288);
INSERT INTO Ben_Jing_Note VALUES (236, '别录', '主痂疥，恶疮，金疮，实主溃脓，逐脓血，子汁疗渴。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 289);
INSERT INTO Ben_Jing_Note VALUES (237, '别录', '苦楝皮治疣虫，利大小肠。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 290);
INSERT INTO Ben_Jing_Note VALUES (238, '孟铣', '破癖气，下四肢水，酒服四十九粒，能泻结气。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 291);
INSERT INTO Ben_Jing_Note VALUES (239, '别录', '作摩膏除小儿百病，逐邪气恶风汗出，除皮中热结，积虫毒，白虫寸白，自出不止，久服令人阴痿。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 292);
INSERT INTO Ben_Jing_Note VALUES (240, '别录', '桐木皮疗奔豚气病。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 293);
INSERT INTO Ben_Jing_Note VALUES (241, '别录', '疗目中疾，主吐逆胃反，小儿热疮，身头烦热，蚀疮，煎汤浴之，并捣敷。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 294);
INSERT INTO Ben_Jing_Note VALUES (242, '别录', '疗头面风，去来疼痛，心腹结聚，关节重，不能步行，除骨间痛，长阴气，强心，令人勇武力作不倦。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 244);
INSERT INTO Ben_Jing_Note VALUES (243, '大明', '治水肿腹胀满。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 298);
INSERT INTO Ben_Jing_Note VALUES (244, '别录', '大人五邪，言语僻越，止呕逆，明目，烧之疗诸恶疮。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 307);
INSERT INTO Ben_Jing_Note VALUES (245, '藏器', '主赤白久热痢，取一升炒烟尽，沃汁半升滤净饮之。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 308);
INSERT INTO Ben_Jing_Note VALUES (246, '别录', '堕胎。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 310);
INSERT INTO Ben_Jing_Note VALUES (247, '别录', '血积，伤人肌，治疥癣，堕胎。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 311);
INSERT INTO Ben_Jing_Note VALUES (248, '别录', '温病寒热，解肌，散结热。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 312);
INSERT INTO Ben_Jing_Note VALUES (249, '主冶', '为解热镇惊退翳之药，治小儿热惊，皮肤风热瘾疹。目生翳瘴，治小儿哑病及阴肿。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 314);
INSERT INTO Ben_Jing_Note VALUES (250, '时珍', '治大小便不通，下利赤白，脱肛，一切痔瘘疔肿，附骨疽疮，灸疮出血不止，鼻中息肉，小儿重舌。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 315);
INSERT INTO Ben_Jing_Note VALUES (251, '日华', '治水肿，头面肿。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 316);
INSERT INTO Ben_Jing_Note VALUES (252, '主冶', '鼠妇主利水道，去瘀积，治久疟寒热，妇人月闭，能堕胎。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 319);
INSERT INTO Ben_Jing_Note VALUES (253, '别录', '惊痫，心下烦热，风气往来，时行头痛，解肌，消心下急满痛，杀狗毒。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 322);
INSERT INTO Ben_Jing_Note VALUES (254, '药征', '主治心中懊恼，旁治心中结痛及心中满而烦也。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 323);
INSERT INTO Ben_Jing_Note VALUES (255, '苏敬', '利石淋，吐呀嗽。囊结疰蛊，痰饮。又煮汁渍阴，疗小便不通。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 324);
INSERT INTO Ben_Jing_Note VALUES (256, '本经', '主女子赤沃，止血养精，保血脉，益气，令人肥健，嗜食。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 325);
INSERT INTO Ben_Jing_Note VALUES (257, '其他', '  ——即戎盐，补肾，泻血热。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 327);
INSERT INTO Ben_Jing_Note VALUES (258, '唐本', '主冷气。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 328);
INSERT INTO Ben_Jing_Note VALUES (259, '其他', '  ——本经无记载。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 329);
INSERT INTO Ben_Jing_Note VALUES (260, '其他', '  ——本经无记载。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 330);
INSERT INTO Ben_Jing_Note VALUES (261, '其他', '  ——本经无记载。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 331);
INSERT INTO Ben_Jing_Note VALUES (262, '其他', '  ——本经无记载。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 332);
INSERT INTO Ben_Jing_Note VALUES (263, '其他', '  ——本经无记载。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 333);
INSERT INTO Ben_Jing_Note VALUES (264, '其他', '  ——补中益气。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 334);
INSERT INTO Ben_Jing_Note VALUES (265, '别录', '去白虫。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 276);
INSERT INTO Ben_Jing_Note VALUES (266, '其他', '  ——又名香豉。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 336);
INSERT INTO Ben_Jing_Note VALUES (267, '其他', '  ——（鸡矢白散）                    ', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 337);
INSERT INTO Ben_Jing_Note VALUES (268, '别录', '止咳逆上气，消心下坚硬，除卒暴击血，通月血，止心腹痛。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 321);
INSERT INTO Ben_Jing_Note VALUES (269, '开宝', '主破血止痛，疗金疮杖疮，诸恶疮，痔瘘，卒下血，目中翳晕痛肤赤。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 339);
INSERT INTO Ben_Jing_Note VALUES (270, '产池', '产西南热地及西藏等处，为树中流出之脂液，凝结成块者，入药。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 340);
INSERT INTO Ben_Jing_Note VALUES (271, '其他', '  ——粳：硬也，糯：懦也、补脾清肺。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 341);
INSERT INTO Ben_Jing_Note VALUES (272, '其他', '  ——本经无记载。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 342);
INSERT INTO Ben_Jing_Note VALUES (273, '其他', '  ——本经无记载。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 343);
INSERT INTO Ben_Jing_Note VALUES (274, '其他', '  ——本经无记载。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 344);
INSERT INTO Ben_Jing_Note VALUES (275, '唐本', '主血积下气，生肌止血，破恶血，血淋，尿血，金疮。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 346);
INSERT INTO Ben_Jing_Note VALUES (276, '别录', '疗脾疽常欲眠，心烦哕，出音声，治耳聋，散痈肿诸结不消，及金疮恶疮，鼠瘘踒折，齆鼻息肉，堕胎。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 347);
INSERT INTO Ben_Jing_Note VALUES (277, '其他', '  ——主止血行血。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 348);
INSERT INTO Ben_Jing_Note VALUES (278, '其他', '  ——调中助阳，散血生肌，泄下焦太肠气滞。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 349);
INSERT INTO Ben_Jing_Note VALUES (279, '其他', '  ——长于降气，下痰止嗽，治肺气壅实，胸膈逆满。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 350);
INSERT INTO Ben_Jing_Note VALUES (280, '其他', '  ——养心除烦，利溲止血。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 352);
INSERT INTO Ben_Jing_Note VALUES (281, '别录', '除留热在关节，营卫虚热去来不定，烦满心痛气结，止汗止渴，除老血，疗泄精，涩大小肠，止大小便，治喉痹，咳嗽，心胁下痞热。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 353);
INSERT INTO Ben_Jing_Note VALUES (282, '其他', '  ——现无资料可查（出于王不留行散中）。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 338);
INSERT INTO Ben_Jing_Note VALUES (283, '别录', '主吐血，衄血，痢血，崩中赤白，益气，令人耐寒暑，去湿痹生肌。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 335);
INSERT INTO Ben_Jing_Note VALUES (284, '其他', '  ——辛能散气，甘调中，温开胃，化水谷，消积滞。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 351);
INSERT INTO Ben_Jing_Note VALUES (285, '大明', '补中益气，补劳。长毛发令黑不老，兼去蒜髪，杀风毒，心痛热黄，生捣汁服并贴之。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 251);
INSERT INTO Ben_Jing_Note VALUES (286, '别录', '鲤鱼煮食治咳逆上气，黄疸，止渴，治水肿脚满，下气。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 122);
INSERT INTO Ben_Jing_Note VALUES (287, '藏器', '食之令人好颜色，榨汁涂发不白。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 126);
INSERT INTO Ben_Jing_Note VALUES (288, '别录', '通血脉，止烦满，消渴，益精神，悦泽人面。除中恶腹痛，毒气疥廔诸疮。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 356);
INSERT INTO Ben_Jing_Note VALUES (289, '容川', '为去暑之要药，去暑只宜清气利湿，不可用燥药，如黄连亦然，甘草也可。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 4);
INSERT INTO Ben_Jing_Note VALUES (290, '好古', '实大肠。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 10);
INSERT INTO Ben_Jing_Note VALUES (291, '大明', '作枕明目，叶亦明目，生熟都可食。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 16);
INSERT INTO Ben_Jing_Note VALUES (292, '元素', '治肺胃阳气不足，肺气虚促短气少气，补中缓中。泻心肺脾胃中火邪，止渴生津液。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 17);
INSERT INTO Ben_Jing_Note VALUES (293, '甄权', '治耳鸣，头风泪下，鬼气，杀虫，恶疮疥掻。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 15);
INSERT INTO Ben_Jing_Note VALUES (294, '甄权', '主腹中冷痛，治惊痫除腹胀满，补益五脏，肾气内伤，令人不痿，主妇人血沥腰痛，凡虚而多热者加用之。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 19);
INSERT INTO Ben_Jing_Note VALUES (295, '大明', '助心胆气，强筋骨，长志安魂定魄，治惊悸劳力，心肺损吐血，鼻血，妇人崩中血晕。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 20);
INSERT INTO Ben_Jing_Note VALUES (296, '甄权', '治心腹胀满，腹中冷痛，胃虚下利，多年气痢，除寒热止呕逆。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 21);
INSERT INTO Ben_Jing_Note VALUES (297, '大明', '补五劳七伤，治鬼交泄精，尿血，润心肺。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 22);
INSERT INTO Ben_Jing_Note VALUES (298, '甄权', '治阴痿，补肾助十二经脉，逐恶血。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 23);
INSERT INTO Ben_Jing_Note VALUES (299, '吴瑞', '舂仁生食补中益气，通血脉，填精髓，止渴，润肝。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 24);
INSERT INTO Ben_Jing_Note VALUES (300, '甄权', '时疾寒热，内补不足，去虚劳客热，头痛不安加而用之良。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 25);
INSERT INTO Ben_Jing_Note VALUES (301, '甄权', '主痃癖气块，膀胱宿水，血气瘤大如盆者，悉能消散。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 26);
INSERT INTO Ben_Jing_Note VALUES (302, '灵胎', '养肝明目。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 14);
INSERT INTO Ben_Jing_Note VALUES (303, '藏器', '去心热，止烦热寒热体劳，下痰饮，合车前地黄丸服去湿痹。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 28);
INSERT INTO Ben_Jing_Note VALUES (304, '甄权', '治头风镇肝，瞳仁破者，得再见物。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 6);
INSERT INTO Ben_Jing_Note VALUES (305, '甄权', '去风毒，肝中风热，毒风冲眼，赤痛障翳，脑痛泪出，压丹石毒，去心胸烦热。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 30);
INSERT INTO Ben_Jing_Note VALUES (306, '大明', '治心腹一切气，膀胱冷痛，呕逆反胃，霍乱泄泻，痢疾，健脾，消食安胎。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 31);
INSERT INTO Ben_Jing_Note VALUES (307, '甄权', '治诸中风湿冷，奔喘气逆，皮肤苦痒，手足挛痛，劳损风毒齿痛。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 29);
INSERT INTO Ben_Jing_Note VALUES (308, '藏器', '炊饭做面食，主不饥，温气，煮饮止消渴，杀蛔虫。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 33);
INSERT INTO Ben_Jing_Note VALUES (309, '甄权', '养肺气，治惊痫，蚀脓。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 11);
INSERT INTO Ben_Jing_Note VALUES (310, '甄权', '肺气咳逆，喘息促急，肺痿生痈吐脓，除热气通肾，止消渴去热，中风，治湿疥，宜久服煮食之，令人肌体滑泽白净，除身上一切恶气不洁之疾。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 18);
INSERT INTO Ben_Jing_Note VALUES (311, '甄权', '治小儿壮热骨蒸，惊痫入心，时疾热黄，痈肿口干。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 36);
INSERT INTO Ben_Jing_Note VALUES (312, '甄权', '添胆气，治咳嗽，去皮风湿痒。风眼泪下，除齿痛，血闭，妇人血沥腰痛。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 37);
INSERT INTO Ben_Jing_Note VALUES (313, '甄权', '治热劳骨蒸烦疼，热气，肩背疼痛，劳乏羸瘦，下气消食，宣畅气血，主时疾内外热不解，单煮服食良。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 27);
INSERT INTO Ben_Jing_Note VALUES (314, '甄权', '男子夜梦鬼交泄精，强阴，下气，治风癞。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 39);
INSERT INTO Ben_Jing_Note VALUES (315, '弘景', '叶做羹饮，可疗劳。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 40);
INSERT INTO Ben_Jing_Note VALUES (316, '甄权', '破积散坚，治腹胀，破血下瘰疬。泻得根出。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 2);
INSERT INTO Ben_Jing_Note VALUES (317, '甄权', '益气，主男子阴痿不起，治心腹胀满。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 43);
INSERT INTO Ben_Jing_Note VALUES (318, '甄权', '补五劳七伤，去冷风，镇心神，安魂魄，补心气不足，开达心孔多记事。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 47);
INSERT INTO Ben_Jing_Note VALUES (319, '苏颂', '疗肿毒。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 48);
INSERT INTO Ben_Jing_Note VALUES (320, '甄权', '益气除热，男女腰脚软弱，健阳，逐皮肌风痹，骨中久疼，补肾益力。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 38);
INSERT INTO Ben_Jing_Note VALUES (321, '苏颂', '作饮止泄泻。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 50);
INSERT INTO Ben_Jing_Note VALUES (322, '大明', '治五劳七伤益气，止心腹痛，惊悸烦燥，润心肺，长肉止血，天行热疾，止盗汗，并疮疥，猪肚蒸为丸，治小儿疳气，安疣杀虫。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 51);
INSERT INTO Ben_Jing_Note VALUES (323, '大明', '主身益精，疗水脏冷，小便多，止遗沥泄精，溺血肿痛。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 53);
INSERT INTO Ben_Jing_Note VALUES (324, '甄权', '主虚喘，肾衰耳聋，疗寒热，治发背，内补。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 54);
INSERT INTO Ben_Jing_Note VALUES (325, '甄权', '益髓悦颜色，大补壮阳，治女子血崩。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 55);
INSERT INTO Ben_Jing_Note VALUES (326, '大明', '治三十六般风，男子一切劳劣，补中益神，风赤眼，止冷泪，及瘫痪，通利五脏关脉，五劳七伤，羸损盗汗，心烦体重，能安神定志匀气脉。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 56);
INSERT INTO Ben_Jing_Note VALUES (327, '大明', '妇人带下，月侯不匀，血气心腹痛，妊妇下血，坠眙，血运，血症，儿枕气痛，颠扑血闷，排脓，疮疖，游风肿毒，下乳汁，止泄精。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 57);
INSERT INTO Ben_Jing_Note VALUES (328, '括要', '固肠胃，有收敛之能，下胞衣，无推荡之峻。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 12);
INSERT INTO Ben_Jing_Note VALUES (329, '大明', '助阳气，补五劳七伤，鬼疥（病），通血脉，开窍。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 42);
INSERT INTO Ben_Jing_Note VALUES (330, '别录', '蔷薇根主泄痢腹痛、五脏客热，除邪逆气，疽癞诸恶疮，金疮伤打，生肉复肌。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 61);
INSERT INTO Ben_Jing_Note VALUES (331, '唐本', '破血生肌，止鼻衄，杀三虫，除诸毒肿疔疮瘘痔金疮内射，身痒瘾疹不止者，皆之立已。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 62);
INSERT INTO Ben_Jing_Note VALUES (332, '甄权', '治肝热，风泪赤眼，每旦取一匙洗净，空心吞之，百日后夜见物光。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 63);
INSERT INTO Ben_Jing_Note VALUES (333, '弘景', '清酒饮，疗风痹，足软。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 64);
INSERT INTO Ben_Jing_Note VALUES (334, '甄权', '治六极伤，心肺吐血，泻血。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 65);
INSERT INTO Ben_Jing_Note VALUES (335, '苏敬', '主留血，疗疳蚀，杀虫。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 66);
INSERT INTO Ben_Jing_Note VALUES (336, '甄权', '治中下气，止呕逆，补虚劳，令人体悦泽。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 67);
INSERT INTO Ben_Jing_Note VALUES (337, '容川', '旋覆花味微咸，花黄色，滴露而生，得金之气多，得水之气少，故润肺而利金降痰。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 68);
INSERT INTO Ben_Jing_Note VALUES (338, '东垣', '其气清香，生津止渴，润肌肉，治消渴、胆痹。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 69);
INSERT INTO Ben_Jing_Note VALUES (339, '大明', '通泄五脏百病，及症结，天行热疾，头痛消肿毒，排脓润毛发。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 3);
INSERT INTO Ben_Jing_Note VALUES (340, '本经', '养肝胆，除寒热，杀白虫，疗头风，脑中寒，止烦渴，补不足，盛阴气。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 7);
INSERT INTO Ben_Jing_Note VALUES (341, '甄权', '风疹恶疮，小儿丹毒及发热。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 72);
INSERT INTO Ben_Jing_Note VALUES (342, '甄权', '主肾虚精自出，治五淋，宣通水道。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 34);
INSERT INTO Ben_Jing_Note VALUES (343, '括要', '下气杀虫，利小便，发汗通经，将根末吹入鼻中，能做轻嚏。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 74);
INSERT INTO Ben_Jing_Note VALUES (344, '大明', '润心肺，助喉声，滋毛发。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 75);
INSERT INTO Ben_Jing_Note VALUES (345, '藏器', '妇人服之，绝产无子。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 79);
INSERT INTO Ben_Jing_Note VALUES (346, '甄权', '治风毒，通血脉。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 81);
INSERT INTO Ben_Jing_Note VALUES (347, '大明', '安魂定魄，鬼附啼泣，疳疮游风肿毒。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 82);
INSERT INTO Ben_Jing_Note VALUES (348, '日华', '补中养气，润养五脏，补肺气，止心惊，利太小肠，耐寒暑，逐风湿气，游风头风，治劳气，产后羸困，催生落胞，细研涂发令长，白蜜蒸饵治百病。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 83);
INSERT INTO Ben_Jing_Note VALUES (349, '甄权', '去皮肌浮风，疝气下坠。治常欲眠。养肝气，宣五脏风气。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 84);
INSERT INTO Ben_Jing_Note VALUES (350, '甄权', '去冷风疼痛。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 88);
INSERT INTO Ben_Jing_Note VALUES (351, '元素', '补下焦不足，治沉寒痼冷之病，去荣卫中风寒，表虚自汗，春夏为禁药，秋冬下部腹痛。非此不能止。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 89);
INSERT INTO Ben_Jing_Note VALUES (352, '甄权', '松脂治骨节风，头眩，去死肌，变白，散水气，润五脏。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 90);
INSERT INTO Ben_Jing_Note VALUES (353, '灵胎', '清湿火疗疮痔。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 91);
INSERT INTO Ben_Jing_Note VALUES (354, '苏颂', '入吐风痰药最快。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 5);
INSERT INTO Ben_Jing_Note VALUES (355, '甄权', '治头风腰肾中冷，膀胱冷，浓宿水，兴阳道，去百邪鬼魅，小儿惊痫。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 93);
INSERT INTO Ben_Jing_Note VALUES (356, '甄权', '开胃止呕逆，善安心神，主肺痿痰壅，心腹胀满，小儿惊痫，女人热淋。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 94);
INSERT INTO Ben_Jing_Note VALUES (357, '别录', '烦心不得眠，脐上下痛，血转久泄，虚汗烦渴，补中益肝气，坚筋骨，助阴气。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 95);
INSERT INTO Ben_Jing_Note VALUES (358, '甄权', '男子阴痿及传茎上疮，治下血如鸡鸭肝片。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 96);
INSERT INTO Ben_Jing_Note VALUES (359, '甄权', '杀三虫，主女人经脉不通。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 97);
INSERT INTO Ben_Jing_Note VALUES (360, '甄权', '破逐恶风，四肢不遂，贼风伤人，软脚兼腰，主多年瘀血在皮肌，治痹湿内不足。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 98);
INSERT INTO Ben_Jing_Note VALUES (361, '甄权', '治贼风，长髭发。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 99);
INSERT INTO Ben_Jing_Note VALUES (362, '大明', '通关脉，治头痛，憎寒体噤，搔痒，入面脂生光泽。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 100);
INSERT INTO Ben_Jing_Note VALUES (363, '容川', '此药味酸枝繁，具木之性，生于桑树之上，桑者，木中之金也，寄生附之，故独得金木之间气，且根不粘土，纯感风气而生，故为清散风木之要药。此药附木而生，象胆附肝，味酸苦，得木火之味，能清肝胆之火，治风热，筋脉结等症，胆通三焦之网膜，外连于筋，寄生如藤附木，象人之筋，故治之。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 101);
INSERT INTO Ben_Jing_Note VALUES (364, '大明', '除风去热，消痰止渴，暖水脏。治中风失音，和桃仁葱汤浴可出汗。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 32);
INSERT INTO Ben_Jing_Note VALUES (365, '之才', '杀天雄附子乌头毒，煎汁饮之。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 35);
INSERT INTO Ben_Jing_Note VALUES (366, '甄权', '治男子女人虚湿痹，毒风屌痛。去男子腰痛，浴男子阴，去风冷，大益阳事。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 70);
INSERT INTO Ben_Jing_Note VALUES (367, '大明', '止血闷血运金疮伤风血痢，入药烧存性用，煎膏长肉，消瘀血。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 106);
INSERT INTO Ben_Jing_Note VALUES (368, '甄权', '逐邪气，安心神，止夜梦鬼交，虚而多梦纷纭，止冷痢，下脓血，女子崩中带下。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 107);
INSERT INTO Ben_Jing_Note VALUES (369, '日华', '治蛇蚕咬，沙蛊溪瘴毒，辟蛊气，杀脏腑虫，治疟疾，吐风痰，疗一切虚损恶病，纳子宫，暖水脏，止冷带下。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 108);
INSERT INTO Ben_Jing_Note VALUES (370, '甄权', '肾冷膂腰痛，人虚而身强直，风也，腰不利加重。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 102);
INSERT INTO Ben_Jing_Note VALUES (371, '日华', '补男子腰肾虚冷，脚膝无力，夜梦鬼交，精溢自出，女人崩中漏血，赤白带下，炙末空心服方寸匕，壮筋骨。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 111);
INSERT INTO Ben_Jing_Note VALUES (372, '药性', '坚筋骨，益气，止痢。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 112);
INSERT INTO Ben_Jing_Note VALUES (373, '白雄鸡', '味酸甘性微温无毒。主调中除邪，下气疗狂，安五脏利小便。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 113);
INSERT INTO Ben_Jing_Note VALUES (374, '孟铣', '治目中热膜明目，和枣肉巨藤末为丸含之，润肺气，五脏生津。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 114);
INSERT INTO Ben_Jing_Note VALUES (375, '藏器', '蜜蜂子主丹毒风疹，腹中留热，利大小便涩，去浮血，下乳汁，妇人带下病。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 115);
INSERT INTO Ben_Jing_Note VALUES (376, '甄权', '冬葵苗煮汁服利小肠，治时行黄病，干叶为末及烧灰服治金疮出血。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 117);
INSERT INTO Ben_Jing_Note VALUES (377, '甄权', '烧灰治脱肛。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 118);
INSERT INTO Ben_Jing_Note VALUES (378, '甄权', '炮熟空心食之，止小便利。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 119);
INSERT INTO Ben_Jing_Note VALUES (379, '甄权', '补精气，诸不足，易颜色，变白，明目安神。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 92);
INSERT INTO Ben_Jing_Note VALUES (380, '大明', '止渴去热，安心止痢。治腰痛及泄精，多食令人欢喜。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 123);
INSERT INTO Ben_Jing_Note VALUES (381, '孟铣', '小儿患秋痢，与蛀枣食之良。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 124);
INSERT INTO Ben_Jing_Note VALUES (382, '甄权', '除肠间水，调中治淋。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 125);
INSERT INTO Ben_Jing_Note VALUES (383, '甄权', '治阴卵颓疾；去风热，可做汤沐浴，与阳起石同服，主丈夫阴痿不起，补气益力。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 71);
INSERT INTO Ben_Jing_Note VALUES (384, '容川', '微甘而涩，能收湿气，故健脾。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 127);
INSERT INTO Ben_Jing_Note VALUES (385, '日华', '补中养气，润养五脏，补肺气，止心惊，利大小肠，耐寒暑，逐风湿气，游风头风，治劳气，产后羸困，催生落胞，细研涂发令长，白蜜蒸饵止百病。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 128);
INSERT INTO Ben_Jing_Note VALUES (386, '开宝', '主补虚劳，润五脏，疏风气，治大肠风热结涩，利大小便。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 129);
INSERT INTO Ben_Jing_Note VALUES (387, '孟铣', '出痈疽头。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 130);
INSERT INTO Ben_Jing_Note VALUES (388, '药征', '主治胸中有毒，欲吐而不吐也。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 131);
INSERT INTO Ben_Jing_Note VALUES (389, '嘉佑', '调十二经脉，霍乱后胃气烦逆，久服强力，虽冷甚益人。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 133);
INSERT INTO Ben_Jing_Note VALUES (390, '大明', '风邪癫痫岚瘴，一切虫兽伤。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 134);
INSERT INTO Ben_Jing_Note VALUES (391, '甄权', '下气，治腰肾久冷，除冷风顽痹，寒热，生用治疥癣，炼服主虚损泄精。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 135);
INSERT INTO Ben_Jing_Note VALUES (392, '甄权', '主下痢肠澼，补肾冷。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 1);
INSERT INTO Ben_Jing_Note VALUES (393, '藏器', '利水道，去热毒。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 137);
INSERT INTO Ben_Jing_Note VALUES (394, '甄权', '治伤寒头痛如裂，壮热皮如火燥，和葱煎茶去头痛。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 138);
INSERT INTO Ben_Jing_Note VALUES (395, '甄权', '补男子肾虚风虚身强，腰中不利，加而明之。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 139);
INSERT INTO Ben_Jing_Note VALUES (396, '甄权', '脚膝软弱，半身不遂，胞衣不下。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 49);
INSERT INTO Ben_Jing_Note VALUES (397, '附方', '面上疱——木兰皮一斤，细切酒渍百日晒干，栀子一斤，共为末，每服方寸匕，日三。 （肘后）。酒疸发斑——赤黑黄色，心下懊痛，足胫肿满，小便黄，由大醉当风，入水所致：木兰皮一两，黄花二两，为末，酒服方寸匕，日三。（肘后）。一一○、蕤核', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 104);
INSERT INTO Ben_Jing_Note VALUES (398, '大明', '治惊邪癫痫，小儿客忤，消食及冷气。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 143);
INSERT INTO Ben_Jing_Note VALUES (399, '甄权', '治天行上气呕逆，开胃下食，解酒毒。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 149);
INSERT INTO Ben_Jing_Note VALUES (400, '藏器', '通关节，去滞热，伤寒用之。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 73);
INSERT INTO Ben_Jing_Note VALUES (401, '甄权', '清痰涎，治上气咳嗽．开胃主气痢，破症瘕痃癖。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 105);
INSERT INTO Ben_Jing_Note VALUES (402, '甄权', '治身上毒风疹痹，皮肉不仁，主壮热温疫，山岚瘴气等。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 152);
INSERT INTO Ben_Jing_Note VALUES (403, '苏颂', '解诸毒，虫毒。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 153);
INSERT INTO Ben_Jing_Note VALUES (404, '甄权', '去诸温毒，通宣血脉。助气补五劳七伤，破症结瘀血，消肿毒，肠风痔瘘乳痈瘰疬，妇人产后一切病，胎漏，子宫冷，面黄虚肿，缩小便，止泄精尿血。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 59);
INSERT INTO Ben_Jing_Note VALUES (405, '苏敬', '疗金疮血内流，痈肿有效。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 155);
INSERT INTO Ben_Jing_Note VALUES (406, '大明', '主五淋，月经不通，破血块排脓。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 156);
INSERT INTO Ben_Jing_Note VALUES (407, '甄权', '热风头痛，伤寒劳复，暴结热，散瘿瘤瘰疬。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 157);
INSERT INTO Ben_Jing_Note VALUES (408, '甄权', '安魂定魄，辟鬼邪，卒中恶，小儿夜啼。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 109);
INSERT INTO Ben_Jing_Note VALUES (409, '甄权', '心烦燥闷，骨热劳往来，产后蓐劳，肾气劳。憎寒虚烦。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 159);
INSERT INTO Ben_Jing_Note VALUES (410, '大明', '消痰润心肺。末和沙糖丸含之，止嗽，烧灰油调，传人畜恶疮，敛疮口。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 160);
INSERT INTO Ben_Jing_Note VALUES (411, '别录', '疗风邪，久渴，吐呕，两胁满，头眩目痒。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 161);
INSERT INTO Ben_Jing_Note VALUES (412, '甄权', '治热毒风，皮肌烦躁，生疮，赤癞，眉脱，除大热，嗜睡，治腹中冷痛，中恶腹痛。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 150);
INSERT INTO Ben_Jing_Note VALUES (413, '甄权', '治热毒骨蒸，寒热往来，肠胃不利，破壅气，治五淋，令人宣畅，解热渴。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 163);
INSERT INTO Ben_Jing_Note VALUES (414, '甄权', '男子女人毒风软脚，肾气虚弱，续筋骨，补益男子。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 164);
INSERT INTO Ben_Jing_Note VALUES (415, '大明', '逐诸风，除心热燥。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 165);
INSERT INTO Ben_Jing_Note VALUES (416, '大明', '主妇人月经不匀，通血脉，淋沥。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 166);
INSERT INTO Ben_Jing_Note VALUES (417, '甄权', '治疮癣。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 167);
INSERT INTO Ben_Jing_Note VALUES (418, '甄权', '治毒风瘰痹，破多年凝血，能化脓为水，产后诸病，止腹痛，余疹烦渴。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 168);
INSERT INTO Ben_Jing_Note VALUES (419, '大明', '女人一切病，胎前产后诸疾，治风补劳，退热除烦，益气，惊狂头痛目赤，明目，肠风泻血，痔瘘，发背，疮疥。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 154);
INSERT INTO Ben_Jing_Note VALUES (420, '甄权', '止呕逆虚劳寒热，下痢腹痛，齿痛，女人沥血腰痛，崩中补诸不足。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 151);
INSERT INTO Ben_Jing_Note VALUES (421, '藏器', '主一切风，变白宜老。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 52);
INSERT INTO Ben_Jing_Note VALUES (422, '大明', '治皮肤疵皯，酒皻，粉刺，癫疾。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 172);
INSERT INTO Ben_Jing_Note VALUES (423, '弘景', '酸浆捣汁服，治黄病多效。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 170);
INSERT INTO Ben_Jing_Note VALUES (424, '别录', '阴痿失溺，老人五缓，关节老血。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 174);
INSERT INTO Ben_Jing_Note VALUES (425, '弘景', '治惊邪风狂痣病，百邪鬼魅。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 175);
INSERT INTO Ben_Jing_Note VALUES (426, '藏器', '捣汁服，主水肿，利小便。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 176);
INSERT INTO Ben_Jing_Note VALUES (427, '开宝', '止冷热痢，疳痢极效。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 177);
INSERT INTO Ben_Jing_Note VALUES (428, '甄权', '主泄经，寒嗽，壮元气，益阳事，通声。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 142);
INSERT INTO Ben_Jing_Note VALUES (429, '大明', '胎前产后百病，通九窍，利关节，养血气破宿血，消症瘕，通小肠，长肌肉，消扑损瘀血，治鼻血吐血，头风目痛，妇人劳瘦，丈夫面黄。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 179);
INSERT INTO Ben_Jing_Note VALUES (430, '甄权', '治腰肾间疼痛冷气，破血去风，通四肢关节，开五脏六腑，宣诸络脉，去风毒冷痹，夜多小便。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 147);
INSERT INTO Ben_Jing_Note VALUES (431, '甄权', '百邪鬼魅，涕泣不止。除心下急满痛，脚气热咳。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 158);
INSERT INTO Ben_Jing_Note VALUES (432, '甄权', '治冷气，散诸痛，女子经脉不通，血沥腰痛。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 182);
INSERT INTO Ben_Jing_Note VALUES (433, '甄权', '辟百邪鬼魅，治气急心下满，疝气下坠，疼痛卵肿，去腹中幽幽作声。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 178);
INSERT INTO Ben_Jing_Note VALUES (434, '日华', '治淋沥遗溺。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 173);
INSERT INTO Ben_Jing_Note VALUES (435, '苏敬', '主小儿惊，生毛发，捣涂漆疮。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 186);
INSERT INTO Ben_Jing_Note VALUES (436, '甄权', '单用治恶风贼风，口面歪斜，遍身瘰痹，心虚忘事，益力添精，辟邪毒气，通利血脉，传送五脏不足气，助脾胃。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 188);
INSERT INTO Ben_Jing_Note VALUES (437, '甄权', '治肺气喘满，虚劳客热，头痛，内补不足。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 190);
INSERT INTO Ben_Jing_Note VALUES (438, '甄权', '治吐血，热毒风，止消渴，压丹石毒。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 191);
INSERT INTO Ben_Jing_Note VALUES (439, '大明', '治丈夫绝阳无子，女人绝阴无子，老人昏耄，中年健忘，一切冷风劳气，筋骨挛急，四肢不仁，补腰膝，强心力。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 162);
INSERT INTO Ben_Jing_Note VALUES (440, '甄权', '去热毒风，除时疾热，解五种黄病，利五淋，通小便，解消渴，明目，主中恶，杀蟅虫毒。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 193);
INSERT INTO Ben_Jing_Note VALUES (441, '甄权', '解伤寒结胸，主上气喘咳，肾内伤冷，阴痿而有气加而用之。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 194);
INSERT INTO Ben_Jing_Note VALUES (442, '甄权', '痛风泪不止，作汤浴小儿身热，煎水澄清洗赤目极效。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 196);
INSERT INTO Ben_Jing_Note VALUES (443, '甄权', '治恶风遍身四肢，疗痹，口齿肿胀摇动，女人月闭不通，产后恶血痢，多年痢，疗腹中冷痛，生毛发，灭瘢。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 197);
INSERT INTO Ben_Jing_Note VALUES (444, '甄权', '霍乱转筋，胃冷吐泻，腹痛，产后心痛，治遍身瘰痹刺痛。腰脚软弱，利大肠壅气，肠风痔疾，杀三虫。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 192);
INSERT INTO Ben_Jing_Note VALUES (445, '元素', '治渴除湿，去心中懊憹。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 200);
INSERT INTO Ben_Jing_Note VALUES (446, '括要', '主补血气，养肌肉，益虚气，除健忘，治怔忡，开胃益脾，补虚长智。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 202);
INSERT INTO Ben_Jing_Note VALUES (447, '甄权', '破陈血，能落胎。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 203);
INSERT INTO Ben_Jing_Note VALUES (448, '甄权', '治一切热毒风，恶风，疮疥癣赤烂，眉发脱脆，皮肌急，壮热恶寒，解热黄，酒黄，急黄，谷黄，劳黄。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 169);
INSERT INTO Ben_Jing_Note VALUES (449, '甄权', '疗肺气心促，急热劳咳，连连不绝，涕唾稠粘，肺痿肺痈吐脓血。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 181);
INSERT INTO Ben_Jing_Note VALUES (450, '日华', '补男子腰肾虚冷，脚膝无力，夜梦鬼交，精溢自出，女人崩中瘘血，赤白带下，炙末空心酒服方寸匕，壮筋骨。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 206);
INSERT INTO Ben_Jing_Note VALUES (451, '甄权', '安魂定魄，辟邪魅，卒中恶，小儿夜啼。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 207);
INSERT INTO Ben_Jing_Note VALUES (452, '大明', '酒齄热毒风，刺风，妇人血膈游风崩中带下。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 199);
INSERT INTO Ben_Jing_Note VALUES (453, '毅明', '治反胃吐食。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 213);
INSERT INTO Ben_Jing_Note VALUES (454, '苏讼', '煎水治热病后毒气冲目，炙研和猪脂涂瘰疬成瘘。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 214);
INSERT INTO Ben_Jing_Note VALUES (455, '甄权', '宿食症块痃癖，冷瘕劳瘦，除骨热结实壅，下气，妇人瘘下五色，下瘀血。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 215);
INSERT INTO Ben_Jing_Note VALUES (456, '甄权', '治积年冷气，腹内雷鸣虚吼，宿食不消，去结水，破宿血，化水谷，止吐酸水，大温胃气，治冷痛，主妇人虚而尿白。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 195);
INSERT INTO Ben_Jing_Note VALUES (457, '甄权', '通月经，治ㄕ疰（由病死之人传染的病）鬼疰，腹痛百邪，鬼魅啼泣。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 218);
INSERT INTO Ben_Jing_Note VALUES (458, '日华', '疗血崩，杀虫。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 219);
INSERT INTO Ben_Jing_Note VALUES (459, '甄权', '治湿风，口面歪斜，手足拘痛，散留痰肺气喘嗽。木防己主治男子肢节中风，毒风不语，散结气痈肿温疟，风水肿，去膀胱热。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 180);
INSERT INTO Ben_Jing_Note VALUES (460, '容川', '蝱虫为飞而食血；故主行上下之血。可以攻血。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 225);
INSERT INTO Ben_Jing_Note VALUES (461, '甄权', '治脑骨痛，疗耳鸣，补肾气，兴阳道，坚阴茎，添精髓，止老人尿不节，治面上疮，能发汗，止月水不定。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 198);
INSERT INTO Ben_Jing_Note VALUES (462, '藏器', '止渴，调中，去痰，治瘴疟。止吐逆霍乱，除冷热利。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 229);
INSERT INTO Ben_Jing_Note VALUES (463, '甄权', '治病疥，蚀恶肉，止金疮血甚长。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 234);
INSERT INTO Ben_Jing_Note VALUES (464, '甄权', '治心腹坚胀，散瘀血，治妇人血闭不通。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 171);
INSERT INTO Ben_Jing_Note VALUES (465, '别录', '去鳖瘕。疗恶疮，止小便利，堕胎。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 237);
INSERT INTO Ben_Jing_Note VALUES (466, '灵胎', '驱风逐痰。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 220);
INSERT INTO Ben_Jing_Note VALUES (467, '甄权', '疗女子血结。涩肠，止痢。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 239);
INSERT INTO Ben_Jing_Note VALUES (468, '元素', '温暖脾胃，除脾湿，肾寒补下焦之阳虚。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 242);
INSERT INTO Ben_Jing_Note VALUES (469, '大明', '安胎健脾，止反胃，吐血，鼻衄，月经不止，肠风痔瘘泻痢，脱精，遗溺，小儿惊痫疳疾，金疮长肉，辟鬼魅。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 238);
INSERT INTO Ben_Jing_Note VALUES (470, '药性', '辟中恶毒气，镇心神，解大热，散风毒，治发背痈疽疮肿，化脓作水，疗时疾热如火，烦毒入心，狂言妄语。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 211);
INSERT INTO Ben_Jing_Note VALUES (471, '甄权', '通女子经候，利水肿，利大小肠，贴热肿毒，小儿寒热，时疾烦热蚀脓。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 247);
INSERT INTO Ben_Jing_Note VALUES (472, '甄权', '疗肺壅土气咳嗽，止喘促，除肺中痰饮。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 248);
INSERT INTO Ben_Jing_Note VALUES (473, '甄权', '治下痢，破血积气，消聚痰涎，去肺热气促嗽逆，除腹中冷痛，主中恶及小儿惊痫。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 249);
INSERT INTO Ben_Jing_Note VALUES (474, '大明', '通小肠，泄精尿血，肠风，赤眼，小儿壮热，扑损续筋骨，乳痈瘰疠金疮，止血排脓，补血长肉，通经脉。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 60);
INSERT INTO Ben_Jing_Note VALUES (475, '甄权', '主水肿，逐大腹，开胃，止呕逆不下食。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 252);
INSERT INTO Ben_Jing_Note VALUES (476, '甄权', '主上气，去积年脓血泄利。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 253);
INSERT INTO Ben_Jing_Note VALUES (477, '甄权', '治疰气消瘀血，通女人月闭。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 255);
INSERT INTO Ben_Jing_Note VALUES (478, '甄权', '治小儿寒热丹疹。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 256);
INSERT INTO Ben_Jing_Note VALUES (479, '甄权', '治诸疟，吐痰涎，治项下瘤瘘。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 257);
INSERT INTO Ben_Jing_Note VALUES (480, '甄权', '能泻十二种水疾，去痰水。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 258);
INSERT INTO Ben_Jing_Note VALUES (481, '大明', '治发背瘰疬，面上疱疮，肠风痔瘘，血痢，刀箭疮扑损，生肌止痛。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 259);
INSERT INTO Ben_Jing_Note VALUES (482, '大明', '治五脏邪气，益脑髓，镇肝，明耳目，坚筋骨，去风寒湿痹。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 260);
INSERT INTO Ben_Jing_Note VALUES (483, '大明', '止惊邪，血，痫疾，风痹，赤眼，症结，温热，疟疾，发背，瘰疬，肠风痔瘘，扑损刀箭伤，汤火疮，生肌止痛。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 262);
INSERT INTO Ben_Jing_Note VALUES (484, '甄权', '下恶血癖块，腹内雷鸣，通月水，堕胎。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 263);
INSERT INTO Ben_Jing_Note VALUES (485, '大明', '止疟疾，消痰，退热。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 264);
INSERT INTO Ben_Jing_Note VALUES (486, '苏颂', '为末水服一钱，止鼻血有效。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 265);
INSERT INTO Ben_Jing_Note VALUES (487, '甄权', '治咳逆上气，喉中肿满，蛊毒，痃癖气块。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 266);
INSERT INTO Ben_Jing_Note VALUES (488, '大明', '杀腹臓一切虫，止赤白痢，煎服。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 267);
INSERT INTO Ben_Jing_Note VALUES (489, '甄权', '泻十种水病，喉痹不通，薄切醋炒涂喉外良。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 269);
INSERT INTO Ben_Jing_Note VALUES (490, '大明', '治癣，杀一切虫，醋磨贴肿毒。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 270);
INSERT INTO Ben_Jing_Note VALUES (491, '甄权', '煮汁饮小儿，疗蚘虫有验。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 271);
INSERT INTO Ben_Jing_Note VALUES (492, '大明', '治痰饮症瘕，亦杀鼠。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 272);
INSERT INTO Ben_Jing_Note VALUES (493, '甄权', '赤痢腹痛，齿痛，百节骨痛，项下瘿瘤。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 273);
INSERT INTO Ben_Jing_Note VALUES (494, '苏敬', '烧灰酒服可下死胎。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 228);
INSERT INTO Ben_Jing_Note VALUES (495, '甄权', '主疝瘕肠痛，伤寒时疾，强阴。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 246);
INSERT INTO Ben_Jing_Note VALUES (496, '大明', '治胎风手足搐，能吐泄瘰疬。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 280);
INSERT INTO Ben_Jing_Note VALUES (497, '甄权', '治疥癣，逐诸风，治百邪魁。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 281);
INSERT INTO Ben_Jing_Note VALUES (498, '容川', '此以时为治之药，夏枯草生于冬末，长于三春，正得水木之气，遇夏则枯，因木当火令，其气退谢，故可用以退肝胆经之火。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 284);
INSERT INTO Ben_Jing_Note VALUES (499, '甄权', '治心腹胀满，去水气寒痰，涕唾如胶，通利血脉，治恶风痹湿，一切毒风，四肢挛急，不能行步。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 285);
INSERT INTO Ben_Jing_Note VALUES (500, '药性', '治十种水肿，瘘痹落胎。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 286);
INSERT INTO Ben_Jing_Note VALUES (501, '甄权', '治头风下泪，腰脚不遂，虚损留结，破血下诸石水，治咳嗽腹内冷痛，除齿痛。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 287);
INSERT INTO Ben_Jing_Note VALUES (502, '甄权', '破坚症腹中痛，能堕胎，又将浸酒中取尽其精，煎成膏涂帛，贴一切肿痛。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 288);
INSERT INTO Ben_Jing_Note VALUES (503, '甄权', '主止血，治湿痹，四肢挛急膝痛。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 289);
INSERT INTO Ben_Jing_Note VALUES (504, '东垣', '入心及小肠，止上下腹痛。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 290);
INSERT INTO Ben_Jing_Note VALUES (505, '甄权', '主崩中。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 8);
INSERT INTO Ben_Jing_Note VALUES (506, '甄权', '逐风，主痫狂走。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 292);
INSERT INTO Ben_Jing_Note VALUES (507, '甄权', '桐木皮治五淋，沐发去头风，生发滋润。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 293);
INSERT INTO Ben_Jing_Note VALUES (508, '大明', '煎汤洗小儿壮热，一切疮疥，皮肤搔痒。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 294);
INSERT INTO Ben_Jing_Note VALUES (509, '苏敬', '下血及碎肉，除疝瘕积冷，九蒸酒渍，服三合。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 298);
INSERT INTO Ben_Jing_Note VALUES (510, '甄权', '喉痹，百鬼魅。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 307);
INSERT INTO Ben_Jing_Note VALUES (511, '日华', '小儿阴囊忽虚热肿痛，以生甘草汁入轻粉末调涂之，以盐研傅疮去热毒，及蛇犬伤。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 308);
INSERT INTO Ben_Jing_Note VALUES (512, '日华', '治癥瘕。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 310);
INSERT INTO Ben_Jing_Note VALUES (513, '甄权', '治瘰疬，通利水道。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 311);
INSERT INTO Ben_Jing_Note VALUES (514, '甄权', '伤寒狂热。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 312);
INSERT INTO Ben_Jing_Note VALUES (515, '孟铣', '治中风筋挛，附骨疼痛，作末蜜服治卒热闷，及热毒，利血，疝气，磨水涂肿毒。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 210);
INSERT INTO Ben_Jing_Note VALUES (516, '丹溪', '治口疮甚效。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 316);
INSERT INTO Ben_Jing_Note VALUES (517, '日华', '主气癃不得小便，妇人月闭血瘕，痫痓，寒热，利水道，堕胎。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 319);
INSERT INTO Ben_Jing_Note VALUES (518, '甄权', '治腹痹不通，发汗主温病角气，咳嗽上气，喘促，入天门冬煎润心肺，和酪作汤润声气。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 322);
INSERT INTO Ben_Jing_Note VALUES (519, '藏器', '煎汁滴鼻中出黄水，去伤冷鼻塞黄疸。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 324);
INSERT INTO Ben_Jing_Note VALUES (520, '藏器', '饮汁去小儿暴热，大人酒后热，鼻塞身热，去头中风热，利口齿，利大小肠。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 325);
INSERT INTO Ben_Jing_Note VALUES (521, '别录', '治目痛赤涩，吐血溺血，齿舌出血。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 327);
INSERT INTO Ben_Jing_Note VALUES (522, '甄权', '破胸膈结气，通利津液，止水道，黑髭发。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 328);
INSERT INTO Ben_Jing_Note VALUES (523, '开宝', '主产后血运口噤，腹内恶血不尽，绞痛，胎死腹中，并酒煮服，亦主蛊毒。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 329);
INSERT INTO Ben_Jing_Note VALUES (524, '别录', '小儿大腹丁奚，三年不能行者。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 330);
INSERT INTO Ben_Jing_Note VALUES (525, '别录', '疗寒热，热中消渴，止泄痢，利小便，下腹胀满，吐逆卒澼。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 331);
INSERT INTO Ben_Jing_Note VALUES (526, '弘景', '捣汁服止伤血，杀蚘虫。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 332);
INSERT INTO Ben_Jing_Note VALUES (527, '别录', '主下气，除寒热，其子尤良。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 333);
INSERT INTO Ben_Jing_Note VALUES (528, '别录', '主补虚乏，止渴，去血。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 334);
INSERT INTO Ben_Jing_Note VALUES (529, '甄权', '通利五淋，小便不通，除心家客热。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 276);
INSERT INTO Ben_Jing_Note VALUES (530, '倪注', '经方中之栀子豆豉汤主治心中懊憹，一般多用于病愈后之失眠烦躁。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 336);
INSERT INTO Ben_Jing_Note VALUES (531, '素问', '治臌胀。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 337);
INSERT INTO Ben_Jing_Note VALUES (532, '元素', '治血结血秘血燥，通润大便。破蓄血。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 321);
INSERT INTO Ben_Jing_Note VALUES (533, '李珣', '堕胎，产后心腹血气痛，并入丸散服。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 339);
INSERT INTO Ben_Jing_Note VALUES (534, '别录', '主风水毒肿，去恶气，伏尸，痈疹痒毒。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 340);
INSERT INTO Ben_Jing_Note VALUES (535, '仲景', '以清热补不足。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 341);
INSERT INTO Ben_Jing_Note VALUES (536, '别录', '李根白皮治消渴，止心烦，逆奔豚气。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 342);
INSERT INTO Ben_Jing_Note VALUES (537, '日华', '金鱼将死，入山漆叶（三七叶）汁于鱼口，即活，无名疮疖，捻叶敷之。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 343);
INSERT INTO Ben_Jing_Note VALUES (538, '好古', '补命门不足，肝风虚。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 243);
INSERT INTO Ben_Jing_Note VALUES (539, '甄权', '单用治女人宿血气心痛，冷气结聚，温醋摩傅之，亦治马胀。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 346);
INSERT INTO Ben_Jing_Note VALUES (540, '甄权', '治五淋，利小便，开关格，治人多唾，主水肿浮大。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 347);
INSERT INTO Ben_Jing_Note VALUES (541, '本经', '主金疮，疮败。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 349);
INSERT INTO Ben_Jing_Note VALUES (542, '别录', '主胸胁逆气，咳嗽上气，呼吸欲绝。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 350);
INSERT INTO Ben_Jing_Note VALUES (543, '别录', '主除客热，止烦渴咽燥，利小便，养肝气，止漏血唾血，令女子易孕。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 352);
INSERT INTO Ben_Jing_Note VALUES (544, '藏器', '粉身止大人小儿盗汗，同麻黄根蛇床子，干姜为粉去阴汗。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 353);
INSERT INTO Ben_Jing_Note VALUES (545, '甄权', '治冷风历节疼痛，止尿血。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 335);
INSERT INTO Ben_Jing_Note VALUES (546, '本草', '痰逆症结，泻利胀满：回乳下胎，治目病。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 351);
INSERT INTO Ben_Jing_Note VALUES (547, '容川', '青蒿色青味苦，正治肝胆之相火，其节中必生红虫，乃感风化而生之虫，故青蒿为去风清热之要药，人之痨虫皆因肝气相火，相煽而生，假血以成质，故必骨蒸而生痨虫，青蒿节以虫杀虫，消瘀去蒸，借虫以攻血，借风气以散郁火也。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 251);
INSERT INTO Ben_Jing_Note VALUES (548, '日华', '鲤鱼治妊娠身肿，及胎气不安。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 122);
INSERT INTO Ben_Jing_Note VALUES (549, '甄权', '主男子肾精虚竭阴痿，能令坚长，女子食之有子。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 126);
INSERT INTO Ben_Jing_Note VALUES (550, '甄权', '制者消痰下肺气，关胃健脾，止呕吐，去胸中痰。生者摩痈肿，除瘤瘿气。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 245);
INSERT INTO Ben_Jing_Note VALUES (551, '甄权', '补肾气，精乏腰痛，膝冷湿痹，子宫久冷，冷症寒瘕，止月水不定。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 140);
INSERT INTO Ben_Jing_Note VALUES (552, '别录', '治小儿惊痫，妇人生子不下，烧灰酒服，治久病。', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 314);
INSERT INTO Ben_Jing_Note VALUES (553, '甄权', '阳中结气，关格不通', 1, '2019-03-09 14:47:36', '2019-03-09 14:47:36', 291);
INSERT INTO Ben_Jing_Note VALUES (554, '甄权', '镇心，主抽风传ㄕ痨痵等。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 356);
INSERT INTO Ben_Jing_Note VALUES (555, '药征', '主治小便不利，旁治渴也。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 4);
INSERT INTO Ben_Jing_Note VALUES (556, '大明', '反胃利小便，主五劳七伤，补腰膝长肌肉，治妇人症瘕。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 21);
INSERT INTO Ben_Jing_Note VALUES (557, '灵胎', '补益肝脾，接续筋骨。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 22);
INSERT INTO Ben_Jing_Note VALUES (558, '大明', '安魂定魄，补五劳七伤，一切虚损，惊悸烦闷健忘，通九窍利百脉，益精养气壮筋骨。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 19);
INSERT INTO Ben_Jing_Note VALUES (559, '吴克潜', '曰近医多用于行血止血，如胎动下血，经行不止，小便混有鲜血，可熬膏用。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 24);
INSERT INTO Ben_Jing_Note VALUES (560, '弘景', '服诸石人不调和者，煮汁饮之。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 25);
INSERT INTO Ben_Jing_Note VALUES (561, '甄权', '治热毒大小，面目肢节浮肿，下水主泄积。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 28);
INSERT INTO Ben_Jing_Note VALUES (562, '大明', '钻孔取浆，点多年青盲，内障翳膜，养精气，其壳摩翳。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 6);
INSERT INTO Ben_Jing_Note VALUES (563, '灵胎', '专利小便。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 30);
INSERT INTO Ben_Jing_Note VALUES (564, '甄权', '九种心痛，积年冷气，痃症块，胀痛，壅气上冲，烦闷羸劣，女人血气刺心，痛不可忍，末酒服之。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 31);
INSERT INTO Ben_Jing_Note VALUES (565, '好古', '去肾间风邪，搜肝风，泻肝气，治项强，腰脊痛。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 29);
INSERT INTO Ben_Jing_Note VALUES (566, '甄权', '治肺痿，肺气积脓血，咳嗽涕唾上气，煎服破毒肿。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 33);
INSERT INTO Ben_Jing_Note VALUES (567, '灵胎', '补血降气。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 11);
INSERT INTO Ben_Jing_Note VALUES (568, '元素', '凉血生血，补肾水真阴，除皮肤燥，去诸湿热。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 20);
INSERT INTO Ben_Jing_Note VALUES (569, '大明', '客忤疳气，热狂，明目止渴治疥疮。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 36);
INSERT INTO Ben_Jing_Note VALUES (570, '容川', '人参多用于病后元气伤者、用人参佐他药来调理，病人邪去正脱者，用人参佐他药调理之，现今人参多人工栽植，药力不如野参，我们可用黄耆甘草代人参，方剂中多以党参代人参。  因生于阴湿之地，禀水阴润泽之气，故味苦甘而有汁液，发为三枒五叶，阳数也，因生于阴而出阳，故于甘苦味中而有一番生阳之气。人身之元气，由肾水之中而上达于肺，此亦生于阴而出阳，与人参同一理，因此人参大能化气，气化而上，出于口鼻，即为津液，所以人参能生津也。人参生于北方、有阴中之阳药称，有化气补津之功。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 17);
INSERT INTO Ben_Jing_Note VALUES (571, '大明', '除风下气，丈夫水脏女人血海冷败，多忘，除烦闷，止心腹痛，霍乱转筋及耳痛者，作末炒，趁热裹。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 15);
INSERT INTO Ben_Jing_Note VALUES (572, '元素', '养目血，去翳膜。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 16);
INSERT INTO Ben_Jing_Note VALUES (573, '大明', '镇心润五脏，补五劳七伤吐血，治咳嗽消痰，去风热烦闷。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 18);
INSERT INTO Ben_Jing_Note VALUES (574, '大明', '治腰膝软怯弱，破症结，排脓止痛，产后心腹痛，并血晕，落死胎。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 23);
INSERT INTO Ben_Jing_Note VALUES (575, '弘景', '含之去口臭。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 37);
INSERT INTO Ben_Jing_Note VALUES (576, '备要', '伤寒邪热，痰热结实，虚劳肌热，十二经疮疽血凝气聚，功同连翘。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 27);
INSERT INTO Ben_Jing_Note VALUES (577, '大明', '含咽治喉痹。黑疸，周身皮肤眼目俱现黑色，为疸症之最重者。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 2);
INSERT INTO Ben_Jing_Note VALUES (578, '时珍', '蘼芜花入面脂用。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 50);
INSERT INTO Ben_Jing_Note VALUES (579, '好古', '心病逆而盛，心积伏梁。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 51);
INSERT INTO Ben_Jing_Note VALUES (580, '毅民', '白疾藜治睛球凹陷，及睛生障翳，视物模糊。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 53);
INSERT INTO Ben_Jing_Note VALUES (581, '日华', '助气，壮筋骨，长肌肉补血，破症癖瘰疬瘿赘，肠风血崩带下，赤白痢，产前后一切病，月侯不匀。痰嗽，头风，热毒，赤目。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 54);
INSERT INTO Ben_Jing_Note VALUES (582, '大明', '男子绝阳不兴，女人绝阴不产，润五脏，长肌肉，暖腰膝，男子泄精血，遗沥，女子带下阴痛。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 55);
INSERT INTO Ben_Jing_Note VALUES (583, '元素', '治上焦风邪，泻肺实，散头目中滞气，经络中留湿，主上部见血。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 56);
INSERT INTO Ben_Jing_Note VALUES (584, '灵胎', '功能治温，分补五脏。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 12);
INSERT INTO Ben_Jing_Note VALUES (585, '括要', '口舌縻烂，蔷薇根煮汁温含，口疮日久，延及胸前生疮，三年以上不愈者，用此效，杨梅疮筋骨毒痛，取根皮煮汁多饮效，下疳用黄花蔷薇叶焙研洗之敷之良。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 61);
INSERT INTO Ben_Jing_Note VALUES (586, '注', '血瘕，留着肠胃之外及少腹间，下有积气，坚牢如石，女子多因经行未尽，饮食过度，血留经络而得之，治法不一。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 62);
INSERT INTO Ben_Jing_Note VALUES (587, '日华', '助肝气益精，以水调末涂肿毒，治太阳穴头痛，又贴胸心止鼻洪，作枕头治头风明目。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 63);
INSERT INTO Ben_Jing_Note VALUES (588, '大明', '养神定志，通利关脉，治冷热劳，骨结疼痛。四肢不遂，头痛赤眼，热温狂闷，破宿血，生新血，安生胎，落死胎，止崩带，调月经，血邪心烦，恶疮疥癣瘿赘，一切肿毒。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 64);
INSERT INTO Ben_Jing_Note VALUES (589, '大明', '止鼻洪尿血，产后血运，月经不止，带下，扑损瘀血，泄精，痔瘘疮节，排脓，酒煎服。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 65);
INSERT INTO Ben_Jing_Note VALUES (590, '萧炳', '小儿疳痢，为散水服，大效。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 66);
INSERT INTO Ben_Jing_Note VALUES (591, '大明', '明目，暖水脏，壮筋骨，治风，消食，反胃，霍乱转筋，痃癖，奔豚冷气，消水肿，心腹气胀，止渴除烦热，解酒毒。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 67);
INSERT INTO Ben_Jing_Note VALUES (592, '元素', '辛平能散肺气郁结，芬芳能去胃气郁滞。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 69);
INSERT INTO Ben_Jing_Note VALUES (593, '容川', '咸味虽重，尚未至于极，故犹有寒水石之性，能大下其火。凡盐味之咸。内皆有火热之性，而水中之火即是命门之火也。微咸之味者，皆能引火下行。大咸者能助火升发也。芒硝得水中阴凝之性，味咸能软坚，下气分之热，以其得水之阴味，未得水中之阳气，故降而不升，能降气分之热，是治热要药，与大黄入血分治火要药不同。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 3);
INSERT INTO Ben_Jing_Note VALUES (594, '弘景', '疗金疮止血。煎水浴小儿，去烦热惊风。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 72);
INSERT INTO Ben_Jing_Note VALUES (595, '别录', '蓝叶杀百药毒，解狼毒，射罔毒。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 48);
INSERT INTO Ben_Jing_Note VALUES (596, '括要', '面身瘢痕，真玉日日磨之则自灭。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 75);
INSERT INTO Ben_Jing_Note VALUES (597, '元素', '下乳汁。                    ', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 81);
INSERT INTO Ben_Jing_Note VALUES (598, '日华', '治一切风，疗水胀。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 39);
INSERT INTO Ben_Jing_Note VALUES (599, '廷飞', '炒食不食，风病人久食，则步履端正，语言不蹇。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 83);
INSERT INTO Ben_Jing_Note VALUES (600, '大明', '补虚止惊烦，益心肺：并一切恶疮疥癣及身痒，排脓消肿毒。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 84);
INSERT INTO Ben_Jing_Note VALUES (601, '丹溪', '横行手臂治痛风。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 88);
INSERT INTO Ben_Jing_Note VALUES (602, '汪昂', '木得桂而枯，又能抑肝风而扶脾土，脾虚恶食、湿盛泄泻，补劳通经。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 89);
INSERT INTO Ben_Jing_Note VALUES (603, '大明', '松脂除邪下气，润心肺，治耳聋。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 90);
INSERT INTO Ben_Jing_Note VALUES (604, '大明', '主头眩耳虚鸣，筋骨挛缩，通小肠，止尿血，主难产，补女人血海，令人有子。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 34);
INSERT INTO Ben_Jing_Note VALUES (605, '吴克潜', '胆矾有腐蚀性与收敛性，故能治恶疮，吐顽痰，喉痹欲死，研末醋调灌之，大吐胶痰而愈，治狐臭，胆矾枯矾各一钱，轻粉百药煎各五分，为细末敷之效，著名方剂有铁桶膏（铜绿胆矾五倍子白芷轻粉郁金麝香）治发背将溃时，根脚走散不收者，用以涂疮四周根上效。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 5);
INSERT INTO Ben_Jing_Note VALUES (606, '好古', '润肝。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 93);
INSERT INTO Ben_Jing_Note VALUES (607, '大明', '补五劳七伤，开心益志，止健忘，暖腰膝，安胎。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 94);
INSERT INTO Ben_Jing_Note VALUES (608, '甄权', '筋骨风，炒仁研汤服。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 95);
INSERT INTO Ben_Jing_Note VALUES (609, '藏器', '热疮疱起，虫疮，血痢，止消渴，杀蛀虫。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 96);
INSERT INTO Ben_Jing_Note VALUES (610, '大明', '强筋骨，主泄精健忘。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 47);
INSERT INTO Ben_Jing_Note VALUES (611, '大明', '明目下气，治中风骨节挛急。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 98);
INSERT INTO Ben_Jing_Note VALUES (612, '大明', '利关节，治痫疾，赤目。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 99);
INSERT INTO Ben_Jing_Note VALUES (613, '灵胎', '疏肝散风。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 100);
INSERT INTO Ben_Jing_Note VALUES (614, '甄权', '主怀孕漏血不止，令胎牢固。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 101);
INSERT INTO Ben_Jing_Note VALUES (615, '甄权', '生含咽津治急喉痹。疗鼻漏鼻血，瘰疬疥癣。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 32);
INSERT INTO Ben_Jing_Note VALUES (616, '甄权', '治健忘，安魂魄，令人不迷，坚壮阳道。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 35);
INSERT INTO Ben_Jing_Note VALUES (617, '大明', '暖丈夫阳气，女人阴气，治腰胯酸疼，四肢顽痹，缩小便，去阴汗，湿癣，齿痛，赤白带下。小儿惊痫，扑损瘀血，煎汤浴大风身痒。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 70);
INSERT INTO Ben_Jing_Note VALUES (618, '苏敬', '乱发烧灰，疗转胞小便不通，赤白痢，痈肿疔肿骨疽。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 106);
INSERT INTO Ben_Jing_Note VALUES (619, '日华', '怀孕漏胎，止肠风下血，鼻洪吐血，止泻痢渴疾，健脾，涩肠胃。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 107);
INSERT INTO Ben_Jing_Note VALUES (620, '弘景', '佩服及置枕间辟恶梦，及ㄕ疰鬼气，又疗蛇毒。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 108);
INSERT INTO Ben_Jing_Note VALUES (621, '大明', '治肾劳腰脊挛。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 102);
INSERT INTO Ben_Jing_Note VALUES (622, '日华', '壮筋骨，暖水脏，益智清气。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 38);
INSERT INTO Ben_Jing_Note VALUES (623, '藏器', '烦热，风疹，丹毒，瘴疟寒热，小儿结热，煮汁饮之。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 40);
INSERT INTO Ben_Jing_Note VALUES (624, '乌雄鸡', '味甘性微温无毒，主补中止痛，除心腹恶气，治风湿麻痹，诸虚羸，安胎，疗折伤。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 113);
INSERT INTO Ben_Jing_Note VALUES (625, '灵胎', '养胃和中。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 114);
INSERT INTO Ben_Jing_Note VALUES (626, '孟铣', '蜂蜜治心腹血气刺痛，及白痢，同生地黄汁各一匙服，即下。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 115);
INSERT INTO Ben_Jing_Note VALUES (627, '药征', '主治诸血症，故兼治心烦不得眠者。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 112);
INSERT INTO Ben_Jing_Note VALUES (628, '弘景', '壳主久嗽，断疟。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 118);
INSERT INTO Ben_Jing_Note VALUES (629, '吴克潜', '桑螵蛸为小儿梦中遗尿要药，得覆盆子菟丝子益智仁补骨脂等甚效，合龙骨疗滑精，著名方剂有桑螵蛸散（盐灸桑螵蛸远志肉龙骨盐炙菖蒲人参茯神醋炙龟板当归）治男子因女劳，小便日数十次，如稠米泔色，心神恍惚，有实效。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 119);
INSERT INTO Ben_Jing_Note VALUES (630, '孟铣', '坚筋骨，耐老，除风去虚劳，补精气。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 92);
INSERT INTO Ben_Jing_Note VALUES (631, '苏颂', '捣碎和米作粥饭食轻身益气，令人强健。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 123);
INSERT INTO Ben_Jing_Note VALUES (632, '大明', '润心肺，止嗽，补五脏，洽虚损，除肠胃癖气。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 124);
INSERT INTO Ben_Jing_Note VALUES (633, '苏颂', '时气痘疮不出，食之或研酒饮之效。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 125);
INSERT INTO Ben_Jing_Note VALUES (634, '日华', '治客热丹肿。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 71);
INSERT INTO Ben_Jing_Note VALUES (635, '元素', '治阳明头痛，补脾胃，去皮肤风邪，解肌肉间风热，疗肺痿咳唾脓血，能发浮汗。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 82);
INSERT INTO Ben_Jing_Note VALUES (636, '廷飞', '炒食不生风病，风人久食则步履端正，语言不蹇。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 128);
INSERT INTO Ben_Jing_Note VALUES (637, '孟铣', '取汁煮粥去五脏风，润肺，治关节不通，发落，产后余疾。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 129);
INSERT INTO Ben_Jing_Note VALUES (638, '弘景', '下丹石毒。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 130);
INSERT INTO Ben_Jing_Note VALUES (639, '臓器', '捣汁饮除面目皮及舌下黄，其白汁涂疔肿拔根，滴痈上利溃。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 133);
INSERT INTO Ben_Jing_Note VALUES (640, '好古', '搜肝气，泻肝风，消涎积。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 134);
INSERT INTO Ben_Jing_Note VALUES (641, '大明', '壮阳道，补精骨劳损，风劳气，止嗽，杀脏虫邪鬼。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 135);
INSERT INTO Ben_Jing_Note VALUES (642, '思邈', '此脾之菜也，宜脾，利胃气，滑大肠。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 117);
INSERT INTO Ben_Jing_Note VALUES (643, '大明', '主天行热疾，除风，安神，镇心，治恶疮疥，杀虫，催生下死胎。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 137);
INSERT INTO Ben_Jing_Note VALUES (644, '大明', '治天行热狂头风旋，下乳，揭齿益齿。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 138);
INSERT INTO Ben_Jing_Note VALUES (645, '灵胎', '补督脉，强肾气。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 111);
INSERT INTO Ben_Jing_Note VALUES (646, '大明', '一切风一切气，一切劳损，一切血，补五劳，壮筋骨，调众脉，破症结宿血，养新血，吐血，鼻血，溺血，脑瘫，发背，瘰疬瘿赘痔瘘疮疥，长肉排脓，消瘀痰血。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 49);
INSERT INTO Ben_Jing_Note VALUES (647, '甄权', '治鼻衄。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 104);
INSERT INTO Ben_Jing_Note VALUES (648, '苏敬', '炒热投酒中饮，疗贼风痉，裹以熨腋下，疗狐臭有验。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 143);
INSERT INTO Ben_Jing_Note VALUES (649, '大明', '子炒用补虚劳，口干，润心肺，治吐血，肠风泻血，赤白痢。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 149);
INSERT INTO Ben_Jing_Note VALUES (650, '大明', '石茵陈治天行时疾热狂，头痛头旋，风眼疼痛，瘴疟，女人症瘕，并闪损乏绝。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 73);
INSERT INTO Ben_Jing_Note VALUES (651, '药征', '主治呃逆也，旁治胸痹停痰。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 105);
INSERT INTO Ben_Jing_Note VALUES (652, '大明', '通九窍，调血脉，开毛孔皮肤。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 152);
INSERT INTO Ben_Jing_Note VALUES (653, '括要', '生产不下，用云母细末，调酒涂于阴门。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 1);
INSERT INTO Ben_Jing_Note VALUES (654, '大明', '治筋骨羸弱，补五劳七伤，眼昏，除烦躁，小儿误呑针铁等，即研细末以筋肉莫令断与末同呑下。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 139);
INSERT INTO Ben_Jing_Note VALUES (655, '大明', '妇人血气烦闷，产后血晕，并经脉不止，崩中带下，消一切疮疖，止鼻衄吐血，通小肠，消酒毒，治黄病，杀蕈毒，傅蛇虫咬。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 155);
INSERT INTO Ben_Jing_Note VALUES (656, '大明', '治游风，补劳损，心惊烦躁，骨蒸，传尸邪气，消肿毒。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 157);
INSERT INTO Ben_Jing_Note VALUES (657, '日华', '主中风失音，口噤，惊悸，天行时疾，健忘虚乏。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 109);
INSERT INTO Ben_Jing_Note VALUES (658, '大明', '热劳传尸疰痛，通小肠，消痰止嗽，润心肺，安心止惊悸。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 159);
INSERT INTO Ben_Jing_Note VALUES (659, '容川', '色白性平，利胸肺之痰气。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 160);
INSERT INTO Ben_Jing_Note VALUES (660, '甄权', '能蚀脓，止心腹血刺痛，女人沥血腰痛血崩。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 161);
INSERT INTO Ben_Jing_Note VALUES (661, '弘景', '渍酒饮治疥杀虫。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 150);
INSERT INTO Ben_Jing_Note VALUES (662, '容川', '黄芩味苦，中多空虚，有孔道，人身惟三焦；是行水气之孔道主相火，故主清相火，火逆呕苦、黄芩为正药。此药苦而色绿，故能入瞻。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 163);
INSERT INTO Ben_Jing_Note VALUES (663, '灵胎', '去寒湿，强筋骨。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 164);
INSERT INTO Ben_Jing_Note VALUES (664, '大明', '调中消痰，止渴润肌肤，添骨髓。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 167);
INSERT INTO Ben_Jing_Note VALUES (665, '大明', '治血气心腹痛，破症结，催生落胞，血运鼻衄，吐血，赤白带下，赤眼障膜，弩肉，聤耳，疮疖疥癣，丹毒，排脓补瘘。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 168);
INSERT INTO Ben_Jing_Note VALUES (666, '元素', '泻肝安脾，收肺气，止泻痢，固腠理，和血脉，收阴气敛逆。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 154);
INSERT INTO Ben_Jing_Note VALUES (667, '大明', '治一切风一切气，补一切劳，破恶血养新血，及症癖胃冷。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 151);
INSERT INTO Ben_Jing_Note VALUES (668, '苏敬', '蝮蛇疮毒心闷，服汁并洗之，刀斧伤疮，敷之愈。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 52);
INSERT INTO Ben_Jing_Note VALUES (669, '元素', '治太阳头痛，巅顶痛，大寒犯脑，痛连齿颊。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 172);
INSERT INTO Ben_Jing_Note VALUES (670, '唐本', '灯笼草治上气咳嗽，风热，明目，根茎花实并用。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 170);
INSERT INTO Ben_Jing_Note VALUES (671, '甄权', '冷气节痹，腰脚瘫缓不遂，手足惊挚，男子腰痛，久冷，膀胱宿水。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 174);
INSERT INTO Ben_Jing_Note VALUES (672, '本草', '主治中风，身热支满，忽忽不知人，血虚，热淋，温疟，洗洗寒热酸痛。妇人伤中淋露，产虚烦呕。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 175);
INSERT INTO Ben_Jing_Note VALUES (673, '大明', '治传尸劳，除风。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 97);
INSERT INTO Ben_Jing_Note VALUES (674, '大明', '止吐血鼻衄，伤风，月经不止，血崩，产前后诸血疾，并水泻。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 177);
INSERT INTO Ben_Jing_Note VALUES (675, '括要', '补五劳七伤。补骨髓，治消渴引饮。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 142);
INSERT INTO Ben_Jing_Note VALUES (676, '灵胎', '清湿热，治水肿。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 179);
INSERT INTO Ben_Jing_Note VALUES (677, '大明', '消痰下气，治转筋吐泻肠脏，反胃干呕，瘀血扑损，止鼻红，解冷热毒，开胃消宿食。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 147);
INSERT INTO Ben_Jing_Note VALUES (678, '附录', '槐角子即槐实，味苦性寒，无毒，主一切热，散一切结，清一切火，治五痔瘘疮（熬膏丸入谷道，日三易），邪热，妇人乳瘕，子脏急痛，补绝伤，疗火疮，阴疮湿痒，大热难产，能明目补脑，除头脑心胸间热，风眩欲倒，心头吐涎如车船上晕者。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 91);
INSERT INTO Ben_Jing_Note VALUES (679, '大明', '通关腠血脉，排脓，消扑损瘀血，续筋骨，除风痹，治胎下胞，产后一切冷热血气。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 182);
INSERT INTO Ben_Jing_Note VALUES (680, '容川', '海藻昆布皆生于水中，味微咸，又具草之质，是禀水木二气而生，故能清火润肝。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 178);
INSERT INTO Ben_Jing_Note VALUES (681, '大明', '安心定胆益志，养五脏，治颠邪狂叫惊悸，产后血狂运，杀虫毒气，胁痈乳痈发背诸疮肿。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 158);
INSERT INTO Ben_Jing_Note VALUES (682, '志良', '主血劳，风气壅满，背脊疼痛，虚汗，理丈夫脚气，筋骨烦痛，及阴阳毒，伤寒头痛，头旋目眩，手足筋急。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 188);
INSERT INTO Ben_Jing_Note VALUES (683, '大明', '调中下气，消痰止渴，开胃下食，杀腹脏虫，止霍乱吐泻，研汁治小儿天吊惊痫客忤。及敷鹅口疮，大验。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 190);
INSERT INTO Ben_Jing_Note VALUES (684, '大明', '消痰，治狂热烦闷，中风失音不语，壮热，头风，头痛，止惊悸，温痔烦闷，妊妇头旋倒地，小儿惊痫天吊。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 191);
INSERT INTO Ben_Jing_Note VALUES (685, '容川', '此物多筋，故治绝伤，因其筋象骨节，故主接筋骨；去骨节间之风寒。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 59);
INSERT INTO Ben_Jing_Note VALUES (686, '元素', '消食，散败血，破积坚，去胃中湿热。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 194);
INSERT INTO Ben_Jing_Note VALUES (687, '容川', '此药象人之皮，味苦兼降湿热。故仲景用治皮肤发黄之症。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 196);
INSERT INTO Ben_Jing_Note VALUES (688, '大明', '腰脚重痛，膀胱痛，及骨节烦痛，不下食。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 43);
INSERT INTO Ben_Jing_Note VALUES (689, '好古', '泻膀胱。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 200);
INSERT INTO Ben_Jing_Note VALUES (690, '大明', '通月水，破症结，止血崩带下，杀腹臓虫，及产后血绞腹痛。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 203);
INSERT INTO Ben_Jing_Note VALUES (691, '大明', '通关节，利九窍，及血脉，通小肠水气，天行时疾，头痛眼疼，其花同功。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 169);
INSERT INTO Ben_Jing_Note VALUES (692, '大明', '润心肺，益五脏，除烦消痰，洗肝明目，及中风等疾。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 181);
INSERT INTO Ben_Jing_Note VALUES (693, '容川', '其宿以头顾尾，能通督脉，督者肾脉，坎中一阳之主脉也，鹿生北方，得坎中一阳之气也，故其督脉极旺，而脊与脑髓极足，因而上发而生角，每年一换，初生为鹿茸，茸之精气极足，为补髓强精壮阳益血之圣药。但其性上行，凡是血逆火逆者不宜用。惟血虚火弱，阳不举，气不上者，乃为合宜。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 206);
INSERT INTO Ben_Jing_Note VALUES (694, '灵胎', '补脾益胃。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 127);
INSERT INTO Ben_Jing_Note VALUES (695, '大明', '煎水漱牙齿，止风虫疼痛，又洗乳癕，蜂疔，恶疮。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 214);
INSERT INTO Ben_Jing_Note VALUES (696, '元素', '治心烦懊憹不得眠，脐下血滞而小便不利。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 193);
INSERT INTO Ben_Jing_Note VALUES (697, '汪机', '明目退热，下乳催生。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 153);
INSERT INTO Ben_Jing_Note VALUES (698, '大明', '镇心，除面皯，头风，暖水脏，生用破血，炙用止血。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 218);
INSERT INTO Ben_Jing_Note VALUES (699, '容川', '鳖甲攻破肝气，去症瘕。                    ', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 215);
INSERT INTO Ben_Jing_Note VALUES (700, '大明', '暖腰膝，助水脏，除一切风，逐一切气，破症结。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 198);
INSERT INTO Ben_Jing_Note VALUES (701, '大明', '治虚劳骨蒸，消酒毒，令人得睡，和建茶干姜为丸服，止休息痢大验。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 229);
INSERT INTO Ben_Jing_Note VALUES (702, '容川', '能去湿热，属苦降之品，其形中空，纹如车轮，能外行腠理，内行三焦，能通水气。味似龙胆，能利水行。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 180);
INSERT INTO Ben_Jing_Note VALUES (703, '好古', '主狂疟温疟，鼽血，汗出，治血痢。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 171);
INSERT INTO Ben_Jing_Note VALUES (704, '甄权', '治积聚不消，炒焦止小儿疳痢。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 237);
INSERT INTO Ben_Jing_Note VALUES (705, '大明', '生肌长肉，吐血，白癜疬疡，瘢疵，痔瘘，瘿赘疣子，妇人粉刺，产后阴不能合，解酒酸，治酒毒，暖水臓，治气。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 234);
INSERT INTO Ben_Jing_Note VALUES (706, '好古', '督脉为病，脊强而厥。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 242);
INSERT INTO Ben_Jing_Note VALUES (707, '容川', '赭石重镇，其色赤，又入血分，故一名血师，因其能降血也。血为气之宅，故旋覆花代赭石汤，止噫气者，正是行血而降其气也。二六〇、卤咸', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 238);
INSERT INTO Ben_Jing_Note VALUES (708, '大明', '治鼻红血，痔瘘泄精，男子水藏冷，女子子宫冷。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 239);
INSERT INTO Ben_Jing_Note VALUES (709, '大明', '通宣一切气，调血脉，利关结，泄壅滞水气，温瘴热疟。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 247);
INSERT INTO Ben_Jing_Note VALUES (710, '容川', '葶苈子多油，当能滑利，又有辛味，舆巴豆之辛有油相似，其味苦，与大黄之苦而滑润相似，故葶苈子隐含巴豆舆大黄二者之性，故能大泻肺中之痰饮脓血，性极速降，是猛药类，故仲景配以大枣补之，入药前须要炒过，不炒则不香，不能散。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 248);
INSERT INTO Ben_Jing_Note VALUES (711, '大明', '下一切气，止霍乱转筋，心腹胀痛，补五劳，养气除邪辟温，破症瘕肺痈，养血排脓，补内漏及喉痹。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 249);
INSERT INTO Ben_Jing_Note VALUES (712, '括要', '下乳汁，消热毒，排脓止血生肌杀虫，能预解时行痘疹毒，取其寒胜热，又入阳明故也，无则以山栀取代之。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 60);
INSERT INTO Ben_Jing_Note VALUES (713, '灵胎', '降气消痰。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 252);
INSERT INTO Ben_Jing_Note VALUES (714, '灵胎', '养心开窍，解毒除邪。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 211);
INSERT INTO Ben_Jing_Note VALUES (715, '大明', '消痰破症结，胸膈满腹胀，气喘，痃癖，开胃下食，镇肝明目。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 255);
INSERT INTO Ben_Jing_Note VALUES (716, '容川', '为治疟要药，痰疟是积湿而成的，常山苗能透达以吐之。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 257);
INSERT INTO Ben_Jing_Note VALUES (717, '药征', '主利水也，旁治掣痛，咳烦，短气，小便难，心下满。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 258);
INSERT INTO Ben_Jing_Note VALUES (718, '东垣', '止肺血。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 262);
INSERT INTO Ben_Jing_Note VALUES (719, '大明', '泻毒药，泻天行，黄病温疟，破症结。                    ', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 263);
INSERT INTO Ben_Jing_Note VALUES (720, '灵胎', '解毒杀虫。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 265);
INSERT INTO Ben_Jing_Note VALUES (721, '大明', '通大小肠，泻蛊毒，堕胎，熁肿毒，傅恶物。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 269);
INSERT INTO Ben_Jing_Note VALUES (722, '括要', '经年癣疮生痂，搔之有黄水出，每阴雨痒甚，为末敷之效。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 272);
INSERT INTO Ben_Jing_Note VALUES (723, '弘景', '止毒痢', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 273);
INSERT INTO Ben_Jing_Note VALUES (724, '日华', '炒服治瘰疬。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 228);
INSERT INTO Ben_Jing_Note VALUES (725, '大明', '蛇虫咬，疥癣恶疮。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 246);
INSERT INTO Ben_Jing_Note VALUES (726, '倪注', '民间方使用单味夏枯草五钱，生鸡蛋带壳一只，用水二碗煮成一碗，将汤喝尽，去壳吃蛋，如果于健康时吃一剂，则终身不再犯扁桃腺炎。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 284);
INSERT INTO Ben_Jing_Note VALUES (727, '大明', '疗咳嗽瘴疟。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 285);
INSERT INTO Ben_Jing_Note VALUES (728, '日华', '通宣一切病，泄壅滞，除风补劳，健脾开胃，消痰破血，排脓消肿毒，杀腹脏虫，治恶疮息肉，及疥癞子疮。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 286);
INSERT INTO Ben_Jing_Note VALUES (729, '大明', '破症结，开胸，治天行时气，产后宿血，壮阳，疗阴汗，暖腰膝，缩小便，止呕逆。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 287);
INSERT INTO Ben_Jing_Note VALUES (730, '大明', '通关节，头风，消痰杀虫，治骨蒸，开胃，中风口噤。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 288);
INSERT INTO Ben_Jing_Note VALUES (731, '日华', '煎膏续筋骨长肉止痛，主服金石人大发热闷，汤火疮毒入腹闷热，及疔毒。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 289);
INSERT INTO Ben_Jing_Note VALUES (732, '括要', '泻膀胱，治诸疝虫痔，能入肝舒筋，导小肠，膀胱之热，为疝气要药。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 290);
INSERT INTO Ben_Jing_Note VALUES (733, '大明', '治邪气及骨节疼，四肢不仁，痔露等疾，久服耐寒暑。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 8);
INSERT INTO Ben_Jing_Note VALUES (734, '时珍', '桐叶消肿毒，生发，桐木皮治恶出疮，小儿丹毒，煎汁涂之。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 293);
INSERT INTO Ben_Jing_Note VALUES (735, '别录', '鼠李皮治身皮热毒。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 298);
INSERT INTO Ben_Jing_Note VALUES (736, '日华', '炙用辟恶，止小儿惊悸客热，煎汁傅疬疡，白瘢风，摧生。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 307);
INSERT INTO Ben_Jing_Note VALUES (737, '苏敬', '傅狂犬伤，出犬毛神效。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 308);
INSERT INTO Ben_Jing_Note VALUES (738, '药性', '治女子月闭，欲成干血劳。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 310);
INSERT INTO Ben_Jing_Note VALUES (739, '日华', '疗淋疾，傅恶疮，瘘烂。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 311);
INSERT INTO Ben_Jing_Note VALUES (740, '弘景', '烧研点目去翳．', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 312);
INSERT INTO Ben_Jing_Note VALUES (741, '藏器', '治惊悸烦闷，心胸恶气，瘰疬恶疮，溪毒。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 210);
INSERT INTO Ben_Jing_Note VALUES (742, '元素', '除肺热，治上焦风燥，利胸膈气逆，润大肠气秘。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 322);
INSERT INTO Ben_Jing_Note VALUES (743, '大明', '吐蛔虫。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 324);
INSERT INTO Ben_Jing_Note VALUES (744, '大明', '治烦渴、崩中，带下，五种黄病。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 325);
INSERT INTO Ben_Jing_Note VALUES (745, '大明', '消痰下气，化食开胃，除烦治水，调中止呕吐，霍乱心腹虚痛，奔豚肾气，肺气喘息，五膈气，肠风泻血，崩中带下，怀孕漏胎，及动胎欲生，胀闷气喘，痢人肛门急痛，产妇阴痛，和蜡烧烟熏之及煎汤熏洗。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 328);
INSERT INTO Ben_Jing_Note VALUES (746, '丹溪', '多用破血，少用养血。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 329);
INSERT INTO Ben_Jing_Note VALUES (747, '弘景', '蜈蚣蜂虿螫人，取置咬处吸其毒。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 330);
INSERT INTO Ben_Jing_Note VALUES (748, '甄权', '治热毒，散恶血，除烦满，通气，健脾胃，令人美食，捣末同鸡子白涂一切热毒痈肿，煮汁洗小儿黄烂疮，不过三度。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 331);
INSERT INTO Ben_Jing_Note VALUES (749, '苏敬', '主衄血下血脓血痢水，煮及丸散任用。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 332);
INSERT INTO Ben_Jing_Note VALUES (750, '孟铣', '除寒热，治一切冷气。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 333);
INSERT INTO Ben_Jing_Note VALUES (751, '思邈', '补虚冷，益气力，止肠鸣咽痛，治唾血，消痰，润肺止嗽。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 334);
INSERT INTO Ben_Jing_Note VALUES (752, '大明', '通小肠，排脓治疮疖，止痛，通月经。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 276);
INSERT INTO Ben_Jing_Note VALUES (753, '孟铣', '杀三虫，又每夜嚼一枚和蜜涂乎面良。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 321);
INSERT INTO Ben_Jing_Note VALUES (754, '大明', '破症瘕宿血，损伤瘀血，消肿痛。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 339);
INSERT INTO Ben_Jing_Note VALUES (755, '之才', '治不眠。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 340);
INSERT INTO Ben_Jing_Note VALUES (756, '张文潜', '粥能畅胃气，生津液，每晨空腹食之，所补不细。与脏腑相宜者唯此最佳。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 341);
INSERT INTO Ben_Jing_Note VALUES (757, '孟铣', '将之炙黄煎汤，日再饮之，治女人卒赤白带下。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 342);
INSERT INTO Ben_Jing_Note VALUES (758, '倪注', '举凡内外伤，诸烂疮，乳痈等都可以用，镇痛止血。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 343);
INSERT INTO Ben_Jing_Note VALUES (759, '丹溪', '治吐衄，妇人倒经，痘毒入心。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 346);
INSERT INTO Ben_Jing_Note VALUES (760, '孟铣', '利诸经脉寒热不通之气。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 347);
INSERT INTO Ben_Jing_Note VALUES (761, '别录', '归骨，除寒热，去水气，温中散结气，作羹食利病人，诸疮中风寒水气肿痛，捣涂之。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 349);
INSERT INTO Ben_Jing_Note VALUES (762, '大明', '主一切气，肺气烦闷，贲豚肾气。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 350);
INSERT INTO Ben_Jing_Note VALUES (763, '思邈', '养心气，心病宜食之。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 352);
INSERT INTO Ben_Jing_Note VALUES (764, '孟铣', '治女子崩中，止痛，除风热风疟，鬼交精出。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 353);
INSERT INTO Ben_Jing_Note VALUES (765, '大明', '健脾，治反胃霍乱转筋冷热气，泻膀胱及五脏一切气，妇人产前产后腹脏不安，杀肠中虫，明耳目，调关节。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 195);
INSERT INTO Ben_Jing_Note VALUES (766, '日华', '主中风失音，口噤，惊悸，天行时疾，健忘虚乏。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 207);
INSERT INTO Ben_Jing_Note VALUES (767, '大明', '下产后余血，治肾气脚气，水肿，通关节，起阳健脾。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 192);
INSERT INTO Ben_Jing_Note VALUES (768, '大明', '灸罯冻疮，烧取汁涂头，黑润鬓发。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 335);
INSERT INTO Ben_Jing_Note VALUES (769, '藏器', '鲤鱼作脍温补，去冷气痃癖气块，横关伏梁，结在心腹。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 122);
INSERT INTO Ben_Jing_Note VALUES (770, '大明', '治带下瘟疫，冷气，补五劳七伤。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 140);
INSERT INTO Ben_Jing_Note VALUES (771, '藏器', '研末一钱，用华水调服，治哑病。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 314);
INSERT INTO Ben_Jing_Note VALUES (772, '大明', '泄五脏膀胱急痛，宣腰胯冷脓，消宿食下气。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 291);
INSERT INTO Ben_Jing_Note VALUES (773, '宗奭', '益肾脏，缩小便，取汁同少蜜煎为稀膏，点服治肺气虚寒。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 126);
INSERT INTO Ben_Jing_Note VALUES (774, '大明', '治吐食反胃，霍乱转筋，肠腹冷，痰疟。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 245);
INSERT INTO Ben_Jing_Note VALUES (775, '药性', '主化水谷宿食症结积滞，健脾暖胃。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 351);
INSERT INTO Ben_Jing_Note VALUES (776, '大明', '天行热疾，酒黄病，壮热心烦闷，热劳，排脓，消扑损瘀血，破症癖，落胎。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 176);
INSERT INTO Ben_Jing_Note VALUES (777, '苏颂', '炒末冷酒调服，治发背。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 173);
INSERT INTO Ben_Jing_Note VALUES (778, '灵胎', '除湿泄热解毒杀虫。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 253);
INSERT INTO Ben_Jing_Note VALUES (779, '大明', '润心肺，治疮痂息肉，可外涂之。', 1, '2019-03-09 14:47:37', '2019-03-09 14:47:37', 356);
INSERT INTO Ben_Jing_Note VALUES (780, '灵胎', '荡肠胃热，利大小便。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 4);
INSERT INTO Ben_Jing_Note VALUES (781, '吴克潜', '曰车前子能利水道而不走真气，与茯苓同功，治暑湿暴下，则泌别清浊，使小便利而泄泻自止，治五淋则渗泄湿热，使从小便下而淋自止，且以通为补，车前尤能强肾益精，如五子衍宗丸等。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 30);
INSERT INTO Ben_Jing_Note VALUES (782, '元素', '散滞气，调诸气，和胃气，泄肺气。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 31);
INSERT INTO Ben_Jing_Note VALUES (783, '容川', '色黑，故兼入少阴经，以达太阳，能散背脊之风寒，同天麻与白头翁皆有风不动，无风而动。但味太辛，气太温，能散风寒，其力胜于天麻，兼能燥湿，但不如天麻之刚柔得中也。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 29);
INSERT INTO Ben_Jing_Note VALUES (784, '孟铣', '祛干湿脚气大验。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 33);
INSERT INTO Ben_Jing_Note VALUES (785, '附录', '益母草性味通茺蔚子，主瘾疹，可做浴汤，捣汁服用主浮肿下水，消恶毒疔肿，乳痈丹游等毒可敷之，又服汁主治胎死腹中及产后血胀闷，滴汁入耳治耳聤。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 24);
INSERT INTO Ben_Jing_Note VALUES (786, '元素', '去目中黄及睛赤肿胀，瘀肉高起，痛不可忍。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 36);
INSERT INTO Ben_Jing_Note VALUES (787, '药征', '主治心下痞坚，痞鞭支结，旁治不食呕吐喜唾心痛腹痛烦悸等。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 17);
INSERT INTO Ben_Jing_Note VALUES (788, '好古', '心积伏梁。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 15);
INSERT INTO Ben_Jing_Note VALUES (789, '容川', '风能胜湿，风伤血则筋燥，玉竹可以润燥。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 25);
INSERT INTO Ben_Jing_Note VALUES (790, '好古', '主心病嗌干，心痛渴欲饮，痿躄嗜卧，足下热而痛。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 18);
INSERT INTO Ben_Jing_Note VALUES (791, '灵胎', '舒筋行血，降气堕胎。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 23);
INSERT INTO Ben_Jing_Note VALUES (792, '容川', '菊叶为治疮之要药，因其性散，能去肌肉中之风邪也。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 16);
INSERT INTO Ben_Jing_Note VALUES (793, '容川', '柴胡一茎直上，花清香，故能升散郁结。专治胸前逆满，太阳经气陷于胸中，不得外达，以致胸满，唯柴胡能透达，其色青，生于春而采于夏，得木火之气，从中土以达木火之气，使不侮肺也，故功能透胸前之结，仲景用柴胡以治少阳，少阳为水中之阳，发于三焦，以行腠理，寄居胆中，以化水谷，故必三焦之膜网通畅，肝胆之木火清和，则水中之阳乃由内达外，柴胡茎中虚松，有白丝通气，象人之三焦膜网，少阳木火，郁于腠理，而不达者，则作寒热，柴胡能达之，阳气得发散，且味清苦，良柴胡须用一茎直上，茎色青，叶生四面，细如竹叶，开小黄花者，乃为真柴胡，此为仲景所用。四川梓潼产柴胡最佳，价极廉。今多不知用，因药典有软柴胡，银柴胡，红柴胡等，皆以假乱真，柴胡入肝经。附银柴胡：柴胡之产于陕西银州者，色黄白而大，味甘性微寒，凡热在骨髓者，非此不除，为清热凉血之品，优于发散而推陈致新，治虚劳者酌用之。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 27);
INSERT INTO Ben_Jing_Note VALUES (794, '好古', '吐肺痿之脓血，消五发之疮疽。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 19);
INSERT INTO Ben_Jing_Note VALUES (795, '好古', '治督脉为病，脊强而厥。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 37);
INSERT INTO Ben_Jing_Note VALUES (796, '容川', '利湿，此以金行水也。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 53);
INSERT INTO Ben_Jing_Note VALUES (797, '元素', '治虚劳自汗，补肺气，泻肺火心火，实皮毛，益胃气，去肌热，及诸经之痛。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 54);
INSERT INTO Ben_Jing_Note VALUES (798, '容川', '象人之阴，味咸入肾，温润而强阴，能助肾中之阳，益命门之火也。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 55);
INSERT INTO Ben_Jing_Note VALUES (799, '吴克潜', '防风善表散，故用以解毒，如砒毒，乌头毒。芫花毒，野菌毒，诸毒药，又防风本制黄耆，黄耆得防风而功最大，治风邪久留不去，表虚自汗不止。徐之才曰得葱白行周身，得泽泻橐本疗风，得归芍阳起石禹余粮治妇人子脏风，元素曰治风去湿之仙药也。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 56);
INSERT INTO Ben_Jing_Note VALUES (800, '容川', '赤石脂粘涩又味甘，能填补止泻利，土之质也，能燥湿。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 12);
INSERT INTO Ben_Jing_Note VALUES (801, '好古', '理胃益脾，补肝风虚，主舌本强，食即呕，胃脘痛，心下急痛，心下水痞，冲脉为病，气逆里急脐腹痛。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 21);
INSERT INTO Ben_Jing_Note VALUES (802, '灵胎', '明目益精。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 63);
INSERT INTO Ben_Jing_Note VALUES (803, '纲目', '活血通心包络，治疝痛。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 64);
INSERT INTO Ben_Jing_Note VALUES (804, '容川', '色赤味苦，根长，故下行之力必重，专能降泄行血。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 65);
INSERT INTO Ben_Jing_Note VALUES (805, '好古', '治喘咳逆，壮水镇阳。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 67);
INSERT INTO Ben_Jing_Note VALUES (806, '汪机', '兰草走气分，故能利水道除痰癖，杀蛊辟恶而为消渴良药。与泽兰同走血分，能消水肿，除痈毒，除症破瘀，为妇人要药。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 69);
INSERT INTO Ben_Jing_Note VALUES (807, '时珍', '主解班蝥，芫青，朱砂，橒鸡，砒石毒。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 48);
INSERT INTO Ben_Jing_Note VALUES (808, '元素', '治肺中伏火，补心气不足，主血妄行，及经水枯，乳汁不下。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 28);
INSERT INTO Ben_Jing_Note VALUES (809, '容川', '色紫而根实，能温肝，足厥阴主风，属阴中之阳，药气温者即阴中之阳，故入肝。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 39);
INSERT INTO Ben_Jing_Note VALUES (810, '苏颂', '生嚼涂小儿头疮，煎汤浴恶疮，妇女阴疮大效。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 83);
INSERT INTO Ben_Jing_Note VALUES (811, '灵胎', '补中益气。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 84);
INSERT INTO Ben_Jing_Note VALUES (812, '元素', '去伤风头痛，开肌理，解表发汗，去皮肤风湿。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 88);
INSERT INTO Ben_Jing_Note VALUES (813, '灵胎', '补助阳气，驱逐阴邪。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 89);
INSERT INTO Ben_Jing_Note VALUES (814, '弘景', '松节酿酒，主脚弱骨节风。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 90);
INSERT INTO Ben_Jing_Note VALUES (815, '好古', '主心病掌中热痛，脾气痿蹶，嗜卧，足下热而痛。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 20);
INSERT INTO Ben_Jing_Note VALUES (816, '灵胎', '养心气，润肌肤。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 93);
INSERT INTO Ben_Jing_Note VALUES (817, '元素', '入肾经，去旧水，养新水，利小便，消肿胀，渗泄止渴。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 34);
INSERT INTO Ben_Jing_Note VALUES (818, '药征', '主软坚，治心下痞坚，心下石鞭，小腹急结，结胸燥屎，大便鞭，旁治宿食腹满，小腹肿痞，诸般难解之毒。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 3);
INSERT INTO Ben_Jing_Note VALUES (819, '元素', '泻膀胱相火，补肾水不足，坚肾壮骨髓，疗下焦虚，诸痿瘫痪，利下窍，除热。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 96);
INSERT INTO Ben_Jing_Note VALUES (820, '丹溪', '生捣贴肿硬毒能消散。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 47);
INSERT INTO Ben_Jing_Note VALUES (821, '苏颂', '酿酒饮治风痹，四肢挛急。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 98);
INSERT INTO Ben_Jing_Note VALUES (822, '元素', '太阳头痛头沉昏闷，除昏闷，散风邪，凉诸经血，止目精内痛。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 99);
INSERT INTO Ben_Jing_Note VALUES (823, '容川', '辛夷花在树梢，其性极升，而味辛散，故能散脑鼻之间风寒。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 100);
INSERT INTO Ben_Jing_Note VALUES (824, '灵胎', '驱风养血安胎。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 101);
INSERT INTO Ben_Jing_Note VALUES (825, '容川', '黄连之味正苦，入心经以泻火。清火之药，是水交于火也，故能止血，凡药得苦味者，其中必含水性，故能降火，此为水火互根之理。其质枯而不泽，其纯于苦味而无气，故守而不走，为去暑之要药。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 51);
INSERT INTO Ben_Jing_Note VALUES (826, '好古', '肾积奔豚。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 35);
INSERT INTO Ben_Jing_Note VALUES (827, '灵胎', '专治下体湿毒之病。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 70);
INSERT INTO Ben_Jing_Note VALUES (828, '丹溪', '乱发消瘀血，止舌血，补阴甚捷。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 106);
INSERT INTO Ben_Jing_Note VALUES (829, '日华', '游风风疹，妇人经血不匀，发背。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 81);
INSERT INTO Ben_Jing_Note VALUES (830, '药性', '热水研服一粒，治小儿惊痫客忤，镇心安神，止小便利，又能蚀一切痈疮脓水。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 108);
INSERT INTO Ben_Jing_Note VALUES (831, '东垣', '能使筋骨相着。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 102);
INSERT INTO Ben_Jing_Note VALUES (832, '灵胎', '养胃生津，补虚除痹。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 38);
INSERT INTO Ben_Jing_Note VALUES (833, '黑雄鸡', '味甘酸性温平，无毒，主风寒湿痹五缓六急，破恶血，补新血，安胎，治产后虚羸，除邪辟恶痈疽排脓，酒浸饮，令人肥白。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 113);
INSERT INTO Ben_Jing_Note VALUES (834, '吴克潜', '曰糖之赤者为赤沙糖，白者为白沙糖，凝结成块者为冰糖，入药用冰糖最良，膏泛丸多用之。时珍曰蔗糖甘寒，能泻大热，素问甘温除大热意也，煎炼成糖则甘温而助湿热，所谓积温成热也，又白糖霜主润心肺，和中消痰，解酒生津，大约功专润泽，赤沙糖主心腹热胀，润心肺大小肠热，解酒毒，又为和血去积之药。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 114);
INSERT INTO Ben_Jing_Note VALUES (835, '本经', '蜜蜂子治头风，除蛊毒，补虚羸伤中，久服令人光泽。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 115);
INSERT INTO Ben_Jing_Note VALUES (836, '容川', '阿胶以水济火，是以坎填离，此阴汁得心火化为赤，即为血，益阴气，使阳不动、保持厥阴之本体，阿胶得阿井伏流之水性，故能伏水中之阳。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 112);
INSERT INTO Ben_Jing_Note VALUES (837, '肃炳', '壳炙末酒服，主风脚弱。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 118);
INSERT INTO Ben_Jing_Note VALUES (838, '灵胎', '养血逐瘀，补肾利水。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 119);
INSERT INTO Ben_Jing_Note VALUES (839, '好古', '主心病嗌干，心痛，渴而引饮，肾病消中。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 92);
INSERT INTO Ben_Jing_Note VALUES (840, '嘉谟', '安靖上下君相火邪。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 123);
INSERT INTO Ben_Jing_Note VALUES (841, '之才', '杀乌头附子天雄毒。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 124);
INSERT INTO Ben_Jing_Note VALUES (842, '灵胎', '补益筋骨，滋养肌肉。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 125);
INSERT INTO Ben_Jing_Note VALUES (843, '附录', '地肤子煎——煎水洗目，去热暗雀盲涩痛，煎水服利小便诸淋，解恶疮。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 71);
INSERT INTO Ben_Jing_Note VALUES (844, '好古', '牙根浮烂恶臭，太阳鼻衄，为疮家圣药。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 82);
INSERT INTO Ben_Jing_Note VALUES (845, '苏颂', '生嚼涂小儿头疮，煎汤浴恶疮；妇人阴疮大效。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 128);
INSERT INTO Ben_Jing_Note VALUES (846, '士良', '主润五脏，利大肠风热燥结及热淋。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 129);
INSERT INTO Ben_Jing_Note VALUES (847, '纲目', '主明目诸痢血淋痔瘘。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 133);
INSERT INTO Ben_Jing_Note VALUES (848, '吴克潜', '按雄黄于疮痬螫外科，最为急用，以其性能杀虫解毒也，吴俗于五月五日饮雄黄酒，并洒之室角以驱五毒，但须要知道雄黄虽有腐蚀之力，能蚀恶肉以杀虫治疮，但过用终不免侵蚀好肉，故不可多服，外科亦须审慎使用，著名方剂有安虫丸（干漆雄黄巴豆霜） ，治吐沫水后腹中必虫痛。雄黄解毒丸（巴豆雄黄玉金）治急喉痹咽喉不通，胸膈气促，金锁匙（朴消雄黄大黄）治一切风热，咽喉闭塞为末，吹入喉中。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 134);
INSERT INTO Ben_Jing_Note VALUES (849, '吴普', '治妇人血结。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 135);
INSERT INTO Ben_Jing_Note VALUES (850, '附方', '肉锥怪疾——手足忽长倒生肉刺，痛不可忍，但食冬葵叶即可。消中尿多——尿崩，日夜尿七八升。冬葵根浓煎。每日平旦服二升。妊妇下血——葵子水煎服，日三。妊妇水肿——小便不利，身重，恶寒，起即头眩。冬葵子，茯苓等分为末，饮服方寸匕，日三，小便利即愈。胎死腹中，葵子为末，酒服方寸匕，口噤不开者，灌之即生', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 117);
INSERT INTO Ben_Jing_Note VALUES (851, '宗奭', '治小儿惊痫热潮。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 137);
INSERT INTO Ben_Jing_Note VALUES (852, '元素', '止阳明经头痛，发热恶寒，日晡潮热，大渴引饮，中暑潮热，牙痛。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 138);
INSERT INTO Ben_Jing_Note VALUES (853, '吴克潜', '曰鹿茸为纯阳之物，性温而燥，专补元阳，凡男子虚怯老人阳亏允为合宜，若吐血尿血等症，由于阴虚火旺下焦有热者居多，误用鹿茸必致反剧，故鹿茸治血症乃治阳衰气虚而不摄血者。治痈疽亦为阴症而火衰者，研习者须知。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 111);
INSERT INTO Ben_Jing_Note VALUES (854, '好古', '搜肝风，补肝血，润肝燥，益肝虚。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 49);
INSERT INTO Ben_Jing_Note VALUES (855, '吴普', '强志，明耳目。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 104);
INSERT INTO Ben_Jing_Note VALUES (856, '之材', '杀野葛巴豆百药毒。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 149);
INSERT INTO Ben_Jing_Note VALUES (857, '药征', '主治发黄也。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 73);
INSERT INTO Ben_Jing_Note VALUES (858, '容川', '陈皮亦木实也，能治胃，兼治脾，但兼辛香，又能上达于肺。枳壳不辛香，故不走肺，厚朴辛，而气太沉，故不走肺。因肺通于大肠，故厚朴可泄肺气。其气不轻不重，故可降上焦与中焦之气。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 105);
INSERT INTO Ben_Jing_Note VALUES (859, '元素', '去营中寒邪，泄卫中风热。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 152);
INSERT INTO Ben_Jing_Note VALUES (860, '灵胎', '清肺明目。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 1);
INSERT INTO Ben_Jing_Note VALUES (861, '灵胎', '补肾降火，强筋壮骨。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 139);
INSERT INTO Ben_Jing_Note VALUES (862, '药征', '主治脐下动也，旁治烦惊失精。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 107);
INSERT INTO Ben_Jing_Note VALUES (863, '倪注', '现在已经是属于被管制中药，因为植物保护法。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 22);
INSERT INTO Ben_Jing_Note VALUES (864, '元素', '凉心去热，治阳明火热，泻膀胱肾经火热，痰厥头痛，下痢腰痛，喉中腥臭。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 159);
INSERT INTO Ben_Jing_Note VALUES (865, '纲目', '滋阴降火，解斑毒，利咽喉，通小便血滞。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 157);
INSERT INTO Ben_Jing_Note VALUES (866, '思邈', '益肝胆，定精神，除热，止惊痢，辟恶气，除百病。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 109);
INSERT INTO Ben_Jing_Note VALUES (867, '苏敬', '治恶虫胫酸。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 150);
INSERT INTO Ben_Jing_Note VALUES (868, '药征', '主治胸膈郁结痰饮。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 160);
INSERT INTO Ben_Jing_Note VALUES (869, '好古', '益肺气，主息贲。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 167);
INSERT INTO Ben_Jing_Note VALUES (870, '大明', '通经脉，捣涎傅癣疮。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 95);
INSERT INTO Ben_Jing_Note VALUES (871, '好古', '理中气治脾虚中满，心下痞，胁下痛，善噫，肺急胀逆，喘咳，太阳鼻衄，目涩肝血不足，阳维病苦寒热，带脉病苦腹痛满，腰溶溶如坐水中。 （血痹即血凝于肌肤而为痹）。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 154);
INSERT INTO Ben_Jing_Note VALUES (872, '好古', '主瘘痹嗜卧，足下热痛，冲脉为痛，气逆里急，带脉为病，腹痛腰溶溶如坐水中。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 151);
INSERT INTO Ben_Jing_Note VALUES (873, '附方', '喉痹肿塞、喘息不通须臾欲绝，用络石草一两，水煎一大碗，细细吞下，少顷即通。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 52);
INSERT INTO Ben_Jing_Note VALUES (874, '附方', '头屑干洗—槀本，白芷等份共末，夜擦旦梳，垢自去。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 172);
INSERT INTO Ben_Jing_Note VALUES (875, '苏颂', '酸浆果食之除热，治黄疸，尤宜小儿。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 170);
INSERT INTO Ben_Jing_Note VALUES (876, '元素', '削年深坚结之积滞，破日久凝结之瘀血。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 97);
INSERT INTO Ben_Jing_Note VALUES (877, '容川', '地榆首之气味厚，故行血更有力，尾之药味薄，故行血之力轻。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 177);
INSERT INTO Ben_Jing_Note VALUES (878, '灵胎', '入肺降逆，利窍下乳。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 142);
INSERT INTO Ben_Jing_Note VALUES (879, '好古', '主心下寒痞，目睛久赤。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 147);
INSERT INTO Ben_Jing_Note VALUES (880, '元素', '治神智不足无汗之谷蒸，衄血吐血。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 182);
INSERT INTO Ben_Jing_Note VALUES (881, '元素', '温肺止嗽。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 158);
INSERT INTO Ben_Jing_Note VALUES (882, '宗奭', '又名枯矾。贴嵌甲牙缝中治血出如衄。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 32);
INSERT INTO Ben_Jing_Note VALUES (883, '容川', '因叶在四旁，则主四散，故能清肌中之热，竹茹象人之筋脉，则清脉络之热以和血，秉天水之寒气，故治一切热。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 191);
INSERT INTO Ben_Jing_Note VALUES (884, '灵胎', '消积解癖。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 2);
INSERT INTO Ben_Jing_Note VALUES (885, '药征', '主治结实之毒，旁治胸满胸痹，腹满腹痛。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 194);
INSERT INTO Ben_Jing_Note VALUES (886, '药征', '主治渴而小便不利也。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 200);
INSERT INTO Ben_Jing_Note VALUES (887, '苏敬', '疗妇人血气大效。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 203);
INSERT INTO Ben_Jing_Note VALUES (888, '容川', '此药生于冬月冰雪之中，而花又在根下，乃坎中之阳象，故能引肺中阳气下行，而为利痰止咳之要药。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 181);
INSERT INTO Ben_Jing_Note VALUES (889, '灵胎', '补阳益血。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 206);
INSERT INTO Ben_Jing_Note VALUES (890, '苏敬', '疗上气，赤白痢，遗尿失禁，烧灰酒服主阴痿，水煮服汁下乳石毒。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 214);
INSERT INTO Ben_Jing_Note VALUES (891, '丹溪', '泻三焦火，清胃脕血，治热厥心痛，解热郁，行结气。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 193);
INSERT INTO Ben_Jing_Note VALUES (892, '容川', '白通草象人身之油膜，故能通达油膜，上可以通乳，下可以通小便。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 153);
INSERT INTO Ben_Jing_Note VALUES (893, '元素', '温肝。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 198);
INSERT INTO Ben_Jing_Note VALUES (894, '弘景', '白梅和药点痣，蚀恶肉。水渍汁饮治伤寒烦热。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 229);
INSERT INTO Ben_Jing_Note VALUES (895, '元素', '治中下湿热肿泄脚气，行十二经。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 180);
INSERT INTO Ben_Jing_Note VALUES (896, '大明', '治痈肿瘘烂，呕逆，疗症瘕，小儿疳气。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 237);
INSERT INTO Ben_Jing_Note VALUES (897, '容川', '人身之气，生于肾中一阳，实则借鼻孔吸入之天阳，歴心系，引心火下交于肾，附子可以振肾阳以蒸动其气。其色纯黑，味辛烈，能从下焦扶阳补气，凡极阳极阴，皆有毒，附子之烈，正因其纯是坎阳之性，可以大毒，与肉桂不同，肉桂是补火，属地二之火气也，附子是助热，热生于水中，是得天水之阳，故附子入气分以助阳，能补命门之火，是以火化水，为肾与膀胱要药，火炙则无毒，水中之阳毒，过火则散，此阴阳相引之意。今用盐腌以去毒，使附子之性不全，非正法也，举凡温药皆秉木气，唯附子是水中之阳，为温肾达阳之正药，而得木火者，为得地二之火，能秉水中之阳，是得天一之阳。仲景用炮附子，是制其毒也，用生附是以毒追风，毒因毒用，一生一炮，有一定之理。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 242);
INSERT INTO Ben_Jing_Note VALUES (898, '元素', '泻诸热不通，除下焦湿热，消宿食，泻心下痞痛。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 247);
INSERT INTO Ben_Jing_Note VALUES (899, '药征', '主治水病也，旁治肺痈结胸也。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 248);
INSERT INTO Ben_Jing_Note VALUES (900, '元素', '利窍，除肺部风热。清利头目咽嗌，胸膈滞气及痛，除鼻塞。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 249);
INSERT INTO Ben_Jing_Note VALUES (901, '日华', '利五脏，消食下气醒酒，作菜生熟皆可食，并煎茶饮之，以豉汁煎服，治暴伤寒能发汗。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 188);
INSERT INTO Ben_Jing_Note VALUES (902, '药征', '主治胸腹及脐下动剧者，故兼治惊狂火逆疟疾。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 257);
INSERT INTO Ben_Jing_Note VALUES (903, '灵胎', '敛疮涩血。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 262);
INSERT INTO Ben_Jing_Note VALUES (904, '容川', '商陆用根，取其内透膜膈，以导水下行。一般用苗则升，用根则降。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 269);
INSERT INTO Ben_Jing_Note VALUES (905, '容川', '白头翁一茎直上如柴胡，所以能治下痢后重者，升散郁结也。其无风动摇，有风不动，色白有毛，凡毛皆得风气，采于秋月，得金木交合之气，故能熄风，从肺金以达风木之气，使木不侮土，故功在升举后重，而止痢疾，又其味苦，是治风热之妙药，仲景治产后中风，及痢疾后重者，走取其熄风火，达肝阳也。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 273);
INSERT INTO Ben_Jing_Note VALUES (906, '宗奭', '治疳有效。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 228);
INSERT INTO Ben_Jing_Note VALUES (907, '灵胎', '补益劳伤，强续筋骨。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 59);
INSERT INTO Ben_Jing_Note VALUES (908, '药征', '主逐水也，旁治咳掣痛。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 285);
INSERT INTO Ben_Jing_Note VALUES (909, '元素', '导气消积，去脏腑停寒，治生冷硬物所伤。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 286);
INSERT INTO Ben_Jing_Note VALUES (910, '孟铣', '益血，利五脏，下乳汁，灭瘢，生毛发。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 287);
INSERT INTO Ben_Jing_Note VALUES (911, '宗奭', '溽暑久雨时，合苍术烧烟辟瘟疫邪湿气。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 288);
INSERT INTO Ben_Jing_Note VALUES (912, '苏敬', '柳枝与根主治痰热淋疾，可为浴汤，洗风肿疮疡，煮酒漱齿痛。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 289);
INSERT INTO Ben_Jing_Note VALUES (913, '灵胎', '除热燥湿，止利消瘀。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 8);
INSERT INTO Ben_Jing_Note VALUES (914, '元素', '去胃中痈疮。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 255);
INSERT INTO Ben_Jing_Note VALUES (915, '孟铣', '治口疳蛀齿，及疳虫蚀人脊骨者，煮浓汁灌之良。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 298);
INSERT INTO Ben_Jing_Note VALUES (916, '孟铣', '安胎。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 307);
INSERT INTO Ben_Jing_Note VALUES (917, '藏器', '痈肿毒肿。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 310);
INSERT INTO Ben_Jing_Note VALUES (918, '元素', '止渴利小便，除湿益燥，和中益气，利腰脐间血。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 94);
INSERT INTO Ben_Jing_Note VALUES (919, '李珣', '下水气浮肿，小儿疳蚀，吐乳。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 312);
INSERT INTO Ben_Jing_Note VALUES (920, '容川', '羚羊挂树干而眠，身悬而能眠，因其筋最直，故能治筋缩抽扯之症，角尤精气之所在，故性微寒，功专舒筋，本天水之阴，以清热也。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 210);
INSERT INTO Ben_Jing_Note VALUES (921, '容川', '杏仁有油，但得苦味，无辛烈之气，故降而不及。色白属金，降气行痰，气降则血降，故凡吐血必咳痰，痰为气分，因气逆而血升，故引出其血也。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 322);
INSERT INTO Ben_Jing_Note VALUES (922, '圣惠', '捣汁日服六七合，治小便出血。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 325);
INSERT INTO Ben_Jing_Note VALUES (923, '萧柄', '下宿物，止肠澼久泄赤白痢。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 328);
INSERT INTO Ben_Jing_Note VALUES (924, '容川', '色赤自入血分，味苦专能泄血，而花性专主轻扬，上行外走，故能泄肌肤脉络在外在上之血，有谚云：治风先治血，血行风自灭，血足则肝阳不动，而风自熄。观仲景之红蓝花酒，治风气百疾，则知治风先治血的道理。色红能生血，而味苦，又能泻血。番红花——红花之产于新疆者，味甘性平无毒，主活血开郁，治心忧气闷，惊悸，伤寒发狂今多用为开胃行经之品。藏红花——红花之产于西藏者，性味同红花，以入滚水内色如血，可冲四次不变色者为真，主活血通经治吐血（各种痞结冲汤服，忌食油荤盐） ，功用最良。土红花——红花之产于福建者，根叶入药，味甘，性微寒，无毒， （或作有毒）治劳热瘴疟，骨节疼痛。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 329);
INSERT INTO Ben_Jing_Note VALUES (925, '苏敬', '主蛇毒，温疟，止呕逆霍乱。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 330);
INSERT INTO Ben_Jing_Note VALUES (926, '士良', '缩气行风，坚筋骨，抽肌肉。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 331);
INSERT INTO Ben_Jing_Note VALUES (927, '甄权', '止崩血肠痔，血搨金疮，止腹痛安胎，苦酒作煎治癣甚良，捣汁饮，冶心腹一切冷气鬼气。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 332);
INSERT INTO Ben_Jing_Note VALUES (928, '日华', '补中益气，治心腹胀满，止霍乱转筋，开胃下食，止角气，通大小肠。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 333);
INSERT INTO Ben_Jing_Note VALUES (929, '孟铣', '健脾胃补中，治吐血，打损瘀血者熬焦酒服，能下恶血，又伤寒大毒嗽，于蔓青薤汁中煮一沸，顿服之良。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 334);
INSERT INTO Ben_Jing_Note VALUES (930, '丹溪', '泻心火，除脾胃湿热，治中部血证以为使。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 276);
INSERT INTO Ben_Jing_Note VALUES (931, '容川', '桃花红而仁味苦，皆得地火之性，仁在核中，又象人心，味苦有生气，是正入心中，故桃仁能破血，亦能生血。气属阳，血属阴，瘀血阻气，则阳不入阴，蒸热汗出，宜破其血，使气得入于血中，则不壅热，桃仁主之。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 321);
INSERT INTO Ben_Jing_Note VALUES (932, '好古', '心胆虚，肝血不足。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 339);
INSERT INTO Ben_Jing_Note VALUES (933, '大明', '下气，益精，补腰膝，治肾气，止霍乱冲恶，中邪气心腹痛疰气，煎膏止痛长肉。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 340);
INSERT INTO Ben_Jing_Note VALUES (934, '甄权', '治脚下气，主热毒烦躁，煮汁服止消渴', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 342);
INSERT INTO Ben_Jing_Note VALUES (935, '元素', '凉心。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 346);
INSERT INTO Ben_Jing_Note VALUES (936, '士良', '理风热小便数急痛，小腹虚满，宜煎汤并葱饮有效。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 347);
INSERT INTO Ben_Jing_Note VALUES (937, '日华', '煮食耐寒，调中补不足，止久痢冷泻。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 349);
INSERT INTO Ben_Jing_Note VALUES (938, '吴克潜', '白前能祛痰而利肺气，寇氏称其能保定肺气，治嗽多用。以温药相左尤佳，而时珍谓其性无补益，虽寇氏称其能保肺气，但其功能专于降气，气降故痰亦下，故惟肺气壅实，兼有痰凝塞者，用之无不奏效，若虚者则不宜，之二说者，可并存也，盖惟其泻肺不补，而痰乃能降，气乃能平，惟其祛痰降气，而肺乃得保，气乃得安，正可以互相发明也。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 350);
INSERT INTO Ben_Jing_Note VALUES (939, '宗奭', '煎汤饮治暴淋。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 352);
INSERT INTO Ben_Jing_Note VALUES (940, '李珣', '男子虚劳，补肾安神，去烦热，小儿惊痫。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 353);
INSERT INTO Ben_Jing_Note VALUES (941, '好古', '主肺气胀满，膨而喘咳。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 195);
INSERT INTO Ben_Jing_Note VALUES (942, '思邈', '益肝胆，定惊神，除热止惊痢，辟恶气，除百病。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 207);
INSERT INTO Ben_Jing_Note VALUES (943, '容川', '吴茱萸辛而带苦，子性又主下降，故主降水饮，行滞气。专治肝寒之吐逆，此药虽不上头，然可以降肝胃之寒，使不上冲入头，故仲景用之治头痛如破，此为治脏腑而经脉自治也。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 192);
INSERT INTO Ben_Jing_Note VALUES (944, '大明', '治目赤努肉，去面皯疵瘢，补胎瘘滑落，破宿血，补新血，乳痈，发背，一切痈疮疥癣，止痛排脓。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 161);
INSERT INTO Ben_Jing_Note VALUES (945, '苏颂', '傅汤火伤，止痛减瘢，服之疗蛊痢，作汤常服杀五脏虫，益人。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 335);
INSERT INTO Ben_Jing_Note VALUES (946, '好古', '补命门火不足。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 140);
INSERT INTO Ben_Jing_Note VALUES (947, '括要', '补虚续绝，强阴健阳，悦泽肌肤，安和五脏，温中益力，疗劳损风。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 126);
INSERT INTO Ben_Jing_Note VALUES (948, '元素', '治寒痰及形寒饮冷伤肺而咳，消胸中痞，膈上痰，除胸寒，和胃气，燥脾湿，治痰厥头痛，消肿散结。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 245);
INSERT INTO Ben_Jing_Note VALUES (949, '宗奭', '除目昏除翳，以水煎汁服，治小儿疮疹出不快，甚良。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 314);
INSERT INTO Ben_Jing_Note VALUES (950, '元素', '下气，以沐浴生毛发。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 176);
INSERT INTO Ben_Jing_Note VALUES (951, '附方', '  赤眼肿痛——鲤鱼胆五枚，黄连末半两，和匀，入蜂蜜少许，瓶盛，安饭上蒸熟，每日点目眦，日五七次。  阴痿——鲤鱼胆，雄鸡肝各一枚，为末，雀卵和丸小豆大，每吞一丸。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 122);
INSERT INTO Ben_Jing_Note VALUES (952, '元素', '养胃气，治赤白痢。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 351);
INSERT INTO Ben_Jing_Note VALUES (953, '元素', '破血润燥。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 291);
INSERT INTO Ben_Jing_Note VALUES (954, '附方', '手足浮肿——圣惠方，桐叶煎汁渍之，并饮少许，加小豆良。肘后方：削桐木煮汁洗，并饮少许。发落不生——桐叶一把，麻子仁三升，米泔煮汤去渣，日日洗之。（肘后）眼见诸物——禽虫飞走，乃肝胆之疾，青桐（紫花者）花，酸枣仁，元明粉，羌活各一两为末，每服二钱，水煎和渣服，日三。（经验方）', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 293);
INSERT INTO Ben_Jing_Note VALUES (955, '药征', '治心下痞也，旁治胸胁满，呕吐下利也。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 163);
INSERT INTO Ben_Jing_Note VALUES (956, '元素', '去上焦痰，眩晕。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 246);
INSERT INTO Ben_Jing_Note VALUES (957, '灵贻', '入心镇怯，定魂除邪。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 356);
INSERT INTO Ben_Jing_Note VALUES (958, '附录', '车前叶──凉血除热，止吐衄，消症瘕，与车前子同功。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 30);
INSERT INTO Ben_Jing_Note VALUES (959, '丹溪', '行肝经气，煨熟实大肠。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 31);
INSERT INTO Ben_Jing_Note VALUES (960, '灵胎', '升提元气，兼补五脏，有崇正去邪之功，然非虚者勿服。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 17);
INSERT INTO Ben_Jing_Note VALUES (961, '时珍', '解巴豆大戟毒。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 15);
INSERT INTO Ben_Jing_Note VALUES (962, '药征', '主治浮肿也。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 33);
INSERT INTO Ben_Jing_Note VALUES (963, '药征', '主治胸胁苦满也，旁治往来寒热也，腹中痛，胁下痞鞭。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 27);
INSERT INTO Ben_Jing_Note VALUES (964, '仲醇', '凡解毒必入甘草。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 19);
INSERT INTO Ben_Jing_Note VALUES (965, '吴克潜', '曰细辛主治诸痛，但用不可过剂。宗奭曰治头面风不可缺此。元素曰以独活为使治少阴头痛如神。张子和曰治头痛，太阳经则羌活，少阴则细辛，阳明则白芷，厥阴则川芎吴萸，少阳则柴胡。成无己曰胆气不足，细辛补之。著名方剂有伤寒论之麻黄附子细辛汤，小青龙汤等。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 37);
INSERT INTO Ben_Jing_Note VALUES (966, '时方', '腰脊引痛——捣末为蜜丸，胡豆大，酒服二丸日三。    年久失明——阴干捣散，食后水服方寸匕。 （外台）', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 53);
INSERT INTO Ben_Jing_Note VALUES (967, '好古', '太阴疟疾，阳维为病苦寒热，督脉为病逆气里急。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 54);
INSERT INTO Ben_Jing_Note VALUES (968, '灵胎', '补精益气，养血润肠。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 55);
INSERT INTO Ben_Jing_Note VALUES (969, '容川', '防风辛而味甘入肌，气香而温，故入脾。散肌肉之风寒。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 56);
INSERT INTO Ben_Jing_Note VALUES (970, '倪注', '此药常与禹余粮合用，因同属涩剂，多用于温中止血痢不止。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 12);
INSERT INTO Ben_Jing_Note VALUES (971, '容川', '内含甘润之油质，可以滋脾之阴，外发辛香之温性，故可以畅达脾之阳，尤以浙产之油质最厚，可补脾之膏油，油不粘水，故能利水，其气香味甘而苦温，能升发，故补脾温土，使脾土之气上达，和肝气以伸脾气，为补脾之正药。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 21);
INSERT INTO Ben_Jing_Note VALUES (972, '东垣', '退肝经邪热，除下焦湿热之肿，泻膀胱之火。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 36);
INSERT INTO Ben_Jing_Note VALUES (973, '灵胎', '养血消瘀。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 64);
INSERT INTO Ben_Jing_Note VALUES (974, '容川', '五味子酸敛肝木，得金收之性，使木气不上逆，则水火二者皆不上冲于肺，其味酸而质润，囊大而中空，有肺中空虚之象，又生于叶间，其性轻浮，故专攻敛肺生津。这是药能敛肝故能敛肺也。性极沉。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 67);
INSERT INTO Ben_Jing_Note VALUES (975, '好古', '消痈肿。调月经，煎水解中牛马毒。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 69);
INSERT INTO Ben_Jing_Note VALUES (976, '灵胎', '补土生金，寒咳勿用。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 28);
INSERT INTO Ben_Jing_Note VALUES (977, '附录', '麻油主利大肠，产妇胞衣不下。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 83);
INSERT INTO Ben_Jing_Note VALUES (978, '元素', '肺寒者用人参，肺热者用沙参，故举凡属肺家有虚热。嗽痰咳血，潮热似痨症，用之甚佳，如属风寒初客肺，浊痰未成，此时宜解表为先。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 84);
INSERT INTO Ben_Jing_Note VALUES (979, '容川', '色赤味辛，入心肝助火，但桂枝实因苓泽之利水，方能入水以化气，可以导心火以下交于水，故多用之以降冲逆之气，加入补血药中，因而能温通血脉，桂枝能散四肢，质较坚实，故桂枝能兼走筋骨。味辛又得土之气，故能散血脉肌肉中之风寒。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 88);
INSERT INTO Ben_Jing_Note VALUES (980, '容川', '肉桂比枝味更厚，气更凝聚。乃木之极致，大辛则大温，能益心火，为以木生火之专药，其实是温肝之品。肝为心之母，虚则补其母也，心肝皆司血分，故肉桂又为温血之要药，仲景用肾气丸，是接引心肝之火使之下归于肾。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 89);
INSERT INTO Ben_Jing_Note VALUES (981, '灵胎', '除湿火敷疮疡。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 90);
INSERT INTO Ben_Jing_Note VALUES (982, '容川', '产中州，得土深水厚之性，内含润泽之湿也。原色为黄，制后为黑，故人知地黄之能滋肾阴，不知地黄实滋脾阴，内经云脾为阴中之至阴，地黄以湿归脾，脾阴足，则肝肾自受其灌溉。其不辛温，故能益水液，滋血之源，而不能变化、以成血色。以水配火，不使壮火食气，如此气纳矣。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 20);
INSERT INTO Ben_Jing_Note VALUES (983, '吴克潜', '曰柏子仁不寒不燥，味甘而补，辛而能润，既养心肾，兼益脾肾，凡补脾药多燥，唯此独润而气味清香，故脾约宜之，燥脾药中，加用最良，著名方剂有柏子养心丸（柏子仁，枸杞子，麦冬，当归，石菖蒲，伏神，元参，熟地，甘草） ，治劳欲过度，心血亏损，精神恍惚，怔忡惊悸，及健忘遗精等症。柏子仁汤（柏子仁，人参，茯苓，陈皮，甘草，麝香少许），治肠胃中津液枯少，脉虚微无力，大便艰涩者。柏子仁散（柏子仁，远志，人参，桑寄生，防风，琥珀，当归，生地，甘草）治产后败血挟邪攻心，乱语狂言，乍见鬼神等症。柏子仁丸（柏子仁，干地黄，茯苓，枳壳，覆盆子，北五味，附子，石斛，鹿茸，酸枣仁，桂心，沉香，黄耆）治妇人臂痛，筋脉挛急，遇寒则剧者。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 93);
INSERT INTO Ben_Jing_Note VALUES (984, '东垣', '去肤中留垢，心下水痞。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 34);
INSERT INTO Ben_Jing_Note VALUES (985, '大明', '安心除劳治骨蒸，洗肝目多泪，口干心热，杀疳虫，治疣心痛，鼻衄，肠风下血，后急热肿痛。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 96);
INSERT INTO Ben_Jing_Note VALUES (986, '容川', '味甘有液，得土之湿气，功能补脾阴即补脾之水以补湿，因其色白，得土中之金气，故有益肺之功，带酸之性故兼入肝肺。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 47);
INSERT INTO Ben_Jing_Note VALUES (987, '雷斅', '作末浸酒饮，治目僻眼瘫。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 98);
INSERT INTO Ben_Jing_Note VALUES (988, '好古', '搜肝风。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 99);
INSERT INTO Ben_Jing_Note VALUES (989, '倪注', '按此药专治热与利湿，凡出现热症皆可用，但是须要辨明是属于何种热，例如虚热，则须在去热药中增加补虚的药物，像黄连阿胶汤；如是实热，则须在去热的药中加去实的药物，像三黄泻心汤之类，学者按此推之，即可善用黄连。黄连大寒，得水之性，故去热。与阿胶鸡子黄为填离清坎之药，故治心内之热。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 51);
INSERT INTO Ben_Jing_Note VALUES (990, '灵胎', '补心通窍，益智除邪。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 35);
INSERT INTO Ben_Jing_Note VALUES (991, '好古', '润肝燥，补肝经风虚。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 102);
INSERT INTO Ben_Jing_Note VALUES (992, '附录', '鲜石斛—石斛茎之鲜者，以铁皮鲜石斛为最良，治胃中大热，津液干竭，凉肺生精，胜于石斛。钗石斛—石斛之状似金钗者，较之寻常石斛凉性多而黏性少，滋阴降火，于小儿胃热尤宜。川石斛—石斛之产于四川者，其茎紧小，清热之功较着。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 38);
INSERT INTO Ben_Jing_Note VALUES (993, '好古', '疗鼻窒不闻香臭。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 108);
INSERT INTO Ben_Jing_Note VALUES (994, '甄权', '蜂蜡主孕妇胎动，下血不绝欲死，以鸡子大煎三五沸。投美酒半升立愈，又主白发镊去，消蜡点孔中即生黑发。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 115);
INSERT INTO Ben_Jing_Note VALUES (995, '日华', '治血麻痹。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 118);
INSERT INTO Ben_Jing_Note VALUES (996, '灵胎', '滋血脉，养脾胃。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 123);
INSERT INTO Ben_Jing_Note VALUES (997, '黄雄鸡', '味甘酸咸．性平无毒，主伤中消渴，小便数而不禁，肠澼泄痢，补盖五脏绝伤，添髓生精，助阳气，疗五劳，益气力，去冷气，消水癖，治产后虚羸，煮汁煎药服佳。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 113);
INSERT INTO Ben_Jing_Note VALUES (998, '容川', '升麻味甘，能升脾胃之气，其能升之理则因根中有孔道，引水气上达于苗，故性主升，然无四散之性，因为是根之性，升麻根大于苗，故根之得气也厚，故专取其根。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 82);
INSERT INTO Ben_Jing_Note VALUES (999, '灵胎', '养肝滋血，补脾安胎。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 112);
INSERT INTO Ben_Jing_Note VALUES (1000, '日华', '补虚劳，逐一切风气；长肌肉，益毛发，通乳汁，止消渴，催生难产。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 129);
INSERT INTO Ben_Jing_Note VALUES (1001, '李珣', '长肌肤，益气力，老人风秘，并宜炼服。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 135);
INSERT INTO Ben_Jing_Note VALUES (1002, '灵胎', '除湿热，解疮毒。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 137);
INSERT INTO Ben_Jing_Note VALUES (1003, '容川', '得石之性多，味虽咸而不甚，且此山之石即能生水，流而为泉，是故此石纯具水性，禀天水之寒，故能清热。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 138);
INSERT INTO Ben_Jing_Note VALUES (1004, '附录', '杜牛膝──性专走下，毫无补益，散恶血破症瘕，止痛，治淋药中宜用。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 23);
INSERT INTO Ben_Jing_Note VALUES (1005, '藏器', '生治足睡，熟治不眠。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 104);
INSERT INTO Ben_Jing_Note VALUES (1006, '备要', '外敷治痈疽蛇犬毒。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 48);
INSERT INTO Ben_Jing_Note VALUES (1007, '容川', '麻油因其油质，故主滑利，但性不热，故行缓，无辛，故气不走窜，故下大便也缓。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 128);
INSERT INTO Ben_Jing_Note VALUES (1008, '吴克潜', '曰天冬麦冬性相近，故一般方剂二药并用。仲醇曰痰之标在脾胃肺，其本在肾，若非肾家有火，炎上薄肺，煎熬津而成黏腻，则痰何自成。天冬味苦气寒，能清热保肺，下通于肾，故为消痰止咳必用要药，观此则天冬治痰乃治本元不足虚火所生之痰，而与外感咳嗽，痰饮咳嗽，虚寒痰嗽，风火痰喘等均不相同，著名方剂如天门冬散治小儿心胸烦闷，体热咳嗽。三才封髓丹治虚火上炎，梦遗失精，滋阴养血，降心火益肾水。三才丸治气血俱虚，虚劳咳嗽。天王补心丹治心血不足，津液枯涸，健忘怔忡。秉水阴，能滋肺以清气分。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 18);
INSERT INTO Ben_Jing_Note VALUES (1009, '纲目', '散赤目肿痛，水肿风肿，产后血滞。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 152);
INSERT INTO Ben_Jing_Note VALUES (1010, '药征', '主治挛急引强急也，旁治咳嗽奔豚烦躁，身疼胁痛，腹中痛。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 124);
INSERT INTO Ben_Jing_Note VALUES (1011, '灵胎', '育阴滋水。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 157);
INSERT INTO Ben_Jing_Note VALUES (1012, '灵胎', '化痰定痫，泄热除邪。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 109);
INSERT INTO Ben_Jing_Note VALUES (1013, '纲目', '杀疳虫，炒存性，米饮服，治肠风泻血并热痢。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 150);
INSERT INTO Ben_Jing_Note VALUES (1014, '藏器', '生者堕胎，蒸食消酒毒，可断谷不饥，作粉尤妙。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 149);
INSERT INTO Ben_Jing_Note VALUES (1015, '灵胎', '淸血除邪。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 167);
INSERT INTO Ben_Jing_Note VALUES (1016, '孟铣', '生皮捣和三年醋泡封，暴患赤肿，女人妒乳肿，日六七易效。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 95);
INSERT INTO Ben_Jing_Note VALUES (1017, '容川', '为春之花，而根微酸，故主能敛肝木，降火行血。苦性较轻，故白芍只微降，大黄则降力大。味苦能泻血，其色白，故又能行气分之水。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 154);
INSERT INTO Ben_Jing_Note VALUES (1018, '容川', '当归辛苦，得地火之味，其气微温，得木之性，质多油，此因得地之湿，故能助心生血，以行于肝，其功专生血。归首之性升，故主生血，尾之性降，故主行血，性温润，可以去寒结。风能胜湿，风伤血则筋燥，当归能润燥。其味辛温，火也，其汁油润，水也，一物而具二者，是水交于火所化之物也，恰与血之生化相同，故主补血。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 151);
INSERT INTO Ben_Jing_Note VALUES (1019, '灵胎', '养筋骨，祛风湿。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 97);
INSERT INTO Ben_Jing_Note VALUES (1020, '容川', '干姜炮制则温而不烈，性轻而上浮，而四逆理中，则干姜不炮，取其性烈，乃能去寒，能温土中之阳，阳出则阴消，为治寒湿之药，补火之药是火能化水也，故能行血。能补脾之火，是以土治水。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 147);
INSERT INTO Ben_Jing_Note VALUES (1021, '容川', '其色味如红花，而根性主下达，与花不同，故主内及泄中下焦之血。气属阳，血属阴，瘀血阻气，则阳不入阴，蒸热汗出，宜破其血，使气得入于血中，则不壅热，丹皮主之。气通则热不蒸，此为治热之变法也。色赤味苦，泻火，即能泻血。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 182);
INSERT INTO Ben_Jing_Note VALUES (1022, '灵胎', '敛气固精，杀虫止利。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 32);
INSERT INTO Ben_Jing_Note VALUES (1023, '容川', '枳壳为木实，有木之质，木能疏土，故归脾胃，其味比厚朴轻，故理胃气。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 194);
INSERT INTO Ben_Jing_Note VALUES (1024, '好古', '泻火而上清肺金，滋水而下润肾燥，治命门相火有馀。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 159);
INSERT INTO Ben_Jing_Note VALUES (1025, '容川', '栀子味苦象心包。故能泻心包之火。性苦寒，有皮膈，象心包，内之子赤，正属心之色，其花色白，当属肺金，结子成赤，当属心火，是为从肺入心，正治心中烦热之药。心为君主之官，肺为相傅之官，以制节心火之太过，栀子花白子赤，正是以肺金而归制心火也，故仲景治心中懊憹必用栀子豆豉汤。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 193);
INSERT INTO Ben_Jing_Note VALUES (1026, '容川', '山茱萸酸而质润，故专入肝，滋养阴血。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 198);
INSERT INTO Ben_Jing_Note VALUES (1027, '时珍', '敛肺，涩肠，止久嗽泻利，反胃咦膈，蛔厥吐逆，消肿涌痰，杀虫，解鱼毒，硫磺毒。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 229);
INSERT INTO Ben_Jing_Note VALUES (1028, '药征', '主治水也。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 180);
INSERT INTO Ben_Jing_Note VALUES (1029, '倪注', '炮附子可以固表虚，去肺家寒症，生附子可以去里寒，二者截然不同。医者须明辨之，方可以用之无误。临床时可见病人因表虚而至真阳外越，我辈当以炮附子为救逆之剂。如脉细无力，病人昏迷不识人，此为里寒至极，因而真阳不足，当此之时又须以生附为主。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 242);
INSERT INTO Ben_Jing_Note VALUES (1030, '容川', '大黄味苦大寒，形大而气烈，得地火之阴味，色黄，为火之退气所发见，故能退火，专下血分之结，以味厚有烈气，味既降又得气之助，故走脾胃，下行更速更远。寒性皆下引。其纯于苦味，又有雄烈之气，以气行其苦味，则走而不守。与黄连之守而不走，正好相反。大黄是治火之药，禀地气，入后天之血分也。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 247);
INSERT INTO Ben_Jing_Note VALUES (1031, '好古', '泄热，益胆气。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 158);
INSERT INTO Ben_Jing_Note VALUES (1032, '药征', '主治浊唾肿脓也，旁治咽喉痛。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 249);
INSERT INTO Ben_Jing_Note VALUES (1033, '苏颂', '治妇人血风，及疮疥为要药。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 188);
INSERT INTO Ben_Jing_Note VALUES (1034, '药征', '主治热痢下重也。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 273);
INSERT INTO Ben_Jing_Note VALUES (1035, '时珍', '治目盲障翳。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 228);
INSERT INTO Ben_Jing_Note VALUES (1036, '容川', '巴豆油滑，而主下降，故能利大便。味大辛故性烈，大热则悍，以悍烈行其滑利，故迅捷而不留也。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 286);
INSERT INTO Ben_Jing_Note VALUES (1037, '汪机', '烧烟熏久痢脱肛。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 288);
INSERT INTO Ben_Jing_Note VALUES (1038, '时珍', '煎服治黄疸，白浊，酒煮熨诸肿痛，去风，止痛，消肿。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 289);
INSERT INTO Ben_Jing_Note VALUES (1039, '倪注', '此药多使用于下脓血利不止，常与赤石脂同用。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 8);
INSERT INTO Ben_Jing_Note VALUES (1040, '容川', '射干微苦，利喉中之痰。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 255);
INSERT INTO Ben_Jing_Note VALUES (1041, '宗奭', '治折伤坠蹼蓄血有功。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 310);
INSERT INTO Ben_Jing_Note VALUES (1042, '东垣', '逐水暖脾，生津导气，平火止泄，除虚热，开肌理。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 94);
INSERT INTO Ben_Jing_Note VALUES (1043, '药征', '主治胸间停水也，故治喘咳，而旁治短气结胸，心痛，形体浮肿。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 322);
INSERT INTO Ben_Jing_Note VALUES (1044, '苏敬', '治痰嗽咽喉不利，合三数枚殊胜。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 328);
INSERT INTO Ben_Jing_Note VALUES (1045, '苏颂', '取汁涂蛇伤，烧啖治小儿腹疳。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 330);
INSERT INTO Ben_Jing_Note VALUES (1046, '孟铣', '散气，去关节烦热，令人心孔开，暴痢后气满不能食者，煮食一顿即愈，和鲤鱼煮食，甚治脚气。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 331);
INSERT INTO Ben_Jing_Note VALUES (1047, '大明', '治带下，霍乱转筋，痢后寒热。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 332);
INSERT INTO Ben_Jing_Note VALUES (1048, '苏颂', '通心经，益脾胃。煮饮尤胜，与橘皮相宜。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 333);
INSERT INTO Ben_Jing_Note VALUES (1049, '宗奭', '脾弱不思食人多用，能和胃气，亦用和药。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 334);
INSERT INTO Ben_Jing_Note VALUES (1050, '东垣', '散诸经血结气聚，消肿。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 276);
INSERT INTO Ben_Jing_Note VALUES (1051, '灵胎', '逐瘀血，除邪气。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 321);
INSERT INTO Ben_Jing_Note VALUES (1052, '吴克潜', '打扑跌损，皆伤筋络，气血不行，瘀痈作痛，乳香没药，皆能止痛消肿生肌，故二药多相兼而用。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 339);
INSERT INTO Ben_Jing_Note VALUES (1053, '元素', '补肾，定诸经之痛。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 340);
INSERT INTO Ben_Jing_Note VALUES (1054, '东垣', '治阳毒入胃，下血频痛。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 346);
INSERT INTO Ben_Jing_Note VALUES (1055, '大明', '安心除烦，止渴退热，明耳目，治鼻塞。通小肠下水，破积聚血块，排脓，治疮疖，止痛推生下胞，女人血闭，月候不匀，天行时疾，头痛目眩；羸劣，乳结，下乳。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 347);
INSERT INTO Ben_Jing_Note VALUES (1056, '东垣', '治泄痢下重，能泄下焦阳明气滞。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 349);
INSERT INTO Ben_Jing_Note VALUES (1057, '素问', '麦属火，心之谷也。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 352);
INSERT INTO Ben_Jing_Note VALUES (1058, '好古', '去胁下坚满，瘰疬，一切疮。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 353);
INSERT INTO Ben_Jing_Note VALUES (1059, '药征', '主治胸腹涨满也，旁治腹痛。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 195);
INSERT INTO Ben_Jing_Note VALUES (1060, '好古', '治痞满塞胸，咽膈不通，润肝燥脾。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 192);
INSERT INTO Ben_Jing_Note VALUES (1061, '仲醇', '欲行血理伤，当与当归牛膝肉桂延胡索同用，欲止血疗崩中补不足，则与白胶阿胶地黄麦冬杜仲人参山茱萸杞子黄耆五味同用，欲安胎则与凉血补血顺气药同用，欲疗金疮，则与金疮药同用。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 59);
INSERT INTO Ben_Jing_Note VALUES (1062, '容川', '辛温以散寒湿，能杀虫化湿。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 287);
INSERT INTO Ben_Jing_Note VALUES (1063, '灵胎', '泻肺气，利水道。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 248);
INSERT INTO Ben_Jing_Note VALUES (1064, '灵胎', '平肝熄风。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 16);
INSERT INTO Ben_Jing_Note VALUES (1065, '灵胎', '敛气涩血，定志安神。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 107);
INSERT INTO Ben_Jing_Note VALUES (1066, '容川', '川芎之味更辛苦于当归，得木火之性尤烈，苦而兼辛，有生血之功，质不柔润，性专走窜，故专主行心肝之血。苦者火之味也，苦而兼辛，则性温而有生血之功，苦而不辛，则性凉而专主泻血，温者阴中之阳，是风木本气，故入肝经，根又主上升故能至于巅顶，以散风寒，气滞血瘀，寒热身疼，女子经闭不通，当行血中之气。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 49);
INSERT INTO Ben_Jing_Note VALUES (1067, '元素', '解利手阳明头痛，中风寒热，及肺经风热，头面皮肤风痹燥痒。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 161);
INSERT INTO Ben_Jing_Note VALUES (1068, '东垣', '专治大肠气滞，燥湿不通。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 291);
INSERT INTO Ben_Jing_Note VALUES (1069, '灵胎', '清热止利。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 163);
INSERT INTO Ben_Jing_Note VALUES (1070, '好古', '补肝风虚，治痰功同半夏。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 246);
INSERT INTO Ben_Jing_Note VALUES (1071, '灵胎', '助阳益气。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 140);
INSERT INTO Ben_Jing_Note VALUES (1072, '好古', '补肝风虚。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 245);
INSERT INTO Ben_Jing_Note VALUES (1073, '甄权', '鲤鱼胆点眼治赤肿翳痛。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 122);
INSERT INTO Ben_Jing_Note VALUES (1074, '时珍', '治头风眩晕，皮肤风热，痘疹作痒，破伤风，及疔肿毒疮，大人失音，小儿噤风天吊，惊哭夜啼，阴肿。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 314);
INSERT INTO Ben_Jing_Note VALUES (1075, '灵胎', '其性清浮，治在肌表。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 176);
INSERT INTO Ben_Jing_Note VALUES (1076, '好古', '治冲脉为病，逆气里急，主浮，渗小便秘。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 31);
INSERT INTO Ben_Jing_Note VALUES (1077, '灵胎', '其性轻扬，能逐半表半里之邪。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 27);
INSERT INTO Ben_Jing_Note VALUES (1078, '泡制', '洗净，大小分开，浸泡 4～8 小时，捞出，润透，切 6 毫米纵片，用清水漂 4～6小时，捞出，晒干或烘干即得。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 55);
INSERT INTO Ben_Jing_Note VALUES (1079, '灵胎', '专治风邪。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 56);
INSERT INTO Ben_Jing_Note VALUES (1080, '药征', '主治水毒下利，故兼治便脓血。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 12);
INSERT INTO Ben_Jing_Note VALUES (1081, '药征', '主利水也，故能治小便自利，不利，旁治身烦疼，痰饮失精，眩冒下利喜唾。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 21);
INSERT INTO Ben_Jing_Note VALUES (1082, '容川', '味苦而坚涩，兼水火之性，故能泻肝胆之火。且龙胆草根多而深细，故泻火并兼降利，能清热以利少阳三焦与胆之火。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 36);
INSERT INTO Ben_Jing_Note VALUES (1083, '药征', '主治咳而冒者也。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 67);
INSERT INTO Ben_Jing_Note VALUES (1084, '藏器', '下三焦热毒气，通大小肠，治蚘心痛，敷一切恶疮疥癣，杀一切虫，取一合和鸡子二个，芒硝一两，搅服，少时即泻下热毒。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 83);
INSERT INTO Ben_Jing_Note VALUES (1085, '药征', '主治冲逆也，旁治奔豚头痛，发热恶风，汗出身痛。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 88);
INSERT INTO Ben_Jing_Note VALUES (1086, '容川', '禀北方水中之阳气以生，以北耆最佳，人身之阳气皆从肾与膀胱之气海出发，上循三焦油膜，以达于肺，而为呼吸，分布于皮毛，则为卫气，北耆之体极松，内行水道之孔更大，故知其气盛，黄耆之根长数尺，深入土中，吸收土下之水以上其苗叶，气即水也，故其气盛而能补气，色黄如三焦油膜，故为三焦之良药，能拓里达表，行三焦之水气，又皮色黑，是水火之色也，色黄味甘，乃土之色味也，得味厚故能补，黄耆入土最深，得土之气最厚，故又大补脾，所以黄耆归脾经，又能通利三焦也。与升麻不同，升麻味不厚，故能升不能补。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 54);
INSERT INTO Ben_Jing_Note VALUES (1087, '附方', '肿毒疮疖——翠玉膏：松脂八两，铜绿二两，麻油二两，雄猪胆汁三个，先溶松脂，乃下油胆，倾入水中扯拔，每用摊贴，不须再换。历节风痛——松节酒：松节二十斤，酒五斗，浸二十一天，每服一杯，日五六服。 （外台）头旋脑肿——三月取松花连苔如鼠尾者，切一升，以绢袋浸三升酒中，五日后，空心暖饮五杯。（普济）肺燥咳嗽——风髓膏：松子仁一两，胡桃仁二两，蜜半两，和研膏，每服二钱。 （外台）', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 90);
INSERT INTO Ben_Jing_Note VALUES (1088, '药征', '主治血症及水病也。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 20);
INSERT INTO Ben_Jing_Note VALUES (1089, '药征', '主治小便不利冒眩也，旁治渴。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 34);
INSERT INTO Ben_Jing_Note VALUES (1090, '东垣', '泻伏火救肾水，治冲脉气逆，不渴而小便不通，诸疮痛不可忍。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 96);
INSERT INTO Ben_Jing_Note VALUES (1091, '容川', '蔓荆子气烈，味辛而气发散，而质轻，故主散头目之风。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 99);
INSERT INTO Ben_Jing_Note VALUES (1092, '药征', '主治心中烦悸也，旁治心下痞，吐下腹中痛。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 51);
INSERT INTO Ben_Jing_Note VALUES (1093, '容川', '其性与桂枝同，但桂枝四达，远志则系根体细瘦，故主入心经，以散心中之滞血。味苦亦入心，兼有辛温之性，故能补心火。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 35);
INSERT INTO Ben_Jing_Note VALUES (1094, '汪机', '治胎漏胎堕。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 102);
INSERT INTO Ben_Jing_Note VALUES (1095, '孟铣', '除百病，治一切恶气，及惊怖恍惚。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 108);
INSERT INTO Ben_Jing_Note VALUES (1096, '附方', '  阴头生疮——以蜜煎甘草涂之。  肛门生疮——白蜜一斤、猪胆汁一枚相和，微火煎令可丸，作挺长约三寸，大如手指，涂油纳肛门内，卧令后重，须臾通泻。  热油灼痛——以白蜜涂之。  肝虚雀目——黄蜡溶汁，入蛤粉相合所得，冷却，每以二钱夹入猪肝内煮之，乘热食肝饮汁，日二，以平安为度。  大风疠疾——须眉已无，皮肉已烂成疮者：蜜蜂子，胡蜂子，黄蜂子并炒各一分，白花蛇，乌梢蛇并酒浸去皮骨炙干，全蝎，僵蚕并炒，各一两，地龙炒半两，蝎虎（守宫） ，赤足蜈蚣各 15 条，丹砂一两，雄黄 一分（，龙脑半钱，共为细末，每服一钱匕，蜜汤调下，月三五服。（圣济。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 115);
INSERT INTO Ben_Jing_Note VALUES (1097, '丹溪', '下甲补阴，主阴血不足，去瘀血，止血痢，续筋骨，治劳倦四肢无力。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 118);
INSERT INTO Ben_Jing_Note VALUES (1098, '乌骨鸡', '味甘性平无毒，主补虚劳羸弱．治消渴，益产妇，治女人崩带，一切虚损诸病，大人小儿噤口痢，并煮食饮汁，验鸡舌黑者良。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 113);
INSERT INTO Ben_Jing_Note VALUES (1099, '附方', '  产后秘塞——老人虚秘，麻仁，紫苏子等分，淘净研细，再以水研滤取汁，煮粥饮之。（本事）  月经不通——二三月或半年者，麻仁，桃仁等分，研匀，老酒浸一宿后，日饮一升． （普济）  发落不生——麻仁研汁煮粥频食之。 （圣济总录）  大风癞疾——大麻浸酒，研取白汁，滤去渣，纳瓶中，煮数沸，每饮一杯。  （圣惠）  健忘——麻勃一升，人参二两，为末，蒸令气遍，每卧服一刀圭。 （范汪）', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 129);
INSERT INTO Ben_Jing_Note VALUES (1100, '容川', '能燃，是水中之火也，其味酸，是得木味，水中之阳，发则为木，故味酸而能燃，又是水中之木也，为温下焦肝肾之猛药。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 135);
INSERT INTO Ben_Jing_Note VALUES (1101, '药征', '主治烦渴，旁治谵语烦躁身热。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 138);
INSERT INTO Ben_Jing_Note VALUES (1102, '容川', '牛膝性降，因根深味苦，故能引水火下行。因根坚实，形不空，无升达之孔道，味既苦泻，故下行双足。其节如膝，能利膝胫，以其形似也。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 23);
INSERT INTO Ben_Jing_Note VALUES (1103, '附录', '蓝汁—蓝叶之汁，主一切毒，百毒药，天行热狂，游风热毒肿毒，吐衄血，金疮血闷，烦渴，女人产后血晕。蓝淀—蓝汁中合有石灰者，主解诸毒，敷热疮，小儿秃疮热肿。其止血拔毒杀虫之功，更胜于蓝。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 48);
INSERT INTO Ben_Jing_Note VALUES (1104, '容川', '虽其茎直上，但却如草之丛生，故能上升又能外散，其茎空，直达而上，且无大味，纯得天轻阳之气，故专主气分。一般取其苗，以其苗细长中空，象人毛孔，而气轻扬，故能发汗，能通阳气，直走鼻皮毛，其根坚实味涩，故能止汗。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 152);
INSERT INTO Ben_Jing_Note VALUES (1105, '灵胎', '补脾和中。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 124);
INSERT INTO Ben_Jing_Note VALUES (1106, '括要', '遍身风疹，痛不可忍，肺热生疮，遍身皆是；瘰疬结核，下部疮瘺，诸般实症用之有效。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 150);
INSERT INTO Ben_Jing_Note VALUES (1107, '开宝', '作粉止渴，利大小便，解酒去烦热，压丹石，傅小儿热疮，捣汁饮治小儿热痞。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 149);
INSERT INTO Ben_Jing_Note VALUES (1108, '药征', '主治胸膈烦躁，不能眠也。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 95);
INSERT INTO Ben_Jing_Note VALUES (1109, '药征', '主治结实而拘挛，旁治腹痛头痛，身体不仁，疼痛腹满，咳逆下利肿脓。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 154);
INSERT INTO Ben_Jing_Note VALUES (1110, '灵胎', '补血养血，润肺润肠。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 151);
INSERT INTO Ben_Jing_Note VALUES (1111, '药征', '主治结滞水毒也，旁治呕吐咳，下利，厥冷，腹痛，胸痛，腰痛。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 147);
INSERT INTO Ben_Jing_Note VALUES (1112, '灵胎', '疏肝气，通经脉。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 182);
INSERT INTO Ben_Jing_Note VALUES (1113, '吴克潜', '编药性字典曰矾石用之甚广。今南方人多用置水缸中以解毒，其水澈底澄清。陈师古曰矾石之用有四，吐利风热痰延，取其酸苦涌泄也。治诸血痛，脱肛阴挺疮疡，取其酸涩而收也。治痰饮泄痢，崩带风眼，取其收而燥湿也。治喉痹中蛊蛇虫伤蛰，取其解毒也，矾石经火炙后为枯矾，研细用做敷扑良，渗小儿脐眼，防腐收湿，矾石治一切疮痈恶毒，或为蛇虫所伤，配黄蜡二倍，溶化研细，外用亦可内服。其它配合方剂很多，举凡矾石色暗者不入药，以白而明最佳，故色白者曰白矾：色光明者曰明矾。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 32);
INSERT INTO Ben_Jing_Note VALUES (1114, '容川', '知母叶至难死。拔之犹生，即知其得水气多，故能清气分之热。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 159);
INSERT INTO Ben_Jing_Note VALUES (1115, '药征', '主治心烦也，旁治发黄。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 193);
INSERT INTO Ben_Jing_Note VALUES (1116, '容川', '乌梅极酸，能敛肝木之阳，能化蛔虫，能去坏肉，此是以木来克土，酸能收之义。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 229);
INSERT INTO Ben_Jing_Note VALUES (1117, '药征', '主逐水，能治恶寒，身体四肢及骨节疼痛，或沉重，或不仁，或厥冷，而旁治腹痛失精下利。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 242);
INSERT INTO Ben_Jing_Note VALUES (1118, '药征', '主通利及结毒也，故能治胸满腹满，腹痛及便秘，小便不利，旁治发黄瘀血脓肿。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 247);
INSERT INTO Ben_Jing_Note VALUES (1119, '吴克潜', '秦艽味苦能杀虫、故外科亦用之，时珍曰：阳明有湿则身体疼烦热，阳明有热则日晡潮热骨蒸，沈金鳌曰感受风寒发热，遍身疼痛，必以秦艽治之，以其能散结除邪也。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 158);
INSERT INTO Ben_Jing_Note VALUES (1120, '药征', '主治心腹胸膈之毒，兼治心腹卒痛，胀满吐脓。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 286);
INSERT INTO Ben_Jing_Note VALUES (1121, '附方', '金疮出血——柳絮封之即止。脚多汗湿——柳絮着鞋袜内穿之。眉毛脱落——柳叶阴干为末，以姜汁在铁器内调膏，夜夜抹之。黄疸初起——柳枝煮浓汁半升，顿服。乳痈妒乳——初起坚紫，众疗不愈，柳根皮熟捣，火温棉裹慰之，冷更易，一宿消。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 289);
INSERT INTO Ben_Jing_Note VALUES (1122, '容川', '是石谷中之土质，又具涩性，故以填涩胃肠，甘而微咸，甘能补正以止利，咸能入肾以涩精。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 8);
INSERT INTO Ben_Jing_Note VALUES (1123, '容川', '锐而善入，又能允血，故主攻血积。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 310);
INSERT INTO Ben_Jing_Note VALUES (1124, '好古', '泻膀胱，益脾胃，治肾积奔豚。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 94);
INSERT INTO Ben_Jing_Note VALUES (1125, '丹溪', '实大肠，敛肺降火。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 328);
INSERT INTO Ben_Jing_Note VALUES (1126, '日华', '斑者治疟疾疔肿。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 330);
INSERT INTO Ben_Jing_Note VALUES (1127, '日华', '解小麦热毒，煮汁解酒毒，解衣粘缀。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 331);
INSERT INTO Ben_Jing_Note VALUES (1128, '好古', '治带脉为病，腹胀满，腰溶溶如坐水中。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 332);
INSERT INTO Ben_Jing_Note VALUES (1129, '苏敬', '下气消缓，虚者宜之，主安胎。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 333);
INSERT INTO Ben_Jing_Note VALUES (1130, '容川', '连翘亦象心包，而质轻扬，味征苦，故轻扬上达，能清心与上焦头目之热，其无辛散之气，故不主散。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 276);
INSERT INTO Ben_Jing_Note VALUES (1131, '倪注', '痘家勿用，因仁有生气，可令痘长。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 321);
INSERT INTO Ben_Jing_Note VALUES (1132, '藏器', '利大小便，令人心宽下气。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 347);
INSERT INTO Ben_Jing_Note VALUES (1133, '思邈', '心病宜食之，利产妇。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 349);
INSERT INTO Ben_Jing_Note VALUES (1134, '圣惠方', '小麦饭治疗烦热少睡多渴。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 352);
INSERT INTO Ben_Jing_Note VALUES (1135, '药征', '主治胸腹之动也，旁治惊狂烦躁。牡蛎黄连龙骨同治烦躁，而各有所主也，心口黄连所主，脐下龙骨所主也，而部位不定，胸腹烦躁者，牡蛎所主也。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 353);
INSERT INTO Ben_Jing_Note VALUES (1136, '容川', '厚朴本皮，味比枳壳更重，故理脾气，故仲景用枳壳治心下满，用厚朴治腹痛，此为此二药轻重之别所在。厚朴行大肠之气，故肺气得泄。仲景治喘有桂枝加厚朴杏仁汤，即因于此。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 195);
INSERT INTO Ben_Jing_Note VALUES (1137, '药征', '主治呕而胸满。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 192);
INSERT INTO Ben_Jing_Note VALUES (1138, '吴克潜', '近世用为平肝良药，头痛目疼多因肝经风热上乘所致，菊花入肝，兼行周身，血脉不利而死肌，得菊花之利血脉皆效，特长于解毒，外科方剂用之治疔毒发背者，沈金鳌曰菊花并茎叶打汁饮，外涂内服皆可。时医用之因防邪热过久而酝酿化毒；平淡多奇功。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 16);
INSERT INTO Ben_Jing_Note VALUES (1139, '灵胎', '下逆开窍。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 15);
INSERT INTO Ben_Jing_Note VALUES (1140, '灵胎', '养胃舒筋，除湿泄热。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 33);
INSERT INTO Ben_Jing_Note VALUES (1141, '宗奭', '研和龙脑点赤眼。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 291);
INSERT INTO Ben_Jing_Note VALUES (1142, '容川', '此生当夏之半，其根成于秋时，得燥金辛烈之气，故主降逆水饮，为阳明之药，此为真性。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 245);
INSERT INTO Ben_Jing_Note VALUES (1143, '肘后', '鲤鱼胆点雀目燥痛即明。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 122);
INSERT INTO Ben_Jing_Note VALUES (1144, '附方', '  小儿惊啼——蝉蜕后半截为末，入薄荷脑少许，每服一匙蜜调下，亦治夜啼。  瘾疹——皮肤起红瘢痒，蝉蜕与薄荷叶等分为末，每酒服一钱，日三。  痘后目翳——蝉蜕为末，每服一钱，日二。  小儿阴肿——蝉蜕半两煎水洗，内服五苓散，即肿消痛止。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 314);
INSERT INTO Ben_Jing_Note VALUES (1145, '附方', '  水气洪肿——小便不利者，浮萍日干为未，每服方寸匕，日二。  身上虚痒——浮萍末一钱，以黄芩一钱，用四物汤煎汤调下。一九○、王瓜', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 176);
INSERT INTO Ben_Jing_Note VALUES (1146, '吴克潜', '白芷为肺胃大肠三经气分药，亦入三经血分，擅散风去湿之功，配白芷治偏正头风，齿痛，带下者，成方颇多。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 161);
INSERT INTO Ben_Jing_Note VALUES (1147, '容川', '细辛形细色黑，故入少阴经，味大辛，能温散少阴之风寒，而少阴为寒水之脏，寒则水气上泛，细辛散少阴之寒，能达水中之阳，故能逐水饮。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 37);
INSERT INTO Ben_Jing_Note VALUES (1148, '吴克潜', '曰九土即九州岛岛土气之精华，故能解诸毒如神，古时有炼石为丹服之者多中药石之毒，服甘草而解，故曰安和七十二种石。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 19);
INSERT INTO Ben_Jing_Note VALUES (1149, '倪注', '一般桂枝多用于动脉有循环问题，能行阳达表。', 1, '2019-03-09 14:47:38', '2019-03-09 14:47:38', 89);
INSERT INTO Ben_Jing_Note VALUES (1150, '灵胎', '辟邪逐疫。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 31);
INSERT INTO Ben_Jing_Note VALUES (1151, '酒制', '取肉苁蓉片，加白酒调匀，置密闭容器内，隔水蒸至酒被吸尽，停火稍闷，取出晒干即得。一般酒制后，较能温肾助阳。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 55);
INSERT INTO Ben_Jing_Note VALUES (1152, '药征', '主治肌表之水，故能治黄汗盗汗皮水，又旁治身体肿或不仁者。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 54);
INSERT INTO Ben_Jing_Note VALUES (1153, '灵胎', '熟地补血，生地凉血。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 20);
INSERT INTO Ben_Jing_Note VALUES (1154, '灵胎', '消水利肿。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 34);
INSERT INTO Ben_Jing_Note VALUES (1155, '丹溪', '得知母降阴火，得苍术除湿清热，为治痿要药，得细辛泻膀胱火，治口舌生疮。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 96);
INSERT INTO Ben_Jing_Note VALUES (1156, '灵胎', '除热燥湿，明目止利。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 51);
INSERT INTO Ben_Jing_Note VALUES (1157, '容川', '杜仲柔韧，象人之筋膜，能伸能缩，极其坚韧，包紫黑，味纯厚，故入肝肾，以强人筋骨。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 102);
INSERT INTO Ben_Jing_Note VALUES (1158, '灵胎', '除邪辟秽，开窍杀虫。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 108);
INSERT INTO Ben_Jing_Note VALUES (1159, '容川', '龟之性伏，其精在板，能通任脉，任为离中之阴，以下交于督，合为既济之象，故龟板益阴滋心肾。与鹿茸是对子。以水配火，不使壮火食气，如此气纳矣。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 118);
INSERT INTO Ben_Jing_Note VALUES (1160, '鸡内金', '味甘性平无毒，主化积，治反胃，疗泄痢尿血下血，崩中带下，小儿食疟疳积，大人淋沥，消导酒积，外敷治一切口疮，牙疳腿疮，谷道生疮。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 113);
INSERT INTO Ben_Jing_Note VALUES (1161, '容川', '麻仁油滑，而无辛烈之性，故但能润降，不能速下。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 129);
INSERT INTO Ben_Jing_Note VALUES (1162, '灵胎', '驱寒燥湿。杀虫化金。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 135);
INSERT INTO Ben_Jing_Note VALUES (1163, '药征', '主治喘咳水气也，旁治恶风恶寒，无汗，身疼，骨节痛，一身黄肿。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 152);
INSERT INTO Ben_Jing_Note VALUES (1164, '吴克潜', '古方中多用大枣，因欲调和营卫，使气血流通津液输布，百药都须其协助，因此方中多用姜枣甘草等，经云：里不足者，以甘补之，形不足者温之以气，大枣甘温，专补脾胃，令津液自生，使十二经脉皆通畅，今医以温脾健胃时多选用红枣，滋阴养胃则用黑枣，而红枣即大枣，黑枣生于南方，黏液多而温性少，又名南枣，著名方剂有大枣汤（大枣十五枚，附子一枚，甘草一尺，黄耆四两，生姜二两，麻黄五两)治历节疼痛甚良。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 124);
INSERT INTO Ben_Jing_Note VALUES (1165, '灵胎', '清心肝火，去小肠热。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 150);
INSERT INTO Ben_Jing_Note VALUES (1166, '苏敬', '猘狗伤捣汁饮，并末傅之。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 149);
INSERT INTO Ben_Jing_Note VALUES (1167, '灵胎', '平肝止痛。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 154);
INSERT INTO Ben_Jing_Note VALUES (1168, '灵胎', '驱寒除湿，和血通气。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 147);
INSERT INTO Ben_Jing_Note VALUES (1169, '倪注', '枯矾可与麝香合用，因为麝香无孔不入，配合之则可去脑鼻之间的风寒与瘀伤。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 32);
INSERT INTO Ben_Jing_Note VALUES (1170, '灵胎', '清胃热，泻肺火。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 193);
INSERT INTO Ben_Jing_Note VALUES (1171, '汪颖', '治乳痈肿毒，杵烂贴之佳。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 229);
INSERT INTO Ben_Jing_Note VALUES (1172, '灵胎', '逐寒邪，回阳气。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 242);
INSERT INTO Ben_Jing_Note VALUES (1173, '灵胎', '攻凝结，消积滞。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 247);
INSERT INTO Ben_Jing_Note VALUES (1174, '容川', '秦艽肌纹多，左右交缠。故治左右偏风，筋脉疼痛之症，利于续接经脉。一七○、百合', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 158);
INSERT INTO Ben_Jing_Note VALUES (1175, '灵胎', '逐瘀破积。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 310);
INSERT INTO Ben_Jing_Note VALUES (1176, '容川', '黏涩又味甘，能填补止泻利。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 331);
INSERT INTO Ben_Jing_Note VALUES (1177, '容川', '艾叶味苦而气温，其茸又能发火，是以能温肝补火，禀母木火之气，能入血室，女人经痛，乃凝血作痛，艾叶可去血室中之冷气也。补火之药是火能化水也，故能行血。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 332);
INSERT INTO Ben_Jing_Note VALUES (1178, '容川', '紫苏色红味辛香，能散血分肌肉中之寒，枝叶披离，故主散之性多，而主升之性少。苏子——主上气咳逆冷气，及腰脚中湿气，风结气，调中止霍乱，消五膈呕吐反胃，消痰止嗽，利大小便，治肺气喘急。苏叶——主发散，盂铣曰苏子与苏叶同功，发散风气宜用叶，清利上下宜用子，近世多用以发散风寒，至化痰之力，则不如子也。主散气分之热，如喉为气之海。苏梗——主顺气表散，汪昂曰，叶发汗散寒，梗顺气安胎，子开郁降气，消痰定喘。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 333);
INSERT INTO Ben_Jing_Note VALUES (1179, '灵胎', '散郁火，治疮疡。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 276);
INSERT INTO Ben_Jing_Note VALUES (1180, '药征', '主治瘀血，少腹满痛，兼治肠痈及妇人经水不利。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 321);
INSERT INTO Ben_Jing_Note VALUES (1181, '李珣', '主诸痿疮，喉痹咽痛，浓煎含咽。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 347);
INSERT INTO Ben_Jing_Note VALUES (1182, '孟铣', '治女人带下赤白，作羹食之，骨哽在咽不去者，食之即下。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 349);
INSERT INTO Ben_Jing_Note VALUES (1183, '附录', '浮小麦——麦之性轻，入水浮起者，味咸性寒无毒，主益气除烦。治盗汗自汗，骨蒸虚热，妇人劳热。麦苗——味辛性寒，主消酒毒，暴热，酒疸目黄，并捣汁日饮。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 352);
INSERT INTO Ben_Jing_Note VALUES (1184, '灵胎', '驱肝风，逐肺寒。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 192);
INSERT INTO Ben_Jing_Note VALUES (1185, '附加菊叶', '菊叶解毒之功胜于花，以鲜者良，捣汁敷一切肿毒，亦可内服。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 16);
INSERT INTO Ben_Jing_Note VALUES (1186, '吴克潜', '菖蒲南人多常用置食水中，或加矾少许，解一切毒，周颠仙曰常食菖蒲饮水，永无腹痛之疾，沈金鳌曰治禁口痢，屡用屡效，道藏经曰能治一切风，手足顽痹瘫痪不遂。正传曰九节菖蒲去毛捣末，入黑猪心一个，批开煮服，之癫痫风疾，著名方剂有菖蒲丸（石菖蒲人参丹参天冬麦冬赤石脂）。治小儿心气不足，不能言语。又醋糊丸棉裹置耳中，治耳内卒痛，聋闭不闻，有菖蒲益智丸（菖蒲远志人参桔梗牛膝附子茯苓桂心)。治健忘恍惚，破积聚止痛，安心定神聪明耳目均效。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 15);
INSERT INTO Ben_Jing_Note VALUES (1187, '容川', '此药生于水中，味极淡，不补又不涩，专于渗利。凡味淡气薄皆能降而渗利，其属阳之阴，不行在上之窍，而行在下之清窍，故能利小便。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 33);
INSERT INTO Ben_Jing_Note VALUES (1188, '倪注', '一般使用生半夏以止呕吐时，多加生姜以制其毒，重症病患之呕吐，唯此生半夏可治，学者当用之勿惧，今之医多不敢使用生药，因惧其毒性，实则此药又名婆婆药，意即可以让人失音，并不会致命，如果失音时，只须口含生姜片，须臾声自回。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 245);
INSERT INTO Ben_Jing_Note VALUES (1189, '容川', '以类入肝胆，味苦又生水中，正得水性．为治肝胆火之正药，故治喉目。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 122);
INSERT INTO Ben_Jing_Note VALUES (1190, '灵胎', '敛肺补肾。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 67);
INSERT INTO Ben_Jing_Note VALUES (1191, '孟铣', '士良曰外科熬膏多用之，以其能凉血解毒，止毒生肌也，虞鍑曰生用消疮毒，熟用利肠胃。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 83);
INSERT INTO Ben_Jing_Note VALUES (1192, '倪注', '去中焦肝胆脾胃之湿热。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 36);
INSERT INTO Ben_Jing_Note VALUES (1193, '药征', '主治宿饮停水也，故治水气在心下而咳满，或上逆，或胁痛。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 37);
INSERT INTO Ben_Jing_Note VALUES (1194, '容川', '味甘正入脾。守而不走，补中气，和诸药，根深四五尺，与黄耆同，但黄耆中空，属气分，是得土中之水气，甘草中实，纯得土气之厚，故深长且实，能补脾阴，益胃之阳。炙用是取其益胃，使胃气上升，如炙甘草汤。生用取其气平，如芍药甘草汤。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 19);
INSERT INTO Ben_Jing_Note VALUES (1195, '附录', '桂心主补阳活血，治一切风气，补五劳七伤，通九窍利关节，益精明目，暖腰膝，破痃癖症瘕，消瘀血治风痹骨节挛缩，续筋骨，生肌肉。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 89);
INSERT INTO Ben_Jing_Note VALUES (1196, '药征', '主治悸及肉瞬筋惕也。旁治小便不利，头眩烦躁。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 94);
INSERT INTO Ben_Jing_Note VALUES (1197, '灵胎', '白术补脾补气，苍术燥湿发汗。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 21);
INSERT INTO Ben_Jing_Note VALUES (1198, '倪注', '桂枝为古今皆用之要药，治疗甚广，与麻黄柴胡荆芥防风生姜葱白等同用，治风寒束表，与桑枝附子牛膝威灵仙茯苓防己等同用， 治关节疼痛。与白芥子厚朴杏仁半夏等同用，治上气咳逆。与白芍同用解肌和中，与葛根同用发汗退热， 伤寒论中使用最多的就是桂枝了。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 88);
INSERT INTO Ben_Jing_Note VALUES (1199, '吴克潜', '曰木香调气定痛，功力甚佳，治中下二焦气结滞而不运，宜槟榔为使，故治痢疾腹痛，有木香槟榔丸之设也。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 31);
INSERT INTO Ben_Jing_Note VALUES (1200, '灵胎', '内补脾胃，外托疮毒。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 54);
INSERT INTO Ben_Jing_Note VALUES (1201, '灵胎', '发汗止喘。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 152);
INSERT INTO Ben_Jing_Note VALUES (1202, '容川', '大枣禀火之赤色，亦入心以养血，肉黄，得火生土之性，故纯于补脾胃。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 124);
INSERT INTO Ben_Jing_Note VALUES (1203, '容川', '花粉清热，入气分，色白味苦而有液，故泻热之功轻，入胃生津之力重。瓜篓实能治膈间之结气，因篓之膜似人胸之膈膜，又似人腹之象，又治大腹之气，因子有油而气烈，包有瓠而味苦，捣烂合用，能解膈膜之痰火', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 149);
INSERT INTO Ben_Jing_Note VALUES (1204, '灵胎', '补肺益气。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 158);
INSERT INTO Ben_Jing_Note VALUES (1205, '倪注', '此药常与银花同用，皆属味清而质轻，皆能升清气。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 276);
INSERT INTO Ben_Jing_Note VALUES (1206, '冬垣', '通经利窍，导小肠火。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 347);
INSERT INTO Ben_Jing_Note VALUES (1207, '苏敬', '白者补益，赤者疗金疮及风，生肌肉。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 349);
INSERT INTO Ben_Jing_Note VALUES (1208, '倪注', '常用于鼻塞不通，脑部受损。内服外用皆宜。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 15);
INSERT INTO Ben_Jing_Note VALUES (1209, '附录', '苡仁根──主下三焦，能堕胎，治黄疸，卒心腹烦满，胸胁痛。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 33);
INSERT INTO Ben_Jing_Note VALUES (1210, '药征', '主治痰饮呕吐也，旁治心痛逆满，咽中痛，咳悸，腹中雷鸣。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 245);
INSERT INTO Ben_Jing_Note VALUES (1211, '吴克潜', '中医之用麝香乃因其有开窍之功，一般毛孔闭塞者麻黄以通之，腠理闭塞者葛根以通之，荣卫闭塞者桂枝以通之，风塞者独活羌活以通之，血塞者延胡郁金以通之，肺气塞者桔梗前胡以通之，心气塞者菖蒲远志以通之，胃气塞者藿香川朴以通之，肝气塞者香附川芎以通之。但是此类用法可以一脏一腑一经为治，并非闭塞之重者。当诸邪深入，或内病暴作，内则经络全被遏阻之势，外则诸窍亦有闭塞之象，是故心神失其主，神志为蒙，此时势非麝香通之不可，著名方剂有麝香天麻丸（麝香天麻防风川芎天南星菊花，治风痰气厥，头痛目眩、四肢倦怠，睡卧不宁），麝昏蟾酥丸（麝香蟾酥轻粉乳香砒石雄黄巴豆寒水石治痈疽发背．疔疮内毒），及至宝丹紫雪丹苏合丸牛黄清心丸等。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 108);
INSERT INTO Ben_Jing_Note VALUES (1212, '龟肉', '味甘酸，性温，无毒。主筋骨疼痛，年久寒嗽，泻血血痢，酿酒治大风缓急，四肢拘挛，瘫缓不收，煮食治疗身肿痿折。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 118);
INSERT INTO Ben_Jing_Note VALUES (1213, '倪注', '仲景经方中使用甘草多处，即知道仲师之重视甘草，而现今却被西洋人购去制造成许多药品或是食物，再回销我国，实在是感叹国家之愚昧竟至于斯，我辈当以复兴中医药为职志，将中药制剂卖到国外，既造福人类又可以赚取外汇，岂非一举两得。甘草缓中，故遇排腹积水时忌用，因其能守，故不利于排水。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 19);
INSERT INTO Ben_Jing_Note VALUES (1214, '日华', '主九种心痛，腹内冷气痛不可忍，咳逆结气雍塞，脚痹不仁，止下利，杀三虫，治鼻中息肉，破血通利月闭，胎衣不下。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 89);
INSERT INTO Ben_Jing_Note VALUES (1215, '灵胎', '疏风散邪，驱逐寒气。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 37);
INSERT INTO Ben_Jing_Note VALUES (1216, '灵胎', '泻肝胆火。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 36);
INSERT INTO Ben_Jing_Note VALUES (1217, '灵胎', '补脾生津，化痰利水。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 94);
INSERT INTO Ben_Jing_Note VALUES (1218, '吴克潜', '曰现市面用药有三种，鲜生地干地黄熟地黄，一物各有其用，鲜生地多用于肺胃实热，口燥烦渴，舌苔光绛色。干地黄多用于滋养阴血，血虚内热之症。熟地黄则用于补阴生精，扶养亏弱之虚。是故元素曰生地凉血，血热者须用。熟地微温，血衰者须用。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 20);
INSERT INTO Ben_Jing_Note VALUES (1219, '鸡屎白', '味苦性微寒无毒，主行水消积，治鼓胀，疗转筋入腹，破石淋二便，下气破血，治伤寒，寒热中风，贼风风痹，白虎风，贴风痛头痛，止遗溺，灭瘢痕，小儿客忤惊啼，解金银毒，酒服疗乳妒乳痈乳头破裂，外敷秃疮耳疮瘰疬。救急方——古人急救吊死或勒死之人时，将活雄鸡一只，腹部切开，趁热直接放置到死人的脖子位置，病患保持平躺姿势，可以救助于危急之时。一二○、雁肪', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 113);
INSERT INTO Ben_Jing_Note VALUES (1220, '灵胎', '清热燥湿。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 96);
INSERT INTO Ben_Jing_Note VALUES (1221, '宗奭', '曰此专决泄胸腹间滞塞冷气。他则次之，得橘皮蔻仁生姜相佐，效尤速。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 31);
INSERT INTO Ben_Jing_Note VALUES (1222, '倪注', '一般言黄耆专于补气，凡气虚之症皆可用，又治自汗，补中气之不足，外科及痘科多以之托里透脓，肾亏溺血亦治之。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 54);
INSERT INTO Ben_Jing_Note VALUES (1223, '倪注', '麻黄为发汗第一要药，能退热平喘利水，皆其解散之功，凡风寒初袭肌表，不得解散则发为头痛项强恶寒诸症状，发其汗则风寒得以解散，诸症自除，举凡外感初起之发热，多由汗不得出，因势利导使汗出而热解，此本经之所言去邪热也，又肺受风寒不解，则咳气上逆，用麻黄利水道，外为发汗，是故痰水因而下行，风寒因而得以宣泄，因病根解除，咳气上逆自愈，若以之点眼，可使瞳孔放大，此因其能散。今麻黄配桂枝能散营分之寒邪，配膏能泄卫分之风热，皮肉不仁，著名方剂如麻黄汤，麻杏甘石汤，大青龙汤。因麻黄根能止汗，节亦止汗，故欲发汗，则须去根与节。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 152);
INSERT INTO Ben_Jing_Note VALUES (1224, '灵胎', '开肺降逆，消痰燥湿。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 245);
INSERT INTO Ben_Jing_Note VALUES (1225, '宗奭', '与蜜同捣，涂汤火伤甚速。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 349);
INSERT INTO Ben_Jing_Note VALUES (1226, '药征', '主治胸痹也，旁治痰饮。治消渴。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 149);
INSERT INTO Ben_Jing_Note VALUES (1227, '吴克潜', '茯苓能行气而和缓，利水而不伤正，故通补皆宜，古今方中用者很多，著名方剂有茯苓半夏汤（茯苓，白术，半夏，神曲，大麦芽，陈皮，天麻，治胃气虚弱，身重有痰，恶心欲吐）。茯苓四逆汤（茯苓，人参，甘草，干姜，生附子，治伤寒太阳证厥而心下悸，汗下后烦燥者）。四苓散（茯苓，猪苓，泽泻，陈皮，一方有白术无陈皮，治湿热霍乱，胸闷胀痛，溺涩烦渴）。茯苓甘草汤（茯苓，甘草桂枝生姜，治伤寒太阳证汗出不渴，或厥而心下悸，或咳而遗溺，或小便不利）。茯苓厚朴汤（治伤寒伤风，夹痰呕逆。及吐泻后喉涎牵响，脾胃气虚，饮食减少）。茯苓桂枝甘草大枣汤（治太阳证发汗后，脐下悸，欲做奔豚）。茯苓杏仁甘草汤（治水邪射肺，阻其出气，治胸痹短气） 。茯苓补心汤（茯苓，桂枝，炙甘草，紫英石，人参，麦冬，大枣，赤小豆，治心气不足，悲愁恚怒，烦热，面黄衄血，喉痛舌强，妇人气血两虚）等。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 94);
INSERT INTO Ben_Jing_Note VALUES (1228, '药征', '主治急迫，治里急急痛，旁治厥冷烦躁冲逆之症。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 19);
INSERT INTO Ben_Jing_Note VALUES (1229, '甄权', '主引血化汗化脓，内托痈疽痘疮，治噎膈满腹。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 89);
INSERT INTO Ben_Jing_Note VALUES (1230, '王硕', '曰男子多阴虚宜熟地，女子多血热宜生地。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 20);
INSERT INTO Ben_Jing_Note VALUES (1231, '弘景', '曰木香疗毒肿消恶气有验，今唯制蛀虫丸中用之，至于木香配合之成方颇多，著者有木香丸（香附黄柏胡黄连木香），治腹痛食伤，气郁作痛，诸虫痛，盖佐以苦寒之药，则不仅治冷痛也。香连丸（木香黄连）治痢疾为调和之剂，木香丸（木香附子卤砂）治寒疝绕脐作痛，结鞭不消，木香饼（生地木香作饼），热熨贴之，治结肿成核，消乳中结核酸痛等，均有灵效。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 31);
INSERT INTO Ben_Jing_Note VALUES (1232, '附录', '黄耆皮——性味功用同黄耆，善走表逐水湿，治虚浮及脚肿。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 54);
INSERT INTO Ben_Jing_Note VALUES (1233, '药征', '主治心胸痛而喘息咳唾也，旁治背痛，心中痞。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 349);
INSERT INTO Ben_Jing_Note VALUES (1234, '吴克潜', '瓜篓根实，皆能润肺治痰，此其同也，其中仁以清胸中痰火，润肠涤秽之功为主，其根治时热大渴，痈疮肿毒。皮能润肠消炎并行。一般治痰剂有宣有下有消有攻有润，而瓜篓实为润也，润剂之别是瓜篓清而润，苏子温而润，莱服子消而润，麦冬补而润，牛蒡杏仁散而润，补以治痰则有五味子之收敛，除湿健脾以治痰则有半夏之温而燥。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 149);
INSERT INTO Ben_Jing_Note VALUES (1235, '附录', '赤茯苓：性质功用与白茯苓同，尤能利窍行水，破结气，泻心小肠膀胱湿热，凡外感证多用之。茯苓皮：主利水道，开肌理，治水肿肤胀，行水而不耗气，胜于大腹皮。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 94);
INSERT INTO Ben_Jing_Note VALUES (1236, '灵胎', '生用泻火，解毒炙用，和中益气。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 19);
INSERT INTO Ben_Jing_Note VALUES (1237, '容川', '木香之理气，以其香气归脾，而味兼微辛，气浮味沉，得木气之温，力能疏土，茎是五枝五节，合于脾之数。其气浮味沉，能理上中下三焦。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 31);
INSERT INTO Ben_Jing_Note VALUES (1238, '倪注', '按伤寒杂病论之用根为生津要药，滋润太阳经之燥与胃经之燥，实为去心包积痰之要药。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 149);
INSERT INTO Ben_Jing_Note VALUES (1239, '容川', '茯苓得木之气，又能疏土，故能化气行水。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 94);
INSERT INTO Ben_Jing_Note VALUES (1240, '纲目', '润肺燥降火，治咳嗽，涤痰结，利咽喉，止消渴，利大肠，消痈肿疮毒。', 1, '2019-03-09 14:47:39', '2019-03-09 14:47:39', 149);
